tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",23,23,"千夫長便叫了兩個百夫長來、說、預備步兵二百、馬兵七十、長槍手二百、今夜亥初往該撒利亞去．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",23,24,"也要預備牲口叫保羅騎上、護送到巡撫腓力斯那裡去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",23,25,"千夫長又寫了文書、")');
