tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",23,27,"這人被猶太人拿住、將要殺害、我得知他是羅馬人、就帶兵丁下去救他出來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",23,28,"因要知道他們告他的緣故、我就帶他下到他們的公會去．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",23,29,"便查知他被告、是因他們律法的辯論、並沒有甚麼該死該綁的罪名。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",23,30,"後來有人把要害他的計謀告訴我、我就立時解他到你那裡去、又吩咐告他的人、在你面前告他。〔有古卷在此有願你平安〕")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",23,31,"於是兵丁照所吩咐他們的、將保羅夜裡帶到安提帕底。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",23,32,"第二天、讓馬兵護送、他們就回營樓去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",23,33,"馬兵來到該撒利亞、把文書呈給巡撫、便叫保羅站在他面前。")');
