tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",23,35,"就說、等告你的人來到、我要細聽你的事、便吩咐人把他看守在希律的衙門裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",24,1,"過了五天、大祭司亞拿尼亞、同幾個長老、和一個辯士帖土羅、下來、向巡撫控告保羅．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",24,2,"保羅被提了來、帖土羅就告他說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",24,3,"腓力斯大人、我們因你得以大享太平、並且這一國的弊病、因著你的先見、得以更正了．我們隨時隨地、滿心感謝不盡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",24,4,"惟恐多說、你嫌煩絮、只求你寬容聽我們說幾句話。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",24,5,"我們看這個人、如同瘟疫一般、是鼓動普天下眾猶太人生亂的、又是拿撒勒教黨裡的一個頭目．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",24,6,"連聖殿他也想要污穢．我們把他捉住了．〔有古卷在此有要按我們的律法審問〕")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",24,7,"〔不料千夫長呂西亞前來甚是強橫從我們手中把他奪去吩咐告他的人到你這裡來〕")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",24,8,"你自己究問他、就可以知道我們告他的一切事了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",24,9,"眾猶太人也隨著告他說、事情誠然是這樣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",24,10,"巡撫點頭叫保羅說話、他就說、我知道你在這國裡斷事多年、所以我樂意為自己分訴。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",24,11,"你查問就可以知道、從我上耶路撒冷禮拜、到今日、不過有十二天．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",24,12,"他們並沒有看見我在殿裡、或是在會堂裡、或是在城裡、和人辯論、聳動眾人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",24,13,"他們現在所告我的事、並不能對你證實了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",24,14,"但有一件事、我向你承認、就是他們所稱為異端的道、我正按著那道事奉我祖宗的　神、又信合乎律法的、和先知書上一切所記載的．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",24,15,"並且靠著　神、盼望死人、無論善惡、都要復活、就是他們自己也有這個盼望。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",24,16,"我因此自己勉勵、對　神、對人、常存無虧的良心。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",24,17,"過了幾年、我帶著賙濟本國的捐項和供獻的物上去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",24,18,"正獻的時候、他們看見我在殿裡已經潔淨了、並沒有聚眾、也沒有吵嚷．惟有幾個從亞西亞來的猶太人．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",24,19,"他們若有告我的事、就應當到你面前來告我。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",24,20,"即或不然、這些人、若看出我站在公會前、有妄為的地方、他們自己也可以說明。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",24,21,"縱然有、也不過一句話、就是我站在他們中間大聲說、我今日在你們面前受審、是為死人復活的道理。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",24,22,"腓力斯本是詳細曉得這道、就支吾他們說、且等千夫長呂西亞下來、我要審斷你們的事。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",24,23,"於是吩咐百夫長看守保羅、並且寬待他、也不攔阻他的親友來供給他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",24,24,"過了幾天、腓力斯和他夫人猶太的女子土西拉、一同來到、就叫了保羅來、聽他講論信基督耶穌的道。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",24,25,"保羅講論公義、節制、和將來的審判、腓力斯甚覺恐懼、說、你暫且去罷、等我得便再叫你來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",24,26,"腓力斯又指望保羅送他銀錢、所以屢次叫他來、和他談論。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",24,27,"過了兩年、波求非斯都接了腓力斯的任．腓力斯要討猶太人的喜歡、就留保羅在監裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",25,1,"非斯都到了任、過了三天、就從該撒利亞上耶路撒冷去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",25,2,"祭司長、和猶太人的首領、向他控告保羅、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",25,3,"又央告他、求他的情、將保羅提到耶路撒冷來．他們要在路上埋伏殺害他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",25,4,"非斯都卻回答說、保羅押在該撒利亞、我自己快要往那裡去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",25,5,"又說、你們中間有權勢的人、與我一同下去、那人若有甚麼不是、就可以告他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",25,6,"非斯都在他們那裡、住了不過十天八天、就下該撒利亞去．第二天坐堂、吩咐將保羅提上來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",25,7,"保羅來了、那些從耶路撒冷下來的猶太人、周圍站著、將許多重大的事控告他、都是不能證實的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",25,8,"保羅分訴說、無論猶太人的律法、或是聖殿、或是該撒、我都沒有干犯。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",25,9,"但非斯都要討猶太人的喜歡、就問保羅說、你願意上耶路撒冷去、在那裡聽我審斷這事麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",25,10,"保羅說、我站在該撒的堂前、這就是我應當受審的地方．我向猶太人並沒有行過甚麼不義的事、這也是你明明知道的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",25,11,"我若行了不義的事、犯了甚麼該死的罪、就是死、我也不辭．他們所告我的事若都不實、就沒有人可以把我交給他們。我要上告於該撒。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",25,12,"非斯都和議會商量了、就說、你既上告於該撒、可以往該撒那裡去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",25,13,"過了些日子、亞基帕王、和百尼基氏、來到該撒利亞、問非斯都安。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",25,14,"在那裡住了多日、非斯都將保羅的事告訴王、說、這裡有一個人、是腓力斯留在監裡的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",25,15,"我在耶路撒冷的時候、祭司長和猶太的長老、將他的事稟報了我、求我定他的罪。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",25,16,"我對他們說、無論甚麼人、被告還沒有和原告對質、未得機會分訴所告他的事、就先定他的罪、這不是羅馬人的條例。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",25,17,"及至他們都來到這裡、我就不耽延、第二天便坐堂、吩咐把那人提上來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",25,18,"告他的人站著告他．所告的、並沒有我所逆料的那等惡事．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",25,19,"不過是有幾樣辯論、為他們自己敬鬼神的事、又為一個人名叫耶穌、是已經死了、保羅卻說他是活著的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",25,20,"這些事當怎樣究問、我心裡作難．所以問他說、你願意上耶路撒冷去、在那裡為這些事聽審麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",25,21,"但保羅求我留下他要聽皇上審斷、我就吩咐把他留下、等我解他到該撒那裡去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",25,22,"亞基帕對非斯都說、我自己也願聽這人辯論。非斯都說、明天你可以聽。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",25,23,"第二天、亞基帕和百尼基大張威勢而來、同著眾千夫長、和城裡的尊貴人、進了公廳．非斯都吩咐一聲、就有人將保羅帶進來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",25,24,"非斯都說、亞基帕王、和在這裡的諸位阿、你們看這人、就是一切猶太人在耶路撒冷、和這裡、曾向我懇求、呼叫說、不可容他再活著。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",25,25,"但我查明他沒有犯甚麼該死的罪．並且他自己上告於皇帝、所以我定意把他解去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",25,26,"論到這人、我沒有確實的事、可以奏明主上．因此我帶他到你們面前、也特意帶他到你亞基帕王面前、為要在查問之後、有所陳奏。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",25,27,"據我看來、解送囚犯、不指明他的罪案、是不合理的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",26,1,"亞基帕對保羅說、准你為自己辯明。於是保羅伸手分訴說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",26,2,"亞基帕王阿、猶太人所告我的一切事、今日得在你面前分訴、實為萬幸．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",26,3,"更可幸的、是你熟悉猶太人的規矩、和他們的辯論．所以求你耐心聽我。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",26,4,"我從起初在本國的民中、並在耶路撒冷、自幼為人如何、猶太人都知道．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",26,5,"他們若肯作見證、就曉得我從起初、是按著我們教中最嚴緊的教門、作了法利賽人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",26,6,"現在我站在這裡受審、是因為指望　神向我們祖宗所應許的．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",26,7,"這應許、我們十二個支派、晝夜切切的事奉　神、都指望得著．王阿、我被猶太人控告、就是因這指望。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",26,8,"　神叫死人復活、你們為甚麼看作不可信的呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",26,9,"從前我自己以為應當多方攻擊拿撒勒人耶穌的名。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",26,10,"我在耶路撒冷也曾這樣行了．既從祭司長得了權柄、我就把許多聖徒囚在監裡．他們被殺、我也出名定案。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",26,11,"在各會堂、我屢次用刑、強逼他們說褻瀆的話．又分外惱恨他們、甚至追逼他們直到外邦的城邑。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",26,12,"那時、我領了祭司長的權柄和命令、往大馬色去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",26,13,"王阿、我在路上、晌午的時候、看見從天發光、比日頭還亮、四面照著我、並與我同行的人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",26,14,"我們都僕倒在地、我就聽見有聲音、用希伯來話、向我說、掃羅、掃羅、為甚麼逼迫我．你用腳踢刺是難的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",26,15,"我說、主阿、你是誰。主說、我就是你所逼迫的耶穌。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",26,16,"你起來站著、我特意向你顯現、要派你作執事作見證、將你所看見的事、和我將要指示你的事、證明出來．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",26,17,"我也要救你脫離百姓和外邦人的手。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",26,18,"我差你到他們那裡去、要叫他們的眼睛得開、從黑暗中歸向光明、從撒但權下歸向　神．又因信我、得蒙赦罪、和一切成聖的人同得基業。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",26,19,"亞基帕王阿、我故此沒有違背那從天上來的異像．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",26,20,"先在大馬色、後在耶路撒冷、和猶太全地、以及外邦、勸勉他們應當悔改歸向　神、行事與悔改的心相稱。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",26,21,"因此、猶太人在殿裡拿住我、想要殺我。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",26,22,"然而我蒙　神的幫助、直到今日還站得住、對著尊貴卑賤老幼作見證．所講的、並不外乎眾先知和摩西所說、將來必成的事．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",26,23,"就是基督必須受害、並且因從死裡復活、要首先把光明的道、傳給百姓和外邦人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",26,24,"保羅這樣分訴、非斯都大聲說、保羅、你癲狂了罷．你的學問太大、反叫你癲狂了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",26,25,"保羅說、非斯都大人、我不是癲狂、我說的乃是真實明白話。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",26,26,"王也曉得這些事、所以我向王放膽直言、我深信這些事沒有一件向王隱藏的．因都不是在背地裡作的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",26,27,"亞基帕王阿、你信先知麼、我知道你是信的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",26,28,"亞基帕對保羅說、你想少微一勸、便叫我作基督徒阿。〔或作你這樣勸我幾乎叫我作基督徒了〕")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",26,29,"保羅說、無論是少勸、是多勸、我向　神所求的、不但你一個人、就是今天一切聽我的、都要像我一樣、只是不要像我有這些鎖鍊。")');
