tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",27,17,"既然把小船拉上來、就用纜索捆綁船底．又恐怕在賽耳底沙灘上擱了淺、就落下篷來、任船飄去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",27,18,"我們被風浪逼得甚急、第二天眾人就把貨物拋在海裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",27,19,"到第三天、他們又親手把船上的器具拋棄了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",27,20,"太陽和星辰多日不顯露、又有狂風大浪催逼、我們得救的指望就都絕了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",27,21,"眾人多日沒有喫甚麼、保羅就出來站在他們中間說、眾位、你們本該聽我的話、不離開革哩底、免得遭這樣的傷損破壞。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Act",27,22,"現在我還勸你們放心．你們的性命、一個也不失喪、惟獨失喪這船。")');
