tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Rom",16,15,"又問非羅羅古、和猶利亞、尼利亞、和他姊妹、同阿林巴、並與他們在一處的眾聖徒安。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Rom",16,16,"你們親嘴問安、彼此務要聖潔。基督的眾教會都問你們安。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Rom",16,17,"弟兄們、那些離間你們、叫你們跌倒、背乎所學之道的人、我勸你們要留意躲避他們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Rom",16,18,"因為這樣的人不服事我們的主基督、只服事自己的肚腹．用花言巧語、誘惑那些老實人的心。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Rom",16,19,"你們的順服、已經傳於眾人、所以我為你們歡喜．但我願意你們在善上聰明、在惡上愚拙。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Rom",16,20,"賜平安的　神、快要將撒但踐踏在你們腳下。願我主耶穌基督的恩、常和你們同在。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Rom",16,21,"與我同工的提摩太、和我的親屬路求、耶孫、所西巴德、問你們安。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Rom",16,22,"我這代筆寫信的德丟、在主裡面問你們安。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Rom",16,23,"那接待我、也接待全教會的該猶、問你們安。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Rom",16,24,"城內管銀庫的以拉都、和兄弟括土、問你們安。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Rom",16,25,"惟有　神能照我所傳的福音、和所講的耶穌基督、並照永古隱藏不言的奧秘、堅固你們的心。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Rom",16,26,"這奧秘如今顯明出來、而且按著永生　神的命、藉眾先知的書指示萬國的民、使他們信服真道。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Rom",16,27,"願榮耀因耶穌基督歸與獨一全智的　神、直到永遠。阿們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",1,1,"奉　神旨意、蒙召作耶穌基督使徒的保羅、同兄弟所提尼、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",1,2,"寫信給在哥林多　神的教會、就是在基督耶穌裡成聖、蒙召作聖徒的、以及所有在各處求告我主耶穌基督之名的人．基督是他們的主、也是我們的主．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",1,3,"願恩惠平安、從　神我們的父、並主耶穌基督、歸與你們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",1,4,"我常為你們感謝我的　神、因　神在基督耶穌裡所賜給你們的恩惠．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",1,5,"又因你們在他裡面凡事富足、口才知識都全備．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",1,6,"正如我為基督作的見證、在你們心裡得以堅固．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",1,7,"以致你們在恩賜上沒有一樣不及人的．等候我們的主耶穌基督顯現．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",1,8,"他也必堅固你們到底、叫你們在我們主耶穌基督的日子、無可責備。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",1,9,"　神是信實的、你們原是被他所召、好與他兒子、我們的主耶穌基督、一同得分。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",1,10,"弟兄們、我藉我們主耶穌基督的名、勸你們都說一樣的話．你們中間也不可分黨．只要一心一意彼此相合。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",1,11,"因為革來氏家裡的人、曾對我提起弟兄們來、說你們中間有分爭。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",1,12,"我的意思就是你們各人說、我是屬保羅的．我是屬亞波羅的．我是屬磯法的．我是屬基督的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",1,13,"基督是分開的麼．保羅為你們釘了十字架麼．你們是奉保羅的名受了洗麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",1,14,"我感謝　神、除了基利司布並該猶以外、我沒有給你們一個人施洗．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",1,15,"免得有人說、你們是奉我的名受洗。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",1,16,"我也給司提反家施過洗．此外給別人施洗沒有、我卻記不清。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",1,17,"基督差遣我、原不是為施洗、乃是為傳福音．並不用智慧的言語、免得基督的十字架落了空。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",1,18,"因為十字架的道理、在那滅亡的人為愚拙．在我們得救的人卻為　神的大能。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",1,19,"就如經上所記、『我要滅絕智慧人的智慧、廢棄聰明人的聰明。』")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",1,20,"智慧人在那裡．文士在那裡．這世上的辯士在那裡．　神豈不是叫這世上的智慧變成愚拙麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",1,21,"世人憑自己的智慧、既不認識　神、　神就樂意用人所當作愚拙的道理、拯救那些信的人．這就是　神的智慧了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",1,22,"猶太人是要神蹟、希利尼人是求智慧．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",1,23,"我們卻是傳釘十字架的基督、在猶太人為絆腳石、在外邦人為愚拙、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",1,24,"但在那蒙召的、無論是猶太人、希利尼人、基督總為　神的能力、　神的智慧。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",1,25,"因　神的愚拙總比人智慧．　神的軟弱總比人強壯。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",1,26,"弟兄們哪、可見你們蒙召的、按著肉體有智慧的不多、有能力的不多、有尊貴的也不多．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",1,27,"　神卻揀選了世上愚拙的、叫有智慧的羞愧．又揀選了世上軟弱的、叫那強壯的羞愧．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",1,28,"　神也揀選了世上卑賤的、被人厭惡的、以及那無有的、為要廢掉那有的．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",1,29,"使一切有血氣的、在　神面前一個也不能自誇。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",1,30,"但你們得在基督耶穌裡、是本乎　神、　神又使他成為我們的智慧、公義、聖潔、救贖．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",1,31,"如經上所記、『誇口的當指著主誇口。』")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",2,1,"弟兄們、從前我到你們那裡去、並沒有用高言大智對你們宣傳　神的奧秘。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",2,2,"因為我曾定了主意、在你們中間不知道別的、只知道耶穌基督、並他釘十字架。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",2,3,"我在你們那裡、又軟弱、又懼怕、又甚戰兢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",2,4,"我說的話講的道、不是用智慧委婉的言語、乃是用聖靈和大能的明證．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",2,5,"叫你們的信不在乎人的智慧、只在乎　神的大能。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",2,6,"然而在完全的人中、我們也講智慧．但不是這世上的智慧、也不是這世上有權有位將要敗亡之人的智慧．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",2,7,"我們講的、乃是從前所隱藏、　神奧秘的智慧、就是　神在萬世以前、預定使我們得榮耀的．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",2,8,"這智慧世上有權有位的人沒有一個知道的．他們若知道、就不把榮耀的主釘在十字架上了．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",2,9,"如經上所記、『　神為愛他的人所預備的、是眼睛未曾看見、耳朵未曾聽見、人心也未曾想到的。』")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",2,10,"只有　神藉著聖靈向我們顯明了．因為聖靈參透萬事、就是　神深奧的事也參透了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",2,11,"除了在人裡頭的靈、誰知道人的事．像這樣、除了　神的靈、也沒有人知道　神的事。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",2,12,"我們所領受的、並不是世上的靈、乃是從　神來的靈、叫我們能知道　神開恩賜給我們的事。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",2,13,"並且我們講說這些事、不是用人智慧所指教的言語、乃是用聖靈所指教的言語、將屬靈的話、解釋屬靈的事。〔或作將屬靈的事講與屬靈的人〕")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",2,14,"然而屬血氣的人不領會　神聖靈的事、反倒以為愚拙．並且不能知道、因為這些事惟有屬靈的人纔能看透。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",2,15,"屬靈的人能看透萬事、卻沒有一人能看透了他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",2,16,"誰曾知道主的心去教導他呢．但我們是有基督的心了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",3,1,"弟兄們、我從前對你們說話、不能把你們當作屬靈的、只得把你們當作屬肉體、在基督裡為嬰孩的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",3,2,"我是用奶餵你們、沒有用飯餵你們．那時你們不能喫、就是如今還是不能。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",3,3,"你們仍是屬肉體的．因為在你們中間有嫉妒分爭、這豈不是屬乎肉體、照著世人的樣子行麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",3,4,"有說、我是屬保羅的．有說、我是屬亞波羅的．這豈不是你們和世人一樣麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",3,5,"亞波羅算甚麼．保羅算甚麼．無非是執事、照主所賜給他們各人的、引導你們相信。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",3,6,"我栽種了、亞波羅澆灌了．惟有　神叫他生長。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",3,7,"可見栽種的算不得甚麼、澆灌的也算不得甚麼．只在那叫他生長的　神。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",3,8,"栽種的和澆灌的都是一樣．但將來各人要照自己的工夫、得自己的賞賜。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",3,9,"因為我們是與　神同工的．你們是　神所耕種的田地、所建造的房屋。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",3,10,"我照　神所給我的恩、好像一個聰明的工頭、立好了根基、有別人在上面建造．只是各人要謹慎怎樣在上面建造。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",3,11,"因為那已經立好的根基、就是耶穌基督、此外沒有人能立別的根基。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",3,12,"若有人用金、銀、寶石、草木、禾楷、在這根基上建造．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",3,13,"各人的工程必然顯露．因為那日子要將他表明出來、有火發現．這火要試驗各人的工程怎樣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",3,14,"人在那根基上所建造的工程、若存得住、他就要得賞賜。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",3,15,"人的工程若被燒了、他就要受虧損．自己卻要得救．雖然得救乃像從火裡經過的一樣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",3,16,"豈不知你們是　神的殿、　神的靈住在你們裡頭麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",3,17,"若有人毀壞　神的殿、　神必要毀壞那人．因為　神的殿是聖的、這殿就是你們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",3,18,"人不可自欺．你們中間若有人、在這世界自以為有智慧、倒不如變作愚拙、好成為有智慧的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",3,19,"因這世界的智慧、在　神看是愚拙．如經上記著說、『主叫有智慧的中了自己的詭計。』")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",3,20,"又說、『主知道智慧人的意念是虛妄的。』")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",3,21,"所以無論誰、都不可拿人誇口．因為萬有全是你們的．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",3,22,"或保羅、或亞波羅、或磯法、或世界、或生、或死、或現今的事、或將來的事、全是你們的．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",3,23,"並且你們是屬基督的．基督又是屬　神的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",4,1,"人應當以我們為基督的執事、為　神奧秘事的管家。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",4,2,"所求於管家的、是要他有忠心。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",4,3,"我被你們論斷、或被別人論斷、我都以為極小的事．連我自己也不論斷自己。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",4,4,"我雖不覺得自己有錯、卻也不能因此得以稱義。但判斷我的乃是主。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",4,5,"所以時候未到、甚麼都不要論斷、只等主來、他要照出暗中的隱情、顯明人心的意念。那時各人要從　神那裡得著稱讚。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",4,6,"弟兄們、我為你們的緣故、拿這些事轉比自己和亞波羅．叫你們效法我們不可過於聖經所記．免得你們自高自大、貴重這個、輕看那個。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",4,7,"使你與人不同的是誰呢．你有甚麼不是領受的呢．若是領受的、為何自誇、彷彿不是領受的呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",4,8,"你們已經飽足了、已經豐富了、不用我們、自己就作王了．我願意你們果真作王、叫我們也得與你們一同作王。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",4,9,"我想　神把我們使徒明明列在末後、好像定死罪的囚犯．因為我們成了一臺戲、給世人和天使觀看。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",4,10,"我們為基督的緣故算是愚拙的、你們在基督裡倒是聰明的、我們軟弱、你們倒強壯．你們有榮耀、我們倒被藐視。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",4,11,"直到如今、我們還是又飢、又渴、又赤身露體、又挨打、又沒有一定的住處．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",4,12,"並且勞苦、親手作工．被人咒罵、我們就祝福．被人逼迫、我們就忍受．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",4,13,"被人毀謗、我們就善勸．直到如今、人還把我們看作世界上的污穢、萬物中的渣滓。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",4,14,"我寫這話、不是叫你們羞愧、乃是警戒你們、好像我所親愛的兒女一樣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",4,15,"你們學基督的、師傅雖有一萬、為父的卻是不多、因我在基督耶穌裡用福音生了你們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",4,16,"所以我求你們效法我。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",4,17,"因此我已打發提摩太到你們那裡去．他在主裡面、是我所親愛有忠心的兒子．他必提醒你們、記念我在基督裡怎樣行事、在各處各教會中怎樣教導人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",4,18,"有些人自高自大、以為我不到你們那裡去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",4,19,"然而主若許我、我必快到你們那裡去．並且我所要知道的、不是那些自高自大之人的言語、乃是他們的權能。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",4,20,"因為　神的國不在乎言語、乃在乎權能。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",4,21,"你們願意怎麼樣呢．是願意我帶著刑杖到你們那裡去呢、還是要我存慈愛溫柔的心呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",5,1,"風聞在你們中間有淫亂的事．這樣的淫亂、連外邦人中也沒有、就是有人收了他的繼母。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",5,2,"你們還是自高自大、並不哀痛、把行這事的人從你們中間趕出去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",5,3,"我身子雖不在你們那裡、心卻在你們那裡、好像我親自與你們同在、已經判斷了行這事的人、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",5,4,"就是你們聚會的時候、我的心也同在、奉我們主耶穌的名、並用我們主耶穌的權能、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",5,5,"要把這樣的人交給撒但、敗壞他的肉體、使他的靈魂在主耶穌的日子可以得救。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",5,6,"你們這自誇是不好的．豈不知一點麵酵能使全團發起來麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",5,7,"你們既是無酵的麵、應當把舊酵除淨、好使你們成為新團．因為我們逾越節的羔羊基督、已經被殺獻祭了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",5,8,"所以我們守這節不可用舊酵、也不可用惡毒〔或作陰毒〕邪惡的酵、只用誠實真正的無酵餅。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",5,9,"我先前寫信給你們說、不可與淫亂的人相交．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",5,10,"此話不是指這世上一概行淫亂的、或貪婪的、勒索的、或拜偶像的、若是這樣、你們除非離開世界方可．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",5,11,"但如今我寫信給你們說、若有稱為弟兄、是行淫亂的、或貪婪的、或拜偶像的、或辱罵的、或醉酒的、或勒索的．這樣的人不可與他相交．就是與他喫飯都不可。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",5,12,"因為審判教外的人與我何干．教內的人豈不是你們審判的麼．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",5,13,"至於外人有　神審判他們．你們應當把那惡人從你們中間趕出去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",6,1,"你們中間有彼此相爭的事、怎敢在不義的人面前求審、不在聖徒面前求審呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",6,2,"豈不知聖徒要審判世界麼．若世界為你們所審、難道你們不配審判這最小的事麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",6,3,"豈不知我們要審判天使麼、何況今生的事呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",6,4,"既是這樣、你們若有今生的事當審判、是派教會所輕看的人審判麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",6,5,"我說這話、是要叫你們羞恥。難道你們中間沒有一個智慧人、能審斷弟兄們的事麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",6,6,"你們竟是弟兄與弟兄告狀、而且告在不信主的人面前。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",6,7,"你們彼此告狀、這已經是你們的大錯了．為甚麼不情願受欺呢．為甚麼不情願喫虧呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",6,8,"你們倒是欺壓人、虧負人、況且所欺壓所虧負的就是弟兄。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",6,9,"你們豈不知、不義的人不能承受　神的國麼。不要自欺．無論是淫亂的、拜偶像的、姦淫的、作孌童的、親男色的、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",6,10,"偷竊的、貪婪的、醉酒的、辱罵的、勒索的、都不能承受　神的國。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",6,11,"你們中間也有人從前是這樣．但如今你們奉主耶穌基督的名、並藉著我們　神的靈、已經洗淨、成聖稱義了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",6,12,"凡事我都可行．但不都有益處。凡事我都可行、但無論那一件、我總不受他的轄制。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",6,13,"食物是為肚腹、肚腹是為食物．但　神要叫這兩樣都廢壞。身子不是為淫亂、乃是為主．主也是為身子．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",6,14,"並且　神已經叫主復活、也要用自己的能力叫我們復活。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",6,15,"豈不知你們的身子是基督的肢體麼．我可以將基督的肢體作為娼妓的肢體麼．斷乎不可。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",6,16,"豈不知與娼妓聯合的、便是與他成為一體麼．因為主說、『二人要成為一體。』")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",6,17,"但與主聯合的、便是與主成為一靈。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",6,18,"你們要逃避淫行。人所犯的、無論甚麼罪、都在身子以外．惟有行淫的、是得罪自己的身子。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",6,19,"豈不知你們的身子就是聖靈的殿麼．這聖靈是從　神而來、住在你們裡頭的．並且你們不是自己的人．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",6,20,"因為你們是重價買來的．所以要在你們的身子上榮耀　神。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",7,1,"論到你們信上所提的事、我說男不近女倒好。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",7,2,"但要免淫亂的事、男子當各有自己的妻子、女子也當各有自己的丈夫。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",7,3,"丈夫當用合宜之分待妻子、妻子待丈夫也要如此。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",7,4,"妻子沒有權柄主張自己的身子、乃在丈夫．丈夫也沒有權柄主張自己的身子、乃在妻子。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",7,5,"夫妻不可彼此虧負、除非兩相情願、暫時分房、為要專心禱告方可、以後仍要同房、免得撒但趁著你們情不自禁、引誘你們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",7,6,"我說這話、原是准你們的、不是命你們的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",7,7,"我願意眾人像我一樣．只是各人領受　神的恩賜、一個是這樣、一個是那樣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",7,8,"我對著沒有嫁娶的和寡婦說、若他們常像我就好。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",7,9,"倘若自己禁止不住、就可以嫁娶。與其慾火攻心、倒不如嫁娶為妙。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",7,10,"至於那已經嫁娶的、我吩咐他們、其實不是我吩咐、乃是主吩咐、說、妻子不可離開丈夫。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",7,11,"若是離開了、不可再嫁．或是仍同丈夫和好．丈夫也不可離棄妻子。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",7,12,"我對其餘的人說、不是主說、倘若某弟兄有不信的妻子、妻子也情願和他同住、他就不要離棄妻子。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",7,13,"妻子有不信的丈夫、丈夫也情願和他同住、他就不要離棄丈夫。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",7,14,"因為不信的丈夫、就因著妻子成了聖潔．並且不信的妻子、就因著丈夫成了聖潔．〔丈夫原文作弟兄〕不然、你們的兒女就不潔淨．但如今他們是聖潔的了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",7,15,"倘若那不信的人要離去、就由他離去罷．無論是弟兄、是姐妹、遇著這樣的事、都不必拘束．　神召我們原是要我們和睦。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",7,16,"你這作妻子的、怎麼知道不能救你的丈夫呢．你這作丈夫的、怎麼知道不能救你的妻子呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",7,17,"只要照主所分給各人的、和　神所召各人的而行。我吩咐各教會都是這樣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",7,18,"有人已受割禮蒙召呢、就不要廢割禮．有人未受割禮蒙召呢、就不要受割禮。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",7,19,"受割禮算不得甚麼、不受割禮也算不得甚麼、只要守　神的誡命就是了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",7,20,"各人蒙召的時候是甚麼身分、仍要守住這身分。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",7,21,"你是作奴隸蒙召的麼、不要因此憂慮．若能以自由、就求自由更好。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",7,22,"因為作奴僕蒙召於主的、就是主所釋放的人．作自由之人蒙召的、就是基督的奴僕。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",7,23,"你們是重價買來的．不要作人的奴僕。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",7,24,"弟兄們、你們各人蒙召的時候是甚麼身分、仍要在　神面前守住這身分。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",7,25,"論到童身的人、我沒有主的命令、但我既蒙主憐恤、能作忠心的人、就把自己的意見告訴你們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",7,26,"因現今的艱難、據我看來、人不如守素安常纔好。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",7,27,"你有妻子纏著呢、就不要求脫離．你沒有妻子纏著呢、就不要求妻子。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",7,28,"你若娶妻、並不是犯罪．處女若出嫁、也不是犯罪．然而這等人肉身必受苦難．我卻願意你們免這苦難。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",7,29,"弟兄們、我對你們說、時候減少了．從此以後、那有妻子的、要像沒有妻子．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",7,30,"哀哭的、要像不哀哭．快樂的、要像不快樂、置買的、要像無有所得．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",7,31,"用世物的、要像不用世物．因為這世界的樣子將要過去了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",7,32,"我願你們無所掛慮。沒有娶妻的、是為主的事掛慮、想怎樣叫主喜悅。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",7,33,"娶了妻的、是為世上的事掛慮、想怎樣叫妻子喜悅。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",7,34,"婦人和處女也有分別。沒有出嫁的、是為主的事掛慮、要身體靈魂都聖潔．已經出嫁的、是為世上的事掛慮、想怎樣叫丈夫喜悅。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",7,35,"我說這話、是為你們的益處．不是要牢籠你們、乃是要叫你們行合宜的事、得以殷勤服事主、沒有分心的事。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",7,36,"若有人以為自己待他的女兒不合宜、女兒也過了年歲、事又當行、他就可隨意辦理、不算有罪、叫二人成親就是了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",7,37,"倘若人心裡堅定、沒有不得已的事、並且由得自己作主、心裡又決定了留下女兒不出嫁、如此行也好。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",7,38,"這樣看來、叫自己的女兒出嫁是好．不叫他出嫁更是好。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",7,39,"丈夫活著的時候、妻子是被約束的．丈夫若死了、妻子就可以自由、隨意再嫁．只是要嫁這在主裡面的人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",7,40,"然而按我的意見、若常守節更有福氣．我也想自己是被　神的靈感動了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",8,1,"論到祭偶像之物、我們曉得我們都有知識．但知識是叫人自高自大、惟有愛心能造就人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",8,2,"若有人以為自己知道甚麼、按他所當知道的、他仍是不知道．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",8,3,"若有人愛　神、這人乃是　神所知道的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",8,4,"論到喫祭偶像之物、我們知道偶像在世上算不得甚麼．也知道　神只有一位、再沒有別的　神。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",8,5,"雖有稱為　神的、或在天、或在地．就如那許多的　神、許多的主．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",8,6,"然而我們只有一位　神、就是父萬物都本於他、我們也歸於他．並有一位主、就是耶穌基督、萬物都是藉著他有的、我們也是藉著他有的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",8,7,"但人不都有這等知識．有人到如今因拜慣了偶像、就以為所喫的是祭偶像之物．他們的良心既然軟弱、也就污穢了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",8,8,"其實食物不能叫　神看中我們．因為我們不喫也無損、喫也無益。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",8,9,"只是你們要謹慎、恐怕你們這自由、竟成了那軟弱人的絆腳石。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",8,10,"若有人見你這有知識的、在偶像的廟裡坐席、這人的良心、若是軟弱、豈不放膽去喫那祭偶像之物麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",8,11,"因此、基督為他死的那軟弱弟兄、也就因你的知識沉淪了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",8,12,"你們這樣得罪弟兄們、傷了他們軟弱的良心、就是得罪基督。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",8,13,"所以食物若叫我弟兄跌倒、我就永遠不喫肉、免得叫我弟兄跌倒了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",9,1,"我不是自由的麼．我不是使徒麼．我不是見過我們的主耶穌麼．你們不是我在主裡面所作之工麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",9,2,"假若在別人我不是使徒、在你們我總是使徒．因為你們在主裡正是我作使徒的印證。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",9,3,"我對那盤問我的人、就是這樣分訴。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",9,4,"難道我們沒有權柄靠福音喫喝麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",9,5,"難道我們沒有權柄娶信主的姊妹為妻、帶著一同往來、彷彿其餘的使徒、和主的弟兄、並磯法一樣麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",9,6,"獨有我與巴拿巴沒有權柄不作工麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",9,7,"有誰當兵、自備糧餉呢．有誰栽葡萄園、不喫園裡的果子呢．有誰牧養牛羊、不喫牛羊的奶呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",9,8,"我說這話、豈是照人的意見．律法不也是這樣說麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",9,9,"就如摩西的律法記著說、『牛在場上踹榖的時候、不可籠住他的嘴。』難道　神所掛念的是牛麼．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",9,10,"不全是為我們說的麼．分明是為我們說的．因為耕種的當存著指望去耕種．打場的也當存得糧的指望去打場。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",9,11,"我們若把屬靈的種子撒在你們中間、就是從你們收割奉養肉身之物、這還算大事麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",9,12,"若別人在你們身上有這權柄、何況我們呢．然而我們沒有用過這權柄、倒凡事忍受、免得基督的福音被阻隔。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",9,13,"你們豈不知為聖事勞碌的、就喫殿中的物麼．伺候祭壇的、就分領壇上的物麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",9,14,"主也是這樣命定、叫傳福音的靠著福音養生。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",9,15,"但這權柄我全沒有用過．我寫這話、並非要你們這樣待我．因為我寧可死、也不叫人使我所誇的落了空。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",9,16,"我傳福音原沒有可誇的．因為我是不得已的．若不傳福音、我便有禍了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",9,17,"我若甘心作這事、就有賞賜．若不甘心、責任卻已經託付我了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",9,18,"既是這樣、我的賞賜是甚麼呢．就是我傳福音的時候、叫人不花錢得福音、免得用盡我傳福音的權柄。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",9,19,"我雖是自由的、無人轄管、然而我甘心作了眾人的僕人、為要多得人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",9,20,"向猶太人、我就作猶太人、為要得猶太人．向律法以下的人、我雖不在律法以下、還是作律法以下的人、為要得律法以下的人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",9,21,"向沒有律法的人、我就作沒有律法的人、為要得沒有律法的人．其實我在　神面前、不是沒有律法、在基督面前、正在律法之下。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",9,22,"向軟弱的人、我就作軟弱的人、為要得軟弱的人．向甚麼樣的人、我就作甚麼樣的人．無論如何、總要救些人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",9,23,"凡我所行的、都是為福音的緣故、為要與人同得這福音的好處。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",9,24,"豈不知在場上賽跑的都跑、但得獎賞的只有一人．你們也當這樣跑、好叫你們得著獎賞。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",9,25,"凡較力爭勝的、諸事都有節制．他們不過是要得能壞的冠冕．我們卻是要得不能壞的冠冕。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",9,26,"所以我奔跑、不像無定向的．我鬥拳、不像打空氣的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",9,27,"我是攻克己身、叫身服我．恐怕我傳福音給別人、自己反被棄絕了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",10,1,"弟兄們、我不願意你們不曉得、我們的祖宗從前都在雲下、都從海中經過．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",10,2,"都在雲裡海裡受洗歸了摩西．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",10,3,"並且都喫了一樣的靈食．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",10,4,"也都喝了一樣的靈水．所喝的是出於隨著他們的靈磐石．那磐石就是基督。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",10,5,"但他們中間、多半是　神不喜歡的人．所以在曠野倒斃。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",10,6,"這些事都是我們的鑑戒、叫我們不要貪戀惡事、像他們那樣貪戀的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",10,7,"也不要拜偶像、像他們有人拜的．如經上所記、『百姓坐下喫喝、起來玩耍。』")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",10,8,"我們也不要行姦淫、像他們有人行的、一天就倒斃了二萬三千人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",10,9,"也不要試探主、〔主有古卷作基督〕像他們有人試探的、就被蛇所滅。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",10,10,"你們也不要發怨言、像他們有發怨言的、就被滅命的所滅。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",10,11,"他們遭遇這些事、都要作為鑑戒．並且寫在經上、正是警戒我們這末世的人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",10,12,"所以自己以為站得穩的、須要謹慎、免得跌倒。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",10,13,"你們所遇見的試探、無非是人所能受的、　神是信實的、必不叫你們受試探過於所能受的．在受試探的時候、總要給你們開一條出路、叫你們能忍受得住。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",10,14,"我所親愛的弟兄阿、你們要逃避拜偶像的事。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",10,15,"我好像對明白人說的、你們要審察我的話。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",10,16,"我們所祝福的杯、豈不是同領基督的血麼．我們所擘開的餅、豈不是同領基督的身體麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",10,17,"我們雖多、仍是一個餅、一個身體．因為我們都是分受這一個餅。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",10,18,"你們看屬肉體的以色列人．那喫祭物的、豈不是在祭壇上有分麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",10,19,"我是怎麼說呢．豈是說祭偶像之物算得甚麼呢。或說偶像算得甚麼呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",10,20,"我乃是說、外邦人所獻的祭、是祭鬼、不是祭　神．我不願意你們與鬼相交。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",10,21,"你們不能喝主的杯、又喝鬼的杯．不能喫主的筵席、又喫鬼的筵席。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",10,22,"我們可惹主的憤恨麼．我們比他還有能力麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",10,23,"凡事都可行．但不都有益處。凡事都可行．但不都造就人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",10,24,"無論何人、不要求自己的益處、乃要求別人的益處。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",10,25,"凡市上所賣的、你們只管喫、不要為良心的緣故問甚麼話．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",10,26,"因為地和其中所充滿的、都屬乎主。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",10,27,"倘有一個不信的人請你們赴席、你們若願意去、凡擺在你們面前的、只管喫、不要為良心的緣故問甚麼話。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",10,28,"若有人對你們說、這是獻過祭的物、就要為那告訴你們的人、並為良心的緣故、不喫。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",10,29,"我說的良心、不是你的、乃是他的．我這自由、為甚麼被別人的良心論斷呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",10,30,"我若謝恩而喫、為甚麼因我謝恩的物被人毀謗呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",10,31,"所以你們或喫或喝、無論作甚麼、都要為榮耀　神而行。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",10,32,"不拘是猶太人、是希利尼人、是　神的教會、你們都不要使他跌倒．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",10,33,"就好像我凡事都叫眾人喜歡、不求自己的益處、只求眾人的益處、叫他們得救。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",11,1,"你們該效法我、像我效法基督一樣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",11,2,"我稱讚你們、因你們凡事記念我、又堅守我所傳給你們的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",11,3,"我願意你們知道、基督是各人的頭．男人是女人的頭、　神是基督的頭。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",11,4,"凡男人禱告或是講道、〔講道或作說預言下同〕若蒙著頭、就羞辱自己的頭。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",11,5,"凡女人禱告或是講道、若不蒙著頭、就羞辱自己的頭．因為這就如同剃了頭髮一樣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",11,6,"女人若不蒙著頭、就該剪了頭髮．女人若以剪髮剃髮為羞愧、就該蒙著頭。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",11,7,"男人本不該蒙著頭、因為他是　神的形像和榮耀、但女人是男人的榮耀。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",11,8,"起初、男人不是由女人而出．女人乃是由男人而出。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",11,9,"並且男人不是為女人造的．女人乃是為男人造的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",11,10,"因此、女人為天使的緣故、應當在頭上有服權柄的記號。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",11,11,"然而照主的安排、女也不是無男、男也不是無女。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",11,12,"因為女人原是由男人而出、男人也是由女人而出．但萬有都是出乎　神。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",11,13,"你們自己審察、女人禱告　神、不蒙著頭、是合宜的麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",11,14,"你們的本性不也指示你們、男人若有長頭髮、便是他的羞辱麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",11,15,"但女人有長頭髮、乃是他的榮耀．因為這頭髮是給他作蓋頭的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",11,16,"若有人想要辯駁、我們卻沒有這樣的規矩、　神的眾教會也是沒有的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",11,17,"我現今吩咐你們的話、不是稱讚你們．因為你們聚會不是受益、乃是招損。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",11,18,"第一、我聽說你們聚會的時候、彼此分門別類．我也稍微的信這話。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",11,19,"在你們中間不免有分門結黨的事、好叫那些有經驗的人、顯明出來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",11,20,"你們聚會的時候、算不得喫主的晚餐．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",11,21,"因為喫的時候、各人先喫自己的飯、甚至這個飢餓、那個酒醉。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",11,22,"你們要喫喝、難道沒有家麼．還是藐視　神的教會、叫那沒有的羞愧呢。我向你們可怎麼說呢．可因此稱讚你們麼．我不稱讚。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",11,23,"我當日傳給你們的、原是從主領受的、就是主耶穌被賣的那一夜、拿起餅來、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",11,24,"祝謝了、就擘開、說、這是我的身體、為你們捨的．〔捨有古卷作擘開〕你們應當如此行、為的是記念我。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",11,25,"飯後、也照樣拿起杯來、說、這杯是用我的血所立的新約．你們每逢喝的時候、要如此行、為的是記念我。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",11,26,"你們每逢喫這餅、喝這杯、是表明主的死、直等到他來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",11,27,"所以無論何人、不按理喫主的餅、喝主的杯、就是干犯主的身主的血了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",11,28,"人應當自己省察、然後喫這餅、喝這杯。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",11,29,"因為人喫喝、若不分辨是主的身體、就是喫喝自己的罪了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",11,30,"因此、在你們中間有好些軟弱的、與患病的、死的也不少。〔死原文作睡〕")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",11,31,"我們若是先分辨自己、就不至於受審。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",11,32,"我們受審的時候、乃是被主懲治．免得我們和世人一同定罪。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",11,33,"所以我弟兄們、你們聚會喫的時候、要彼此等待。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",11,34,"若有人飢餓、可以在家裡先喫．免得你們聚會自己取罪。其餘的事、我來的時候再安排。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",12,1,"弟兄們、論到屬靈的恩賜、我不願意你們不明白。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",12,2,"你們作外邦人的時候、隨事被牽引受迷惑、去服事那啞吧偶像．這是你們知道的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",12,3,"所以我告訴你們、被　神的靈感動的、沒有說耶穌是可咒詛的．若不是被聖靈感動的、也沒有能說耶穌是主的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",12,4,"恩賜原有分別、聖靈卻是一位。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",12,5,"職事也有分別、主卻是一位。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",12,6,"功用也有分別、　神卻是一位、在眾人裡面運行一切的事。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",12,7,"聖靈顯在各人身上、是叫人得益處。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",12,8,"這人蒙聖靈賜他智慧的言語．那人也蒙這位聖靈賜他知識的言語．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",12,9,"又有一人蒙這位聖靈賜他信心．還有一人蒙這位聖靈賜他醫病的恩賜．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",12,10,"又叫一人能行異能．又叫一人能作先知．又叫一人能辨別諸靈．又叫一人能說方言．又叫一人能翻方言。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",12,11,"這一切都是這位聖靈所運行、隨己意分給各人的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",12,12,"就如身子是一個、卻有許多肢體、而且肢體雖多、仍是一個身子．基督也是這樣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",12,13,"我們不拘是猶太人、是希利尼人、是為奴的、是自主的、都從一位聖靈受洗、成了一個身體．飲於一位聖靈。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",12,14,"身子原不是一個肢體、乃是許多肢體。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",12,15,"設若腳說、我不是手、所以不屬乎身子．他不能因此就不屬乎身子。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",12,16,"設若耳說、我不是眼、所以不屬乎身子．他也不能因此就不屬乎身子。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",12,17,"若全身是眼、從那裡聽聲呢．若全身是耳、從那裡聞味呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",12,18,"但如今　神隨自己的意思、把肢體俱各安排在身上了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",12,19,"若都是一個肢體、身子在哪裡呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",12,20,"但如今肢體是多的、身子卻是一個。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",12,21,"眼不能對手說、我用不著你．頭也不能對腳說、我用不著你。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",12,22,"不但如此、身上肢體人以為軟弱的、更是不可少的．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",12,23,"身上肢體、我們看為不體面的、越發給他加上體面．不俊美的、越發得著俊美．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",12,24,"我們俊美的肢體、自然用不著裝飾．但　神配搭這身子、把加倍的體面給那有缺欠的肢體．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",12,25,"免得身上分門別類．總要肢體彼此相顧。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",12,26,"若一個肢體受苦、所有的肢體就一同受苦．若一個肢體得榮耀、所有的肢體就一同快樂。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",12,27,"你們就是基督的身子、並且各自作肢體。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",12,28,"　神在教會所設立的、第一是使徒．第二是先知．第三是教師．其次是行異能的．再次是得恩賜醫病的．幫助人的．治理事的．說方言的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",12,29,"豈都是使徒麼．豈都是先知麼．豈都是教師麼．豈都是行異能的麼．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",12,30,"豈都是得恩賜醫病的麼．豈都是說方言的麼．豈都是翻方言的麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",12,31,"你們要切切的求那更大的恩賜、我現今把最妙的道指示你們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",13,1,"我若能說萬人的方言、並天使的話語、卻沒有愛、我就成了鳴的鑼、響的鈸一般。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",13,2,"我若有先知講道之能、也明白各樣的奧秘、各樣的知識．而且有全備的信、叫我能夠移山、卻沒有愛、我就算不得甚麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",13,3,"我若將所有的賙濟窮人、又捨己身叫人焚燒、卻沒有愛、仍然與我無益。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",13,4,"愛是恆久忍耐、又有恩慈．愛是不嫉妒．愛是不自誇．不張狂．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",13,5,"不作害羞的事．不求自己的益處．不輕易發怒．不計算人的惡．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",13,6,"不喜歡不義．只喜歡真理．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",13,7,"凡事包容．凡事相信．凡事盼望．凡事忍耐。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",13,8,"愛是永不止息．先知講道之能、終必歸於無有．說方言之能、終必停止、知識也終必歸於無有。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",13,9,"我們現在所知道的有限、先知所講的也有限．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",13,10,"等那完全的來到、這有限的必歸於無有了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",13,11,"我作孩子的時候、話語像孩子、心思像孩子、意念像孩子．既成了人、就把孩子的事丟棄了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",13,12,"我們如今彷彿對著鏡子觀看、模糊不清．〔模糊不清原文作如同猜謎〕到那時、就要面對面了．我如今所知道的有限．到那時就全知道、如同主知道我一樣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",13,13,"如今常存的有信、有望、有愛、這三樣、其中最大的是愛。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",14,1,"你們要追求愛、也要切慕屬靈的恩賜、其中更要羨慕的、是作先知講道。〔原文作是說預言下同〕")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",14,2,"那說方言的、原不是對人說、乃是對　神說．因為沒有人聽出來．然而他在心靈裡、卻是講說各樣的奧秘。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",14,3,"但作先知講道的、是對人說、要造就、安慰、勸勉人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",14,4,"說方言的、是造就自己．作先知講道的、乃是造就教會。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",14,5,"我願意你們都說方言．更願意你們作先知講道．因為說方言的、若不翻出來、使教會被造就、那作先知講道的、就比他強了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",14,6,"弟兄們、我到你們那裡去、若只說方言、不用啟示、或知識、或預言、或教訓、給你們講解、我與你們有甚麼益處呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",14,7,"就是那有聲無氣的物、或簫、或琴、若發出來的聲音、沒有分別、怎能知道所吹所彈的是甚麼呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",14,8,"若吹無定的號聲、誰能預備打仗呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",14,9,"你們也是如此、舌頭若不說容易明白的話、怎能知道所說的是甚麼呢．這就是向空說話了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",14,10,"世上的聲音、或者甚多、卻沒有一樣是無意思的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",14,11,"我若不明白那聲音的意思、這說話的人必以我為化外之人、我也以他為化外之人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",14,12,"你們也是如此．既是切慕屬靈的恩賜、就當求多得造就教會的恩賜。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",14,13,"所以那說方言的、就當求著能翻出來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",14,14,"我若用方言禱告、是我的靈禱告．但我的悟性沒有果效。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",14,15,"這卻怎麼樣呢．我要用靈禱告、也要用悟性禱告．我要用靈歌唱、也要用悟性歌唱。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",14,16,"不然、你用靈祝謝、那在座不通方言的人、既然不明白你的話、怎能在你感謝的時候說阿們呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",14,17,"你感謝的固然是好、無奈不能造就別人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",14,18,"我感謝　神、我說方言比你們眾人還多．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",14,19,"但在教會中、寧可用悟性說五句教導人的話、強如說萬句方言。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",14,20,"弟兄們、在心志上不要作小孩子．然而在惡事上要作嬰孩．在心志上總要作大人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",14,21,"律法上記著、『主說、我要用外邦人的舌頭、和外邦人的嘴唇、向這百姓說話．雖然如此、他們還是不聽從我。』")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",14,22,"這樣看來、說方言、不是為信的人作證據、乃是為不信的人．作先知講道、不是為不信的人作證據、乃是為信的人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",14,23,"所以全教會聚在一處的時候、若都說方言、偶然有不通方言的、或是不信的人進來、豈不說你們癲狂了麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",14,24,"若都作先知講道、偶然有不信的、或是不通方言的人進來、就被眾人勸醒、被眾人審明．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",14,25,"他心裡的隱情顯露出來、就必將臉伏地、敬拜　神、說　神真是在你們中間了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",14,26,"弟兄們、這卻怎麼樣呢．你們聚會的時候、各人或有詩歌、或有教訓、或有啟示、或有方言、或有翻出來的話．凡事都當造就人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",14,27,"若有說方言的、只好兩個人、至多三個人、且要輪流著說、也要一個人翻出來．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",14,28,"若沒有人翻、就當在會中閉口．只對自己和　神說、就是了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",14,29,"至於作先知講道的、只好兩個人、或是三個人、其餘的就當慎思明辨。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",14,30,"若旁邊坐著的得了啟示、那先說話的就當閉口不言。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",14,31,"因為你們都可以一個一個的作先知講道、叫眾人學道理．叫眾人得勸勉。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",14,32,"先知的靈、原是順服先知的．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",14,33,"因為　神不是叫人混亂、乃是叫人安靜。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",14,34,"婦女在會中要閉口不言、像在聖徒的眾教會一樣．因為不准他們說話．他們總要順服、正如律法所說的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",14,35,"他們若要學甚麼、可以在家裡問自己的丈夫．因為婦女在會中說話原是可恥的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",14,36,"　神的道理、豈是從你們出來麼．豈是單臨到你們麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",14,37,"若有人以為自己是先知、或是屬靈的、就該知道、我所寫給你們的是主的命令。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",14,38,"若有不知道的、就由他不知道罷。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",14,39,"所以我弟兄們、你們要切慕作先知講道、也不要禁止說方言。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",14,40,"凡事都要規規矩矩的按著次序行。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",15,1,"弟兄們、我如今把先前所傳給你們的福音、告訴你們知道、這福音你們也領受了、又靠著站立得住．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",15,2,"並且你們若不是徒然相信、能以持守我所傳給你們的、就必因這福音得救。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",15,3,"我當日所領受又傳給你們的、第一、就是基督照聖經所說、為我們的罪死了．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",15,4,"而且埋葬了．又照聖經所說、第三天復活了．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",15,5,"並且顯給磯法看．然後顯給十二使徒看．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",15,6,"後來一時顯給五百多弟兄看、其中一大半到如今還在、卻也有已經睡了的．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",15,7,"以後顯給雅各看．再顯給眾使徒看．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",15,8,"末了也顯給我看．我如同未到產期而生的人一般。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",15,9,"我原是使徒中最小的、不配稱為使徒、因為我從前逼迫　神的教會。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",15,10,"然而我今日成了何等人、是蒙　神的恩纔成的．並且他所賜我的恩、不是徒然的．我比眾使徒格外勞苦．這原不是我、乃是　神的恩與我同在。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",15,11,"不拘是我是眾使徒、我們如此傳、你們也如此信了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",15,12,"既傳基督是從死裡復活了、怎麼在你們中間、有人說沒有死人復活的事呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",15,13,"若沒有死人復活的事、基督也就沒有復活了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",15,14,"若基督沒有復活、我們所傳的便是枉然、你們所信的也是枉然。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",15,15,"並且明顯我們是為　神妄作見證的．因我們見證　神是叫基督復活了．若死人真不復活、　神也就沒有叫基督復活了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",15,16,"因為死人若不復活、基督也就沒有復活了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",15,17,"基督若沒有復活、你們的信便是徒然．你們仍在罪裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",15,18,"就是在基督裡睡了的人也滅亡了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",15,19,"我們若靠基督、只在今生有指望、就算比眾人更可憐。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",15,20,"但基督已經從死裡復活、成為睡了之人初熟的果子。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",15,21,"死既是因一人而來、死人復活也是因一人而來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",15,22,"在亞當裡眾人都死了．照樣、在基督裡眾人也都要復活。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",15,23,"但各人是按著自己的次序復活。初熟的果子是基督．以後在他來的時候、是那些屬基督的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",15,24,"再後末期到了、那時、基督既將一切執政的、掌權的、有能的、都毀滅了、就把國交與父　神。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",15,25,"因為基督必要作王、等　神把一切仇敵、都放在他的腳下。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",15,26,"儘末了所毀滅的仇敵、就是死。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",15,27,"因為經上說、『　神叫萬物都服在他的腳下。』既說萬物都服了他、明顯那叫萬物服他的不在其內了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",15,28,"萬物既服了他、那時、子也要自己服那叫萬物服他的、叫　神在萬物之上、為萬物之主。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",15,29,"不然、那些為死人受洗的、將來怎樣呢．若死人總不復活、因何為他們受洗呢．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",15,30,"我們又因何時刻冒險呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",15,31,"弟兄們、我在我主基督耶穌裡、指著你們所誇的口、極力的說、我是天天冒死。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",15,32,"我若當日像尋常人、在以弗所同野獸戰鬥、那於我有甚麼益處呢．若死人不復活、我們就喫喫喝喝罷．因為明天要死了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",15,33,"你們不要自欺．濫交是敗壞善行。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",15,34,"你們要醒悟為善、不要犯罪．因為有人不認識　神．我說這話、是要叫你們羞愧。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",15,35,"或有人問、死人怎樣復活．帶著甚麼身體來呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",15,36,"無知的人哪、你所種的、若不死就不能生。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",15,37,"並且你所種的、不是那將來的形體、不過是子粒、即如麥子、或是別樣的穀．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",15,38,"但　神隨自己的意思、給他一個形體、並叫各等子粒、各有自己的形體。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",15,39,"凡肉體各有不同．人是一樣、獸又是一樣、鳥又是一樣、魚又是一樣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",15,40,"有天上的形體、也有地上的形體．但天上形體的榮光是一樣、地上形體的榮光又是一樣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",15,41,"日有日的榮光、月有月的榮光、星有星的榮光．這星和那星的榮光、也有分別。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",15,42,"死人復活也是這樣．所種的是必朽壞的、復活的是不朽壞的．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",15,43,"所種的是羞辱的、復活的是榮耀的．所種的是軟弱的、復活的是強壯的．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",15,44,"所種的是血氣的身體、復活的是靈性的身體．若有血氣的身體、也必有靈性的身體。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",15,45,"經上也是這樣記著說、『首先的人亞當、成了有靈的活人。』〔靈或作血氣〕末後的亞當、成了叫人活的靈。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",15,46,"但屬靈的不在先、屬血氣的在先．以後纔有屬靈的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",15,47,"頭一個人是出於地、乃屬土．第二個人是出於天。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",15,48,"那屬土的怎樣、凡屬土的也就怎樣．屬天的怎樣、凡屬天的也就怎樣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",15,49,"我們既有屬土的形狀、將來也必有屬天的形狀。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",15,50,"弟兄們、我告訴你們說、血肉之體、不能承受　神的國．必朽壞的、不能承受不朽壞的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",15,51,"我如今把一件奧秘的事告訴你們．我們不是都要睡覺、乃是都要改變、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",15,52,"就在一霎時、眨眼之間、號筒末次吹響的時候．因號筒要響、死人要復活成為不朽壞的、我們也要改變。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",15,53,"這必朽壞的、總要變成不朽壞的．〔變成原文作穿下同〕這必死的、總要變成不死的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",15,54,"這必朽壞的、既變成不朽壞的．這必死的、既變成不死的．那時經上所記、死被得勝吞滅的話就應驗了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",15,55,"死阿、你得勝的權勢在那裡．死阿、你的毒鉤在那裡．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",15,56,"死的毒鉤就是罪．罪的權勢就是律法。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",15,57,"感謝　神、使我們藉著我們的主耶穌基督得勝。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",15,58,"所以我親愛的弟兄們、你們務要堅固不可搖動、常常竭力多作主工、因為知道你們的勞苦、在主裡面不是徒然的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",16,1,"論到為聖徒捐錢、我從前怎樣吩咐加拉太的眾教會、你們也當怎樣行。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",16,2,"每逢七日的第一日、各人要照自己的進項抽出來留著．免得我來的時候現湊。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",16,3,"及至我來到了、你們寫信舉薦誰、我就打發他們、把你們的捐資送到耶路撒冷去．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",16,4,"若我也該去、他們可以和我同去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",16,5,"我要從馬其頓經過．既經過了、就要到你們那裡去．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",16,6,"或者和你們同住幾時、或者也過冬．無論我往那裡去、你們就可以給我送行。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",16,7,"我如今不願意路過見你們．主若許我、我就指望和你們同住幾時。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",16,8,"但我要仍舊住在以弗所、直等到五旬節．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",16,9,"因為有寬大又有功效的門、為我開了、並且反對的人也多。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",16,10,"若是提摩太來到、你們要留心、叫他在你們那裡無所懼怕．因為他勞力作主的工、像我一樣．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",16,11,"所以無論誰、都不可藐視他．只要送他平安前行、叫他到我這裡來．因我指望他和弟兄們同來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",16,12,"至於兄弟亞波羅、我再三的勸他、同弟兄們到你們那裡去．但這時他決不願意去．幾時有了機會他必去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",16,13,"你們務要儆醒、在真道上站立得穩、要作大丈夫、要剛強。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",16,14,"凡你們所作的、都要憑愛心而作。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",16,15,"弟兄們、你們曉得司提反一家、是亞該亞初結的果子．並且他們專以服事聖徒為念．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",16,16,"我勸你們順服這樣的人、並一切同工同勞的人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",16,17,"司提反、和福徒拿都、並亞該古、到這裡來、我很喜歡．因為你們待我有不及之處、他們補上了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",16,18,"他們叫我和你們心裡都快活．這樣的人、你們務要敬重。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",16,19,"亞西亞的眾教會問你們安。亞居拉和百基拉、並在他們家裡的教會、因主多多的問你們安。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",16,20,"眾弟兄都問你們安。你們要親嘴問安、彼此務要聖潔。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",16,21,"我保羅親筆問安。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",16,22,"若有人不愛主、這人可詛可咒。主必要來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",16,23,"願主耶穌基督的恩、常與你們眾人同在。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Co",16,24,"我在基督耶穌裡的愛、與你們眾人同在。阿們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",1,1,"奉　神旨意、作基督耶穌使徒的保羅、和兄弟提摩太、寫信給在哥林多　神的教會、並亞該亞遍處的眾聖徒．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",1,2,"願恩惠平安、從　神我們的父和主耶穌基督歸與你們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",1,3,"願頌讚歸與我們的主耶穌基督的父　神、就是發慈悲的父、賜各樣安慰的　神．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",1,4,"我們在一切患難中、他就安慰我們、叫我們能用　神所賜的安慰、去安慰那遭各樣患難的人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",1,5,"我們既多受基督的苦楚、就靠基督多得安慰。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",1,6,"我們受患難呢、是為叫你們得安慰得拯救．我們得安慰呢、也是為叫你們得安慰．這安慰能叫你們忍受我們所受的那樣苦楚。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",1,7,"我們為你們所存的盼望是確定的．因為知道你們既是同受苦楚、也必同得安慰。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",1,8,"弟兄們、我們不要你們不曉得、我們從前在亞西亞遭遇苦難、被壓太重、力不能勝、甚至連活命的指望都絕了．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",1,9,"自己心裡也斷定是必死的、叫我們不靠自己、只靠叫死人復活的　神．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",1,10,"他曾救我們脫離那極大的死亡、現在仍要救我們、並且我們指望他將來還要救我們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",1,11,"你們以祈禱幫助我們、好叫許多人為我們謝恩、就是為我們因許多人所得的恩。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",1,12,"我們所誇的、是自己的良心、見證我們憑著　神的聖潔和誠實、在世為人、不靠人的聰明、乃靠　神的恩惠、向你們更是這樣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",1,13,"我們現在寫給你們的話、並不外乎你們所念的、所認識的、我也盼望你們到底還是要認識．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",1,14,"正如你們已經有幾分認識我們．以我們誇口、好像我們在我們主耶穌的日子、以你們誇口一樣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",1,15,"我既然這樣深信、就早有意到你們那裡去、叫你們再得益處．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",1,16,"也要從你們那裡經過、往馬其頓去、再從馬其頓回到你們那裡、叫你們給我送行往猶太去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",1,17,"我有此意、豈是反復不定麼．我所起的意、豈是從情慾起的、叫我忽是忽非麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",1,18,"我指著信實的　神說、我們向你們所傳的道、並沒有是而又非的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",1,19,"因為我和西拉、並提摩太、在你們中間所傳　神的兒子耶穌基督、總沒有是而又非的、在他只有一是。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",1,20,"　神的應許、不論有多少、在基督都是是的．所以藉著他也都是實在的、〔實在原文作阿們〕叫　神因我們得榮耀。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",1,21,"那在基督裡堅固我們和你們、並且膏我們的、就是　神．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",1,22,"他又用印印了我們、並賜聖靈在我們心裡作憑據。〔原文作質〕")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",1,23,"我呼籲　神給我的心作見證、我沒有往哥林多去是為要寬容你們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",1,24,"我們並不是轄管你們的信心、乃是幫助你們的快樂．因為你們憑信纔站立得住。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",2,1,"我自己定了主意、再到你們那裡去、必須大家沒有憂愁。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",2,2,"倘若我叫你們憂愁、除了我叫那憂愁的人以外、誰能叫我快樂呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",2,3,"我曾把這事寫給你們、恐怕我到的時候、應該叫我快樂的那些人、反倒叫我憂愁．我也深信、你們眾人都以我的快樂為自己的快樂。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",2,4,"我先前心裡難過痛苦、多多的流淚、寫信給你們．不是叫你們憂愁、乃是叫你們知道我格外的疼愛你們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",2,5,"若有叫人憂愁的、他不但叫我憂愁、也是叫你們眾人有幾分憂愁、我說幾分、恐怕說得太重。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",2,6,"這樣的人、受了眾人的責罰、也就夠了．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",2,7,"倒不如赦免他、安慰他、免得他憂愁太過、甚至沉淪了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",2,8,"所以我勸你們、要向他顯出堅定不移的愛心來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",2,9,"為此我先前也寫信給你們、要試驗你們、看你們凡事順從不順從。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",2,10,"你們赦免誰、我也赦免誰、我若有所赦免的、是在基督面前為你們赦免的．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",2,11,"免得撒但趁著機會勝過我們．因我們並非不曉得他的詭計。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",2,12,"我從前為基督的福音到了特羅亞、主也給我開了門。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",2,13,"那時因為沒有遇見兄弟提多、我心裡不安、便辭別那裡的人往馬其頓去了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",2,14,"感謝　神、常帥領我們在基督裡誇勝、並藉著我們在各處顯揚那因認識基督而有的香氣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",2,15,"因為我們在　神面前、無論在得救的人身上、或滅亡的人身上、都有基督馨香之氣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",2,16,"在這等人、就作了死的香氣叫他死．在那等人、就作了活的香氣叫他活．這事誰能當得起呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",2,17,"我們不像那許多人、為利混亂　神的道．乃是由於誠實、由於　神、在　神面前憑著基督講道。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",3,1,"我們豈是又舉薦自己麼．豈像別人、用人的薦信給你們、或用你們的薦信給人麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",3,2,"你們就是我們的薦信、寫在我們的心裡、被眾人所知道所念誦的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",3,3,"你們明顯是基督的信、藉著我們修成的．不是用墨寫的、乃是用永生　神的靈寫的．不是寫在石版上、乃是寫在心版上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",3,4,"我們因基督所以在　神面前纔有這樣的信心．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",3,5,"並不是我們憑自己能承擔甚麼事、我們所能承擔的、乃是出於　神．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",3,6,"他叫我們能承當這新約的執事．不是憑著字句、乃是憑著精意．因為那字句是叫人死、精意是叫人活。〔精意或作聖靈〕")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",3,7,"那用字刻在石頭上屬死的職事、尚且有榮光、甚至以色列人因摩西面上的榮光、不能定睛看他的臉．這榮光原是漸漸退去的．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",3,8,"何況那屬靈的職事、豈不更有榮光麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",3,9,"若是定罪的職事有榮光、那稱義的職事、榮光就越發大了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",3,10,"那從前有榮光的、因這極大的榮光、就算不得有榮光了．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",3,11,"若那廢掉的有榮光、這長存的就更有榮光了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",3,12,"我們既有這樣的盼望、就大膽講說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",3,13,"不像摩西將帕子蒙在臉上、叫以色列人不能定睛看到那將廢者的結局．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",3,14,"但他們的心地剛硬．直到今日誦讀舊約的時候、這帕子還沒有揭去．這帕子在基督裡已經廢去了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",3,15,"然而直到今日、每逢誦讀摩西書的時候、帕子還在他們心上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",3,16,"但他們的心幾時歸向主、帕子就幾時除去了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",3,17,"主就是那靈、主的靈在那裡、那裡就得以自由。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",3,18,"我們眾人既然敞著臉、得以看見主的榮光、好像從鏡子裡返照、就變成主的形狀、榮上加榮、如同從主的靈變成的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",4,1,"我們既然蒙憐憫、受了這職分、就不喪膽．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",4,2,"乃將那些暗昧可恥的事棄絕了、不行詭詐、不謬講　神的道理．只將真理表明出來、好在　神面前把自己薦與各人的良心。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",4,3,"如果我們的福音蒙蔽、就是蒙蔽在滅亡的人身上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",4,4,"此等不信之人、被這世界的　神弄瞎了心眼、不叫基督榮耀福音的光照著他們．基督本是　神的像。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",4,5,"我們原不是傳自己、乃是傳基督耶穌為主、並且自己因耶穌作你們的僕人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",4,6,"那吩咐光從黑暗裡照出來的　神、已經照在我們心裡、叫我們得知　神榮耀的光、顯在耶穌基督的面上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",4,7,"我們有這寶貝放在瓦器裡、要顯明這莫大的能力、是出於　神、不是出於我們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",4,8,"我們四面受敵、卻不被困住．心裡作難、卻不至失望．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",4,9,"遭逼迫、卻不被丟棄．打倒了、卻不至死亡．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",4,10,"身上常帶著耶穌的死、使耶穌的生、也顯明在我們身上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",4,11,"因為我們這活著的人、是常為耶穌被交於死地、使耶穌的生、在我們這必死的身上顯明出來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",4,12,"這樣看來、死是在我們身上發動、生卻在你們身上發動。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",4,13,"但我們既有信心、正如經上記著說、『我因信、所以如此說話。』我們也信、所以也說話．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",4,14,"自己知道、那叫主耶穌復活的、也必叫我們與耶穌一同復活、並且叫我們與你們一同站在他面前。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",4,15,"凡事都是為你們、好叫恩惠因人多越發加增、感謝格外顯多、以致榮耀歸與　神。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",4,16,"所以我們不喪膽．外體雖然毀壞、內心卻一天新似一天。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",4,17,"我們這至暫至輕的苦楚、要為我們成就極重無比永遠的榮耀。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",4,18,"原來我們不是顧念所見的、乃是顧念所不見的．因為所見的是暫時的、所不見的是永遠的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",5,1,"我們原知道、我們這地上的帳棚若拆毀了、必得　神所造、不是人手所造、在天上永存的房屋。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",5,2,"我們在這帳棚裡歎息、深想得那從天上來的房屋、好像穿上衣服．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",5,3,"倘若穿上、被遇見的時候就不至於赤身了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",5,4,"我們在這帳棚裡、歎息勞苦、並非願意脫下這個、乃是願意穿上那個、好叫這必死的被生命吞滅了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",5,5,"為此培植我們的就是　神、他又賜給我們聖靈作憑據。〔原文作質〕")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",5,6,"所以我們時常坦然無懼、並且曉得我們住在身內便與主相離。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",5,7,"因我們行事為人、是憑著信心、不是憑著眼見。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",5,8,"我們坦然無懼、是更願意離開身體與主同住。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",5,9,"所以無論是住在身內、離開身外、我們立了志向、要得主的喜悅。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",5,10,"因為我們眾人、必要在基督臺前顯露出來、叫各人按著本身所行的、或善或惡受報。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",5,11,"我們既知道主是可畏的、所以勸人、但我們在　神面前是顯明的、盼望在你們的良心裡也是顯明的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",5,12,"我們不是向你們再舉薦自己、乃是叫你們因我們有可誇之處、好對那憑外貌不憑內心誇口的人、有言可答。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",5,13,"我們若果顛狂、是為　神．若果謹守、是為你們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",5,14,"原來基督的愛激勵我們．因我們想一人既替眾人死、眾人就都死了．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",5,15,"並且他替眾人死、是叫那些活著的人、不再為自己活、乃為替他們死而復活的主活。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",5,16,"所以我們從今以後、不憑著外貌〔原文作肉體本節同〕認人了．雖然憑著外貌認過基督、如今卻不再這樣認他了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",5,17,"若有人在基督裡、他就是新造的人．舊事已過、都變成新的了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",5,18,"一切都是出於　神、他藉著基督使我們與他和好、又將勸人與他和好的職分賜給我們．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",5,19,"這就是　神在基督裡、叫世人與自己和好、不將他們的過犯歸到他們身上．並且將這和好的道理託付了我們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",5,20,"所以我們作基督的使者、就好像　神藉我們勸你們一般．我們替基督求你們與　神和好。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",5,21,"　神使那無罪的、〔無罪原文作不知罪〕替我們成為罪．好叫我們在他裡面成為　神的義。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",6,1,"我們與　神同工的、也勸你們、不可徒受他的恩典．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",6,2,"因為他說、『在悅納的時候、我應允了你．在拯救的日子、我搭救了你。』看哪、現在正是悅納的時候、現在正是拯救的日子。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",6,3,"我們凡事都不叫人有妨礙、免得這職分被人毀謗．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",6,4,"反倒在各樣的事上、表明自己是　神的用人、就如在許多的忍耐、患難、窮乏、困苦、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",6,5,"鞭打、監禁、擾亂、勤勞、儆醒、不食、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",6,6,"廉潔、知識、恆忍、恩慈、聖靈的感化、無偽的愛心、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",6,7,"真實的道理、　神的大能．仁義的兵器在左在右．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",6,8,"榮耀羞辱、惡名美名．似乎是誘惑人的、卻是誠實的．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",6,9,"似乎不為人所知、卻是人所共知的．似乎要死、卻是活著的．似乎受責罰、卻是不至喪命的．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",6,10,"似乎憂愁、卻是常常快樂的．似乎貧窮、卻是叫許多人富足的．似乎一無所有、卻是樣樣都有的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",6,11,"哥林多人哪、我們向你們、口是張開的、心是寬宏的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",6,12,"你們狹窄、原不在乎我們、是在乎自己的心腸狹窄。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",6,13,"你們也要照樣用寬宏的心報答我．我這話正像對自己的孩子說的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",6,14,"你們和不信的原不相配、不要同負一軛．義和不義有甚麼相交呢．光明和黑暗有甚麼相通呢．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",6,15,"基督和彼列〔彼列就是撒但的別名〕有甚麼相和呢．信主的和不信主的有甚麼相干呢．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",6,16,"　神的殿和偶像有甚麼相同呢．因為我們是永生　神的殿．就如　神曾說、『我要在他們中間居住、在他們中間來往．我要作他們的　神、他們要作我的子民。』")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",6,17,"又說、『你們務要從他們中間出來、與他們分別、不要沾不潔淨的物、我就收納你們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",6,18,"我要作你們的父、你們要作我的兒女。』這是全能的主說的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",7,1,"親愛的弟兄阿、我們既有這等應許、就當潔淨自己、除去身體靈魂一切的污穢、敬畏　神、得以成聖。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",7,2,"你們要心地寬大收納我們．我們未曾虧負誰、未曾敗壞誰、未曾佔誰的便宜。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",7,3,"我說這話、不是要定你們的罪．我已經說過、你們常在我們心裡、情願與你們同生同死。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",7,4,"我大大的放膽、向你們說話．我因你們多多誇口、滿得安慰．我們在一切患難中分外的快樂。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",7,5,"我們從前就是到了馬其頓的時候、身體也不得安寧、周圍遭患難、外有爭戰、內有懼怕．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",7,6,"但那安慰喪氣之人的　神、藉著提多來安慰了我們．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",7,7,"不但藉著他來、也藉著他從你們所得的安慰、安慰了我們．因他把你們的想念、哀慟、和向我的熱心、都告訴了我、叫我更加歡喜。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",7,8,"我先前寫信叫你們憂愁．我後來雖然懊悔、如今卻不懊悔．因我知道那信叫你們憂愁、不過是暫時的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",7,9,"如今我歡喜、不是因你們憂愁、是因你們從憂愁中生出懊悔來．你們依著　神的意思憂愁、凡事就不至於因我們受虧損了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",7,10,"因為依著　神的意思憂愁、就生出沒有後悔的懊悔來、以致得救．但世俗的憂愁、是叫人死。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",7,11,"你看、你們依著　神的意思憂愁、從此就生出何等的殷勤、自訴、自恨、恐懼、想念、熱心、責罰．〔或作自責〕在這一切事上你們都表明自己是潔淨的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",7,12,"我雖然從前寫信給你們、卻不是為那虧負人的、也不是為那受人虧負的、乃要在　神面前、把你們顧念我們的熱心、表明出來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",7,13,"故此我們得了安慰、並且在安慰之中、因你們眾人使提多心裡暢快歡喜、我們就更加歡喜了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",7,14,"我若對提多誇獎了你們甚麼、也覺得沒有慚愧．因我對提多誇獎你們的話、成了真的．正如我對你們所說的話、也都是真的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",7,15,"並且提多想起你們眾人的順服、是怎樣恐懼戰兢的接待他、他愛你們的心腸就越發熱了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",7,16,"我如今歡喜、能在凡事上為你們放心。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",8,1,"弟兄們、我把　神賜給馬其頓眾教會的恩告訴你們．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",8,2,"就是他們在患難中受大試煉的時候、仍有滿足的快樂、在極窮之間、還格外顯出他們樂捐的厚恩。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",8,3,"我可以證明他們是按著力量、而且也過了力量、自己甘心樂意的捐助．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",8,4,"再三的求我們、准他們在這供給聖徒的恩情上有分．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",8,5,"並且他們所作的、不但照我們所想望的、更照　神的旨意、先把自己獻給主、又歸附了我們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",8,6,"因此我勸提多、既然在你們中間開辦這慈惠的事、就當辦成了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",8,7,"你們既然在信心、口才、知識、熱心、和待我們的愛心上、都格外顯出滿足來、就當在這慈惠的事上、也格外顯出滿足來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",8,8,"我說這話、不是吩咐你們、乃是藉著別人的熱心、試驗你們愛心的實在。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",8,9,"你們知道我們主耶穌基督的恩典、他本來富足、卻為你們成了貧窮、叫你們因他的貧窮、可以成為富足。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",8,10,"我在這事上把我的意見告訴你們、是與你們有益．因為你們下手辦這事．而且起此心意、已經有一年了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",8,11,"如今就當辦成這事．既有願作的心、也當照你們所有的去辦成。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",8,12,"因為人若有願作的心、必蒙悅納、乃是照他所有的、並不是照他所無的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",8,13,"我原不是要別人輕省、你們受累、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",8,14,"乃要均平．就是要你們的富餘、現在可以補他們的不足、使他們的富餘、將來也可以補你們的不足、這就均平了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",8,15,"如經上所記、『多收的也沒有餘、少收的也沒有缺。』")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",8,16,"多謝　神、感動提多的心、叫他待你們殷勤、像我一樣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",8,17,"他固然是聽了我的勸．但自己更是熱心、情願往你們那裡去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",8,18,"我們還打發一位兄弟和他同去．這人在福音上得了眾教會的稱讚．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",8,19,"不但這樣、他也被眾教會挑選、和我們同行、把所託與我們的這捐貲送到了、可以榮耀主、又表明我們樂意的心。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",8,20,"這就免得有人因我們收的捐銀很多、就挑我們的不是。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",8,21,"我們留心行光明的事、不但在主面前、就在人面前、也是這樣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",8,22,"我們又打發一位兄弟同去．這人的熱心、我們在許多事上、屢次試驗過、現在他因為深信你們、就更加熱心了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",8,23,"論到提多、他是我的同伴、一同為你們勞碌的．論到那兩位弟兄、他們是眾教會的使者、是基督的榮耀。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",8,24,"所以你們務要在眾教會面前、顯明你們愛心的憑據、並我所誇獎你們的憑據。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",9,1,"論到供給聖徒的事、我不必寫信給你們．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",9,2,"因為我知道你們樂意的心、常對馬其頓人誇獎你們、說亞該亞人預備好了、已經有一年了．並且你們的熱心激動了許多人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",9,3,"但我打發那幾位弟兄去、要叫你們照我的話預備妥當．免得我們在這事上誇獎你們的話落了空。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",9,4,"萬一有馬其頓人與我同去、見你們沒有預備、就叫我們所確信的、反成了羞愧．你們羞愧、更不用說了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",9,5,"因此、我想不得不求那幾位弟兄、先到你們那裡去、把從前所應許的捐貲、預備妥當、就顯出你們所捐的、是出於樂意、不是出於勉強．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",9,6,"少種的少收、多種的多收．這話是真的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",9,7,"各人要隨本心所酌定的．不要作難、不要勉強．因為捐得樂意的人、是　神所喜愛的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",9,8,"　神能將各樣的恩惠、多多的加給你們．使你們凡事常常充足、能多行各樣善事．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",9,9,"如經上所記、『他施捨錢財、賙濟貧窮．他的仁義存到永遠。』")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",9,10,"那賜種給撒種的、賜糧給人喫的、必多多加給你們種地的種子、又增添你們仁義的果子．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",9,11,"叫你們凡事富足、可以多多施捨、就藉著我們使感謝歸於　神。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",9,12,"因為辦這供給的事、不但補聖徒的缺乏、而且叫許多人越發感謝　神。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",9,13,"他們從這供給的事上得了憑據、知道你們承認基督順服他的福音、多多的捐錢給他們和眾人、便將榮耀歸與　神．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",9,14,"他們也因　神極大的恩賜、顯在你們心裡、就切切的想念你們、為你們祈禱。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",9,15,"感謝　神、因他有說不盡的恩賜。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",10,1,"我保羅、就是與你們見面的時候是謙卑的、不在你們那裡的時候向你們是勇敢的、如今親自藉著基督的溫柔和平、勸你們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",10,2,"有人以為我是憑著血氣行事、我也以為必須用勇敢待這等人、求你們不要叫我在你們那裡的時候、有這樣的勇敢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",10,3,"因為我們雖然在血氣中行事、卻不憑著血氣爭戰．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",10,4,"我們爭戰的兵器、本不是屬血氣的、乃是在　神面前有能力、可以攻破堅固的營壘、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",10,5,"將各樣的計謀、各樣攔阻人認識　神的那些自高之事、一概攻破了、又將人所有的心意奪回、使他都順服基督．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",10,6,"並且我已經預備好了、等你們十分順服的時候、要責罰那一切不順服的人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",10,7,"你們是看眼前的麼．倘若有人自信是屬基督的、他要再想想、他如何屬基督、我們也是如何屬基督的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",10,8,"主賜給我們權柄、是要造就你們、並不是要敗壞你們、我就是為這權柄稍微誇口、也不至於慚愧．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",10,9,"我說這話免得你們以為我寫信是要威嚇你們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",10,10,"因為有人說、他的信、又沉重、又利害．及至見面、卻是氣貌不揚、言語粗俗的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",10,11,"這等人當想、我們不在那裡的時候、信上的言語如何、見面的時候、行事也必如何。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",10,12,"因為我們不敢將自己和那自薦的人同列相比．他們用自己度量自己、用自己比較自己、乃是不通達的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",10,13,"我們不願意分外誇口、只要照　神所量給我們的界限、搆到你們那裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",10,14,"我們並非過了自己的界限、好像搆不到你們那裡．因為我們早到你們那裡、傳了基督的福音。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",10,15,"我們不仗著別人所勞碌的、分外誇口．但指望你們信心增長的時候、所量給我們的界限、就可以因著你們更加開展、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",10,16,"得以將福音傳到你們以外的地方、並不是在別人界限之內、藉著他現成的事誇口。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",10,17,"但誇口的當指著主誇口。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",10,18,"因為蒙悅納的、不是自己稱許的、乃是主所稱許的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",11,1,"但願你們寬容我這一點愚妄．其實你們原是寬容我的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",11,2,"我為你們起的憤恨、原是　神那樣的憤恨．因為我曾把你們許配一個丈夫、要把你們如同貞潔的童女、獻給基督。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",11,3,"我只怕你們的心或偏於邪、失去那向基督所存純一清潔的心、就像蛇用詭詐誘惑了夏娃一樣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",11,4,"假如有人來、另傳一個耶穌、不是我們所傳過的．或者你們另受一個靈、不是你們所受過的．或者另得一個福音、不是你們所得過的．你們容讓他也就罷了．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",11,5,"但我想、我一點不在那些最大的使徒以下。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",11,6,"我的言語雖然粗俗、我的知識卻不粗俗．這是我們在凡事上、向你們眾人顯明出來的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",11,7,"我因為白白傳　神的福音給你們、就自居卑微、叫你們高升、這算是我犯罪麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",11,8,"我虧負了別的教會、向他們取了工價來、給你們效力。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",11,9,"我在你們那裡缺乏的時候、並沒有累著你們一個人．因我所缺乏的、那從馬其頓來的弟兄們都補足了．我向來凡事謹守、後來也必謹守、總不至於累著你們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",11,10,"既有基督的誠實在我裡面、就無人能在亞該亞一帶地方阻擋我這自誇。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",11,11,"為甚麼呢．是因我不愛你們麼．這有　神知道。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",11,12,"我現在所作的、後來還要作、為要斷絕那些尋機會人的機會、使他們在所誇的事上、也不過與我們一樣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",11,13,"那等人是假使徒、行事詭詐、裝作基督使徒的模樣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",11,14,"這也不足為怪．因為連撒但也裝作光明的天使。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",11,15,"所以他的差役、若裝作仁義的差役、也不算希奇．他們的結局、必然照著他們的行為。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",11,16,"我再說、人不可把我看作愚妄的．縱然如此、也要把我當作愚妄人接納、叫我可以略略自誇。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",11,17,"我說的話、不是奉主命說的、乃是像愚妄人放膽自誇．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",11,18,"既有好些人憑著血氣自誇、我也要自誇了．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",11,19,"你們既是精明人、就能甘心忍耐愚妄人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",11,20,"假若有人強你們作奴僕、或侵吞你們、或擄掠你們、或悔慢你們、或打你們的臉、你們都能忍耐他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",11,21,"我說這話、是羞辱自己．好像我們從前是軟弱的．然而人在何事上勇敢、（我說句愚妄話）我也勇敢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",11,22,"他們是希伯來人麼．我也是。他們是以色列人麼．我也是。他們是亞伯拉罕的後裔麼．我也是。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",11,23,"他們是基督的僕人麼．（我說句狂話）我更是。我比他們多受勞苦、多下監牢、受鞭打是過重的、冒死是屢次有的．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",11,24,"被猶太人鞭打五次、每次四十、減去一下．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",11,25,"被棍打了三次、被石頭打了一次、遇著船壞三次、一晝一夜在深海裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",11,26,"又屢次行遠路、遭江河的危險、盜賊的危險、同族的危險、外邦人的危險、城裡的危險、曠野的危險、海中的危險、假弟兄的危險。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",11,27,"受勞碌、受困苦、多次不得睡、又飢又渴、多次不得食、受寒冷、赤身露體。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",11,28,"除了這外面的事、還有為眾教會掛心的事、天天壓在我身上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",11,29,"有誰軟弱、我不軟弱呢．有誰跌倒、我不焦急呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",11,30,"我若必須自誇、就誇那關乎我軟弱的事便了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",11,31,"那永遠可稱頌之主耶穌的父　神、知道我不說謊。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",11,32,"在大馬色亞哩達王手下的提督、把守大馬色城要捉拿我．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",11,33,"我就從窗戶中、在筐子裡從城牆上被人縋下去、脫離了他的手。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",12,1,"我自誇固然無益、但我是不得已的．如今我要說到主的顯現和啟示。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",12,2,"我認得一個在基督裡的人、他前十四年被提到第三層天上去．或在身內、我不知道．或在身外、我也不知道．只有　神知道。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",12,3,"我認得這人、或在身內、或在身外、我都不知道．只有　神知道。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",12,4,"他被提到樂園裡、聽見隱秘的言語、是人不可說的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",12,5,"為這人、我要誇口．但是為我自己、除了我的軟弱以外、我並不誇口。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",12,6,"我就是願意誇口、也不算狂．因為我必說實話．只是我禁止不說、恐怕有人把我看高了、過於他在我身上所看見所聽見的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",12,7,"又恐怕我因所得的啟示甚大、就過於自高、所以有一根刺加在我肉體上、就是撒但的差役、要攻擊我、免得我過於自高。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",12,8,"為這事、我三次求過主、叫這刺離開我。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",12,9,"他對我說、我的恩典夠你用的．因為我的能力、是在人的軟弱上顯得完全．所以我更喜歡誇自己的軟弱、好叫基督的能力覆庇我．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",12,10,"我為基督的緣故、就以軟弱、凌辱、急難、逼迫、困苦、為可喜樂的．因我甚麼時候軟弱、甚麼時候就剛強了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",12,11,"我成了愚妄人、是被你們強逼的．我本該被你們稱許纔是．我雖算不了甚麼、卻沒有一件事在那些最大的使徒以下。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",12,12,"我在你們中間、用百般的忍耐、藉著神蹟奇事異能、顯出使徒的憑據來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",12,13,"除了我不累著你們這一件事、你們還有甚麼事不及別的教會呢．這不公之處、求你們饒恕我罷。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",12,14,"如今我打算第三次到你們那裡去、也必不累著你們．因我所求的是你們、不是你們的財物．兒女不該為父母積財、父母該為兒女積財。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",12,15,"我也甘心樂意為你們的靈魂費財費力。難道我越發愛你們、就越發少得你們的愛麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",12,16,"罷了、我自己並沒有累著你們、你們卻有人說、我是詭詐、用心計牢籠你們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",12,17,"我所差到你們那裡去的人、我藉著他們一個人佔過你們的便宜麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",12,18,"我勸了提多到你們那裡去、又差那位兄弟與他同去．提多佔過你們的便宜麼．我們行事、不同是一個心靈麼．不同是一個腳蹤麼。〔心靈或作聖靈〕")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",12,19,"你們到如今、還想我們是向你們分訴．我們本是在基督裡當　神面前說話。親愛的弟兄阿、一切的事、都是為造就你們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",12,20,"我怕我再來的時候、見你們不合我所想望的、你們見我也不合你們所想望的．又怕有分爭、嫉妒、惱怒、結黨、毀謗、讒言、狂傲、混亂的事．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",12,21,"且怕我來的時候、我的　神叫我在你們面前慚愧．又因許多人從前犯罪、行污穢姦淫邪蕩的事、不肯悔改、我就憂愁。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",13,1,"這是我第三次要到你們那裡去。憑兩三個人的口作見證、句句都要定準。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",13,2,"我從前說過、如今不在你們那裡又說、正如我第二次見你們的時候所說的一樣、就是對那犯了罪的、和其餘的人說、我若再來必不寬容。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",13,3,"你們既然尋求基督在我裡面說話的憑據、我必不寬容．因為基督在你們身上、不是軟弱的．在你們裡面、是有大能的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",13,4,"他因軟弱被釘在十字架上、卻因　神的大能、仍然活著．我們也是這樣同他軟弱、但因　神向你們所顯的大能、也必與他同活。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",13,5,"你們總要自己省察有信心沒有．也要自己試驗．豈不知你們若不是可棄絕的、就有耶穌基督在你們心裡麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",13,6,"我卻盼望你們曉得我們不是可棄絕的人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",13,7,"我們求　神、叫你們一件惡事都不作．這不是要顯明我們是蒙悅納的、是要你們行事端正、任憑人看我們是被棄絕的罷。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",13,8,"我們凡事不能敵擋真理、只能扶助真理。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",13,9,"即使我們軟弱、你們剛強、我們也歡喜．並且我們所求的、就是你們作完全人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",13,10,"所以我不在你們那裡的時候、把這話寫給你們、好叫我見你們的時候、不用照主所給我的權柄、嚴厲的待你們．這權柄原是為造就人、並不是為敗壞人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",13,11,"還有末了的話、願弟兄們都喜樂．要作完全人．要受安慰．要同心合意．要彼此和睦．如此仁愛和平的　神、必常與你們同在。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",13,12,"你們親嘴問安．彼此務要聖潔。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",13,13,"眾聖徒都問你們安。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Co",13,14,"願主耶穌基督的恩惠、　神的慈愛、聖靈的感動、常與你們眾人同在。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",1,1,"作使徒的保羅、（不是由於人、也不是藉著人、乃是藉著耶穌基督、與叫他從死裡復活的父　神）")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",1,2,"和一切與我同在的眾弟兄、寫信給加拉太的各教會。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",1,3,"願恩惠平安、從父　神與我們的主耶穌基督、歸與你們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",1,4,"基督照我們父　神的旨意為我們的罪捨己、要救我們脫離這罪惡的世代．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",1,5,"但願榮耀歸於　神直到永永遠遠。阿們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",1,6,"我希奇你們這麼快離開那藉著基督之恩召你們的、去從別的福音．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",1,7,"那並不是福音、不過有些人攪擾你們、要把基督的福音更改了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",1,8,"但無論是我們、是天上來的使者、若傳福音給你們、與我們所傳給你們的不同、他就應當被咒詛。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",1,9,"我們已經說了、現在又說、若有人傳福音給你們、與你們所領受的不同、他就應當被咒詛。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",1,10,"我現在是要得人的心呢、還是要得　神的心呢．我豈是討人的喜歡麼．若仍舊討人的喜歡、我就不是基督的僕人了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",1,11,"弟兄們、我告訴你們、我素來所傳的福音、不是出於人的意思。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",1,12,"因為我不是從人領受的、也不是人教導我的、乃是從耶穌基督啟示來的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",1,13,"你們聽見我從前在猶太教中所行的事、怎樣極力逼迫殘害　神的教會。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",1,14,"我又在猶太教中、比我本國許多同歲的人更有長進、為我祖宗的遺傳更加熱心。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",1,15,"然而那把我從母腹裡分別出來、又施恩召我的　神、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",1,16,"既然樂意將他兒子啟示在我心裡、叫我把他傳在外邦人中、我就沒有與屬血氣的人商量、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",1,17,"也沒有上耶路撒冷去、見那些比我先作使徒的．惟獨往亞拉伯去．後又回到大馬色。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",1,18,"過了三年、纔上耶路撒冷去見磯法、和他同住了十五天。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",1,19,"至於別的使徒、除了主的兄弟雅各、我都沒有看見。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",1,20,"我寫給你們的、不是謊話、這是我在　神面前說的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",1,21,"以後我到了敘利亞和基利家境內。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",1,22,"那時、猶太信基督的各教會都沒有見過我的面。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",1,23,"不過聽說、那從前逼迫我們的、現在傳揚他原先所殘害的真道。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",1,24,"他們就為我的緣故、歸榮耀給　神。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",2,1,"過了十四年、我同巴拿巴又上耶路撒冷去、並帶著提多同去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",2,2,"我是奉啟示上去的、把我在外邦人中所傳的福音、對弟兄們陳說．卻是背地裡對那有名望之人說的．惟恐我現在、或是從前、徒然奔跑。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",2,3,"但與我同去的提多、雖是希利尼人、也沒有勉強他受割禮．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",2,4,"因為有偷著引進來的假弟兄、私下窺探我們在基督耶穌裡的自由、要叫我們作奴僕．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",2,5,"我們就是一刻的工夫、也沒有容讓順服他們、為要叫福音的真理仍存在你們中間。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",2,6,"至於那些有名望的、不論他是何等人、都與我無干．　神不以外貌取人．那些有名望的、並沒有加增我甚麼．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",2,7,"反倒看見了主託我傳福音給那未受割禮的人、正如託彼得傳福音給那受割禮的人．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",2,8,"（那感動彼得、叫他為受割禮之人作使徒的、也感動我、叫我為外邦人作使徒）")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",2,9,"又知道所賜給我的恩典、那稱為教會柱石的雅各、磯法、約翰、就向我和巴拿巴用右手行相交之禮、叫我們往外邦人那裡去、他們往受割禮的人那裡去．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",2,10,"只是願意我們記念窮人．這也是我本來熱心去行的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",2,11,"後來磯法到了安提阿、因他有可責之處、我就當面抵擋他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",2,12,"從雅各那裡來的人、未到以先、他和外邦人一同喫飯．及至他們來到、他因怕奉割禮的人、就退去與外邦人隔開了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",2,13,"其餘的猶太人、也都隨著他裝假．甚至連巴拿巴也隨夥裝假。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",2,14,"但我一看見他們行的不正、與福音的真理不合、就在眾人面前對磯法說、你既是猶太人、若隨外邦人行事、不隨猶太人行事、怎麼還勉強外邦人隨猶太人呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",2,15,"我們這生來的猶太人、不是外邦的罪人、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",2,16,"既知道人稱義、不是因行律法、乃是因信耶穌基督、連我們也信了基督耶穌、使我們因信基督稱義、不因行律法稱義．因為凡有血氣的、沒有一人因行律法稱義。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",2,17,"我們若求在基督裡稱義、卻仍舊是罪人、難道基督是叫人犯罪的麼．斷乎不是。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",2,18,"我素來所拆毀的、若重新建造、這就證明自己是犯罪的人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",2,19,"我因律法、就向律法死了、叫我可以向　神活著。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",2,20,"我已經與基督同釘十字架．現在活著的、不再是我、乃是基督在我裡面活著．並且我如今在肉身活著、是因信　神的兒子而活、他是愛我、為我捨己。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",2,21,"我不廢掉　神的恩．義若是藉著律法得的、基督就是徒然死了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",3,1,"無知的加拉太人哪、耶穌基督釘十字架、已經活畫在你們眼前、誰又迷惑了你們呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",3,2,"我只要問你們這一件、你們受了聖靈、是因行律法呢、是因聽信福音呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",3,3,"你們既靠聖靈入門、如今還靠肉身成全麼．你們是這樣的無知麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",3,4,"你們受苦如此之多、都是徒然的麼．難道果真是徒然的麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",3,5,"那賜給你們聖靈、又在你們中間行異能的、是因你們行律法呢、是因你們聽信福音呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",3,6,"正如『亞伯拉罕信　神這就算為他的義。』")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",3,7,"所以你們要知道那以信為本的人、就是亞伯拉罕的子孫。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",3,8,"並且聖經既然預先看明、　神要叫外邦人因信稱義、就早已傳福音給亞伯拉罕、說、『萬國都必因你得福。』")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",3,9,"可見那以信為本的人、和有信心的亞伯拉罕一同得福。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",3,10,"凡以行律法為本的、都是被咒詛的．因為經上記著、『凡不常照律法書上所記一切之事去行的、就被咒詛。』")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",3,11,"沒有一個人靠著律法在　神面前稱義、這是明顯的．因為經上說、『義人必因信得生。』")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",3,12,"律法原不本乎信、只說、『行這些事的、就必因此活著。』")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",3,13,"基督既為我們受了咒詛、〔受原文作成〕就贖出我們脫離律法的咒詛．因為經上記著、『凡掛在木頭上都是被咒詛的。』")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",3,14,"這便叫亞伯拉罕的福、因基督耶穌可以臨到外邦人、使我們因信得著所應許的聖靈。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",3,15,"弟兄們、我且照著人的常話說、雖然是人的文約、若已經立定了、就沒有能廢棄或加增的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",3,16,"所應許的原是向亞伯拉罕和他子孫說的．　神並不是說眾子孫、指著許多人、乃是說你那一個子孫、指著一個人、就是基督。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",3,17,"我是這麼說、　神預先所立的約、不能被那四百三十年以後的律法廢掉、叫應許歸於虛空。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",3,18,"因為承受產業、若本乎律法、就不本乎應許．但　神是憑著應許、把產業賜給亞伯拉罕。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",3,19,"這樣說來、律法是為甚麼有的呢．原是為過犯添上的、等候那蒙應許的子孫來到．並且是藉天使經中保之手設立的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",3,20,"但中保本不是為一面作的．　神卻是一位。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",3,21,"這樣、律法是與　神的應許反對麼．斷乎不是．若曾傳一個能叫人得生的律法、義就誠然本乎律法了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",3,22,"但聖經把眾人都圈在罪裡、使所應許的福因信耶穌基督、歸給那信的人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",3,23,"但這因信得救的理、還未來以先、我們被看守在律法之下、直圈到那將來的真道顯明出來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",3,24,"這樣、律法是我們訓蒙的師傅、引我們到基督那裡、使我們因信稱義。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",3,25,"但這因信得救的理、既然來到、我們從此就不在師傅的手下了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",3,26,"所以你們因信基督耶穌、都是　神的兒子。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",3,27,"你們受洗歸入基督的、都是披戴基督了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",3,28,"並不分猶太人、希利尼人、自主的、為奴的、或男或女．因為你們在基督耶穌、裡都成為一了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",3,29,"你們既屬乎基督、就是亞伯拉罕的後裔、是照著應許承受產業的了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",4,1,"我說那承受產業的、雖然是全業的主人、但為孩童的時候、卻與奴僕毫無分別．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",4,2,"乃在師傅和管家的手下、直等他父親預定的時候來到。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",4,3,"我們為孩童的時候、受管於世俗小學之下、也是如此。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",4,4,"及至時候滿足、　神就差遣他的兒子、為女子所生、且生在律法以下、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",4,5,"要把律法以下的人贖出來、叫我們得著兒子的名分。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",4,6,"你們既為兒子、　神就差他兒子的靈、進入你們〔原文作我們〕的心、呼叫阿爸、父。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",4,7,"可見、從此以後、你不是奴僕、乃是兒子了．既是兒子、就靠著　神為後嗣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",4,8,"但從前你們不認識　神的時候、是給那些本來不是　神的作奴僕。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Gal",4,9,"現在你們既然認識　神、更可說是被　神所認識的、怎麼還要歸回那懦弱無用的小學、情願再給他作奴僕呢。")');
