tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jas",2,10,"因為凡遵守全律法的、只在一條上跌倒、他就是犯了眾條。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jas",2,11,"原來那說不可姦淫的、也說不可殺人．你就是不姦淫、卻殺人、仍是成了犯律法的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jas",2,12,"你們既然要按使人自由的律法受審判、就該照這律法說話行事。")');
