tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jas",2,25,"妓女喇合接待使者、又放他們從別的路上出去、不也是一樣因行為稱義麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jas",2,26,"身體沒有靈魂是死的、信心沒有行為也是死的。")');
