tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jas",3,2,"原來我們在許多事上都有過失．若有人在話語上沒有過失、他就是完全人、也能勒住自己的全身。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jas",3,3,"我們若把嚼環放在馬嘴裡、叫他順服、就能調動他的全身。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jas",3,4,"看哪、船隻雖然甚大、又被大風催逼、只用小小的舵、就隨著掌舵的意思轉動。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jas",3,5,"這樣、舌頭在百體裡也是最小的、卻能說大話。看哪、最小的火、能點最大的樹林．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jas",3,6,"舌頭就是火、在我們百體中、舌頭是個罪惡的世界、能污穢全身、也能把生命的輪子點起來．並且是從地獄裡點著的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jas",3,7,"各類的走獸、飛禽、昆蟲、水族、本來都可以制伏、也已經被人制伏了．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jas",3,8,"惟獨舌頭沒有人能制伏、是不止息的惡物．滿了害死人的毒氣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jas",3,9,"我們用舌頭頌讚那為主為父的、又用舌頭咒詛那照著　神形像被造的人．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jas",3,10,"頌讚和咒詛從一個口裡出來、我的弟兄們、這是不應當的。")');
