tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",5,6,"以色列人在曠野走了四十年、等到國民、就是出埃及的兵丁、都消滅了、因為他們沒有聽從耶和華的話。耶和華曾向他們起誓、必不容他們看見耶和華向他們列祖起誓應許賜給我們的地、就是流奶與蜜之地。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",5,7,"他們的子孫、就是耶和華所興起來接續他們的、都沒有受過割禮．因為在路上沒有給他們行割禮、約書亞這纔給他們行了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",5,8,"國民都受完了割禮、就住在營中自己的地方、等到痊愈了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",5,9,"耶和華對約書亞說、我今日將埃及的羞辱從你們身上滾去了。因此那地方名叫吉甲、直到今日。〔吉甲就是滾的意思〕")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",5,10,"以色列人在吉甲安營．正月十四日晚上、在耶利哥的平原守逾越節。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",5,11,"逾越節的次日、他們就喫了那地的出產．正當那日喫無酵餅、和烘的穀。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",5,12,"他們喫了那地的出產、第二日嗎哪就止住了、以色列人也不再有嗎哪了．那一年他們卻喫迦南地的出產。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",5,13,"約書亞靠近耶利哥的時候舉目觀看、不料、有一個人手裡有拔出來的刀、對面站立．約書亞到他那裡、問他說、你是幫助我們呢、是幫助我們敵人呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",5,14,"他回答說、不是的、我來是要作耶和華軍隊的元帥。約書亞就俯伏在地下拜、說、我主有甚麼話吩咐僕人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",5,15,"耶和華軍隊的元帥對約書亞說、把你腳上的鞋脫下來、因為你所站的地方是聖的。約書亞就照著行了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",6,1,"耶利哥的城門因以色列人就關得嚴緊、無人出入。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",6,2,"耶和華曉諭約書亞說、看哪、我已經把耶利哥、和耶利哥的王、並大能的勇士、都交在你手中。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",6,3,"你們的一切兵丁要圍繞這城、一日圍繞一次．六日都要這樣行。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",6,4,"七個祭司要拿七個羊角走在約櫃前．到第七日你們要繞城七次、祭司也要吹角。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",6,5,"他們吹的角聲拖長、你們聽見角聲、眾百姓要大聲呼喊、城牆就必塌陷．各人都要往前直上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",6,6,"嫩的兒子約書亞召了祭司來、吩咐他們說、你們抬起約櫃來．要有七個祭司拿七個羊角走在耶和華的約櫃前。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",6,7,"又對百姓說、你們前去繞城．帶兵器的要走在耶和華的約櫃前。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",6,8,"約書亞對百姓說完了話、七個祭司拿七個羊角走在耶和華面前吹角、耶和華的約櫃在他們後面跟隨。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",6,9,"帶兵器的、走在吹角的祭司前面、後隊隨著約櫃行、祭司一面走一面吹。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",6,10,"約書亞吩咐百姓說、你們不可呼喊、不可出聲、連一句話也不可出你們的口、等到我吩咐你們呼喊的日子．那時纔可以呼喊。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",6,11,"這樣、他使耶和華的約櫃繞城、把城繞了一次．眾人回到營裡、就在營裡住宿。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",6,12,"約書亞清早起來、祭司又抬起耶和華的約櫃。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",6,13,"七個祭司拿七個羊角在耶和華的約櫃前、時常行走吹角．帶兵器的在他們前面走．後隊隨著耶和華的約櫃行、祭司一面走一面吹。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",6,14,"第二日、眾人把城繞了一次、就回營裡去．六日都是這樣行。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",6,15,"第七日清早、黎明的時候、他們起來、照樣繞城七次．惟獨這日把城繞了七次。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",6,16,"到了第七次、祭司吹角的時候、約書亞吩咐百姓說、呼喊罷．因為耶和華已經把城交給你們了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",6,17,"這城和其中所有的、都要在耶和華面前毀滅．只有妓女喇合、與他家中所有的、可以存活、因為他隱藏了我們所打發的使者。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",6,18,"至於你們、務要謹慎、不可取那當滅的物、恐怕你們取了那當滅的物、就連累以色列的全營、使全營受咒詛。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",6,19,"惟有金子、銀子、和銅鐵的器皿、都要歸耶和華為聖．必入耶和華的庫中。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",6,20,"於是百姓呼喊、祭司也吹角．百姓聽見角聲、便大聲呼喊、城牆就塌陷、百姓便上去進城、各人往前直上、將城奪取。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",6,21,"又將城中所有的、不拘男女、老少、牛羊、和驢、都用刀殺盡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",6,22,"約書亞吩咐窺探地的兩個人說、你們進那妓女的家、照著你們向他所起的誓、將那女人、和他所有的、都從那裡帶出來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",6,23,"當探子的兩個少年人就進去、將喇合、與他的父母、弟兄、和他所有的、並他一切的親眷、都帶出來、安置在以色列的營外。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",6,24,"眾人就用火將城、和其中所有的、焚燒了．惟有金子、銀子、和銅鐵的器皿、都放在耶和華殿的庫中。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",6,25,"約書亞卻把妓女喇合、與他父家、並他所有的、都救活了．因為他隱藏了約書亞所打發窺探耶利哥的使者．他就住在以色列中、直到今日。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",6,26,"當時約書亞叫眾人起誓說、有興起重修這耶利哥城的人、當在耶和華面前受咒詛．他立根基的時候、必喪長子、安門的時候、必喪幼子。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",6,27,"耶和華與約書亞同在．約書亞的聲名傳揚遍地。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",7,1,"以色列人在當滅的物上犯了罪．因為猶大支派中、謝拉的曾孫、撒底的孫子、迦米的兒子亞干、取了當滅的物．耶和華的怒氣、就向以色列人發作。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",7,2,"當下約書亞從耶利哥打發人往伯特利東邊靠近伯亞文的艾城去、吩咐他們說、你們上去窺探那地．他們就上去窺探艾城。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",7,3,"他們回到約書亞那裡、對他說、眾民不必都上去．只要二三千人上去、就能攻取艾城．不必勞累眾民都去、因為那裡的人少。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",7,4,"於是民中約有三千人上那裡去、竟在艾城人面前逃跑了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",7,5,"艾城的人擊殺了他們三十六人．從城門前追趕他們、直到示巴琳、在下坡殺敗他們．眾民的心就消化如水。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",7,6,"約書亞便撕裂衣服．他和以色列的長老把灰撒在頭上、在耶和華的約櫃前、俯伏在地、直到晚上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",7,7,"約書亞說、哀哉、主耶和華阿、你為甚麼竟領這百姓過約但河、將我們交在亞摩利人的手中、使我們滅亡呢．我們不如住在約但河那邊倒好。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",7,8,"主阿、以色列人既在仇敵面前轉背逃跑、我還有甚麼可說的呢．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",7,9,"迦南人和這地一切的居民聽見了、就必圍困我們、將我們的名從地上除滅．那時你為你的大名要怎樣行呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",7,10,"耶和華吩咐約書亞說、起來、你為何這樣俯伏在地呢．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",7,11,"以色列人犯了罪、違背了我所吩咐他們的約、取了當滅的物．又偷竊、又行詭詐、又把那當滅的放在他們的家具裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",7,12,"因此、以色列人在仇敵面前站立不住．他們在仇敵面前轉背逃跑、是因成了被咒詛的．你們若不把當滅的物、從你們中間除掉、我就不再與你們同在了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",7,13,"你起來、叫百姓自潔、對他們說、你們要自潔預備明天．因為耶和華以色列的　神這樣說、以色列阿、你們中間有當滅的物．你們若不除掉、在仇敵面前、必站立不住。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",7,14,"到了早晨、你們要按著支派近前來．耶和華所取的支派、要按著宗族近前來．耶和華所取的宗族、要按著家室近前來．耶和華所取的家室、要按著人丁、一個一個地近前來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",7,15,"被取的人有當滅的物在他那裡、他和他所有的、必被火焚燒．因他違背了耶和華的約、又因他在以色列中、行了愚妄的事。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",7,16,"於是約書亞清早起來、使以色列人按著支派近前來．取出來的、是猶大支派．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",7,17,"使猶大支派〔原文作宗族〕近前來、就取了謝拉的宗族．使謝拉的宗族、按著家室人丁、一個一個地近前來．取出來的、是撒底．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",7,18,"使撒底的家室、按著人丁、一個一個地近前來、就取出猶大支派的人謝拉的曾孫、撒底的孫子、迦米的兒子亞干。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",7,19,"約書亞對亞干說、我兒、我勸你將榮耀歸給耶和華以色列的　神、在他面前認罪將你所作的事告訴我．不要向我隱瞞。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",7,20,"亞干回答約書亞說、我實在得罪了耶和華以色列的　神．我所作的事、如此如此。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",7,21,"我在所奪的財物中、看見一件美好的示拿衣服、二百舍客勒銀子、一條金子、重五十舍客勒、我就貪愛這些物件、便拿去了．現今藏在我帳棚內的地裡、銀子在衣服底下。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",7,22,"約書亞就打發人跑到亞干的帳棚裡．那件衣服果然藏在他帳棚內、銀子在底下。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",7,23,"他們就從帳棚裡取出來、拿到約書亞和以色列眾人那裡、放在耶和華面前。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",7,24,"約書亞和以色列眾人、把謝拉的曾孫亞干、和那銀子、那件衣服、那條金子、並亞干的兒女、牛、驢、羊、帳棚、以及他所有的、都帶到亞割谷去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",7,25,"約書亞說、你為甚麼連累我們呢．今日耶和華必叫你受連累。於是以色列眾人用石頭打死他、將石頭扔在其上．又用火焚燒他所有的。〔他所有的原文作他們〕")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",7,26,"眾人在亞干身上堆成一大堆石頭、直存到今日．於是耶和華轉意、不發他的烈怒。因此那地方名叫亞割谷、直到今日。〔亞割就是連累的意思〕")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",8,1,"耶和華對約書亞說、不要懼怕、也不要驚惶．你起來率領一切兵丁、上艾城去．我已經把艾城的王、和他的民、他的城、並他的地、都交在你手裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",8,2,"你怎樣待耶利哥、和耶利哥的王、也當照樣待艾城、和艾城的王．只是城內所奪的財物、和牲畜、你們可以取為自己的掠物．你要在城後設下伏兵。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",8,3,"於是約書亞、和一切兵丁、都起來、要上艾城去．約書亞選了三萬大能的勇士、夜間打發他們前往．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",8,4,"吩咐他們說、你們要在城後埋伏．不可離城太遠、都要各自準備。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",8,5,"我與我所帶領的眾民、要向城前往．城裡的人像初次出來攻擊我們的時候、我們就在他們面前逃跑．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",8,6,"他們必出來追趕我們、直到我們引誘他們離開城．因為他們必說、這些人像初次在我們面前逃跑．所以我們要在他們面前逃跑．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",8,7,"你們就從埋伏的地方起來、奪取那城．因為耶和華你們的　神、必把城交在你們手裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",8,8,"你們奪了城以後、就放火燒城．要照耶和華的話行．這是我吩咐你們的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",8,9,"約書亞打發他們前往．他們就上埋伏的地方去、住在伯特利和艾城的中間．就是在艾城的西邊．這夜約書亞卻在民中住宿。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",8,10,"約書亞清早起來、點齊百姓、他和以色列的長老在百姓前面上艾城去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",8,11,"眾民就是他所帶領的兵丁都上去、向前直往、來到城前、在艾城北邊安營．在約書亞和艾城中間有一山谷。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",8,12,"他挑了約有五千人、使他們埋伏在伯特利和艾城的中間、就是在艾城的西邊。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",8,13,"於是安置了百姓、就是城北的全軍、和城西的伏兵．這夜約書亞進入山谷之中。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",8,14,"艾城的王看見這景況、就和全城的人、清早急忙起來、按所定的時候、出到亞拉巴前、要與以色列人交戰．王卻不知道在城後有伏兵。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",8,15,"約書亞和以色列眾人在他們面前裝敗、往那通曠野的路逃跑。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",8,16,"城內的眾民、都被招聚、追趕他們．艾城人追趕的時候、就被引誘離開城。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",8,17,"艾城、和伯特利城、沒有一人不出來追趕以色列人的、撇了敞開的城門、去追趕以色列人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",8,18,"耶和華吩咐約書亞說、你向艾城伸出手裡的短槍、因為我要將城交在你手裡．約書亞就向城伸出手裡的短槍。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",8,19,"他一伸手、伏兵就從埋伏的地方急忙起來、奪了城、跑進城去、放火焚燒。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",8,20,"艾城的人回頭一看、不料、城中煙氣沖天、他們就無力向左向右逃跑．那往曠野逃跑的百姓、便轉身攻擊追趕他們的人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",8,21,"約書亞和以色列眾人見伏兵已經奪了城、城中煙氣飛騰、就轉身回去、擊殺艾城的人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",8,22,"伏兵也出城迎擊艾城人．艾城人就困在以色列人中間、前後都是以色列人．於是以色列人擊殺他們、沒有留下一個、也沒有一個逃脫的．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",8,23,"生擒了艾城的王、將他解到約書亞那裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",8,24,"以色列人在田間和曠野殺盡所追趕一切艾城的居民．艾城人倒在刀下、直到滅盡、以色列眾人就回到艾城、用刀殺了城中的人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",8,25,"當日殺斃的人、連男帶女、共有一萬二千、就是艾城所有的人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",8,26,"約書亞沒有收回手裡所伸出來的短槍、直到把艾城的一切居民、盡行殺滅。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",8,27,"惟獨城中的牲畜、和財物、以色列人都取為自己的掠物、是照耶和華所吩咐約書亞的話。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",8,28,"約書亞將艾城焚燒、使城永為高堆、荒場、直到今日。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",8,29,"又將艾城王掛在樹上、直到晚上。日落的時候、約書亞吩咐人把屍首從樹上取下來、丟在城門口、在屍首上堆成一大堆石頭、直存到今日。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",8,30,"那時約書亞在以巴路山上、為耶和華以色列的　神、築一座壇．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",8,31,"是用沒有動過鐵器的整石頭築的、照著耶和華僕人摩西所吩咐以色列人的話、正如摩西律法書上所寫的．眾人在這壇上給耶和華奉獻燔祭、和平安祭。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",8,32,"約書亞在那裡、當著以色列人面前、將摩西所寫的律法抄寫在石頭上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",8,33,"以色列眾人、無論是本地人、是寄居的、和長老、官長、並審判官、都站在約櫃兩旁、在抬耶和華約櫃的祭司利未人面前、一半對著基利心山、一半對著以巴路山、為以色列民祝福、正如耶和華僕人摩西先前所吩咐的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",8,34,"隨後約書亞將律法上祝福咒詛的話、照著律法書上一切所寫的、都宣讀了一遍。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",8,35,"摩西所吩咐的一切話、約書亞在以色列全會眾、和婦女、孩子、並他們中間寄居的外人面前、沒有一句不宣讀的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",9,1,"約但河西住山地、高原、並對著利巴嫩山沿大海一帶的諸王、就是赫人、亞摩利人、迦南人、比利洗人、希未人、耶布斯人的諸王、聽見這事、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",9,2,"就都聚集、同心合意的、要與約書亞和以色列人爭戰。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",9,3,"基遍的居民聽見約書亞向耶利哥和艾城所行的事、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",9,4,"就設詭計。假充使者、拿舊口袋、和破裂縫補的舊皮酒袋、馱在驢上、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",9,5,"將補過的舊鞋穿在腳上、把舊衣服穿在身上、他們所帶的餅都是乾的、長了霉了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",9,6,"他們到吉甲營中見約書亞、對他和以色列人說、我們是從遠方來的．現在求你與我們立約。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",9,7,"以色列人對這些希未人說、只怕你們是住在我們中間的．若是這樣、怎能和你們立約呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",9,8,"他們對約書亞說、我們是你的僕人。約書亞問他們說、你們是甚麼人、是從那裡來的．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",9,9,"他們回答說、僕人從極遠之地而來、是因聽見耶和華你　神的名聲、和他在埃及所行的一切事、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",9,10,"並他向約但河東的兩個亞摩利王、就是希實本王西宏、和在亞斯他錄的巴珊王噩、一切所行的事．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",9,11,"我們的長老和我們那地的一切居民對我們說、你們手裡要帶著路上用的食物、去迎接以色列人、對他們說、我們是你們的僕人．現在求你們與我們立約。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",9,12,"我們出來要往你們這裡來的日子、從家裡帶出來的這餅還是熱的．看哪、現在都乾了。長了霉了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",9,13,"這皮酒袋、我們盛酒的時候還是新的．看哪、現在已經破裂．我們這衣服和鞋、因為道路甚遠、也都穿舊了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",9,14,"以色列人受了他們些食物、並沒有求問耶和華。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",9,15,"於是約書亞與他們講和、與他們立約、容他們活著．會眾的首領、也向他們起誓。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",9,16,"以色列人與他們立約之後、過了三天、纔聽見他們是近鄰、住在以色列人中間的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",9,17,"以色列人起行、第三天到了他們的城邑、就是基遍、基非拉、比錄、基列耶琳。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",9,18,"因為會眾的首領、已經指著耶和華以色列的　神向他們起誓、所以以色列人不擊殺他們．全會眾就向首領發怨言。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",9,19,"眾首領對全會眾說、我們已經指著耶和華以色列的　神向他們起誓．現在我們不能害他們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",9,20,"我們要如此待他們、容他們活著．免得有忿怒因我們所起的誓臨到我們身上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",9,21,"首領又對會眾說、要容他們活著．於是他們為全會眾作了劈柴挑水的人、正如首領對他們所說的話。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",9,22,"約書亞召了他們來、對他們說、為甚麼欺哄我們說、我們離你們甚遠呢．其實你們是住在我們中間。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",9,23,"現在你們是被咒詛的．你們中間的人、必斷不了作奴僕、為我　神的殿、作劈柴挑水的人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",9,24,"他們回答約書亞說、因為有人實在告訴你的僕人、耶和華你的　神曾吩咐他的僕人摩西、把這全地賜給你們、並在你們面前滅絕這地的一切居民．所以我們為你們的緣故、甚怕喪命、就行了這事。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",9,25,"現在我們在你手中、你以怎樣待我們為善為正、就怎樣作罷。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",9,26,"於是約書亞這樣待他們、救他們脫離以色列人的手、以色列人就沒有殺他們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",9,27,"當日約書亞使他們在耶和華所要選擇的地方、為會眾、和耶和華的壇、作劈柴挑水的人、直到今日。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",10,1,"耶路撒冷王亞多尼洗德聽見約書亞奪了艾城、盡行毀滅．怎樣待耶利哥和耶利哥的王、也照樣待艾城和艾城的王．又聽見基遍的居民與以色列人立了和約、住在他們中間．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",10,2,"就甚懼怕、因為基遍是一座大城、如都城一般、比艾城更大、並且城內的人都是勇士。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",10,3,"所以耶路撒冷王亞多尼洗德打發人去見希伯崙王何鹹、耶末王毗蘭、拉吉王雅非亞、和伊磯倫王底璧、說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",10,4,"求你們上來幫助我、我們好攻打基遍．因為他們與約書亞和以色列人立了和約。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",10,5,"於是五個亞摩利王、就是耶路撒冷王、希伯崙王、耶末王、拉吉王、伊磯倫王、大家聚集、率領他們的眾軍上去、對著基遍安營、攻打基遍。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",10,6,"基遍人就打發人往吉甲的營中去見約書亞、說、你不要袖手不顧你的僕人．求你速速上來拯救我們、幫助我們．因為住山地亞摩利人的諸王都聚集攻擊我們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",10,7,"於是約書亞、和他一切兵丁、並大能的勇士、都從吉甲上去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",10,8,"耶和華對約書亞說、不要怕他們、因為我已將他們交在你手裡、他們無一人能在你面前站立得住。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",10,9,"約書亞就終夜從吉甲上去、猛然臨到他們那裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",10,10,"耶和華使他們在以色列人面前潰亂．約書亞在基遍大大的殺敗他們、追趕他們在伯和崙的上坡路、擊殺他們直到亞西加、和瑪基大。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",10,11,"他們在以色列人面前逃跑、正在伯和崙下坡的時候、耶和華從天上降大冰雹在他們身上、〔冰雹原文作石頭〕直降到亞西加、打死他們．被冰雹打死的、比以色列人用刀殺死的還多。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",10,12,"當耶和華將亞摩利人交付以色列人的日子、約書亞就禱告耶和華、在以色列人眼前說、日頭阿、你要停在基遍．月亮阿、你要止在亞雅崙谷。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",10,13,"於是日頭停留、月亮止住、直等國民向敵人報仇．這事豈不是寫在雅煞珥書上麼．日頭在天當中停住、不急速下落、約有一日之久。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",10,14,"在這日以前、這日以後、耶和華聽人的禱告、沒有像這日的．是因耶和華為以色列爭戰。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",10,15,"約書亞和以色列眾人回到吉甲的營中。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",10,16,"那五王逃跑、藏在瑪基大洞裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",10,17,"有人告訴約書亞說、那五王已經找到了、都藏在瑪基大洞裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",10,18,"約書亞說、你們把幾塊大石頭滾到洞口、派人看守．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",10,19,"你們卻不可耽延．要追趕你們的仇敵、擊殺他們儘後邊的人、不容他們進自己的城邑．因為耶和華你們的　神已經把他們交在你們手裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",10,20,"約書亞和以色列人大大殺敗他們、直到將他們滅盡．其中剩下的人都進了堅固的城。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",10,21,"眾百姓就安然回瑪基大營中、到約書亞那裡．沒有一人敢向以色列人饒舌。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",10,22,"約書亞說、打開洞口、將那五王從洞裡帶出來、領到我面前。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",10,23,"眾人就這樣行、將那五王、就是耶路撒冷王、希伯崙王、耶末王、拉吉王、伊磯倫王、從洞裡帶出來、領到約書亞面前。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",10,24,"帶出那五王到約書亞面前的時候、約書亞就召了以色列眾人來、對那些和他同去的軍長說、你們近前來、把腳踏在這些王的頸項上。他們就近前來、把腳踏在這些王的頸項上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",10,25,"約書亞對他們說、你們不要懼怕、也不要驚惶、應當剛強壯膽．因為耶和華必這樣待你們所要攻打的一切仇敵。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",10,26,"隨後約書亞將這五王殺死、掛在五棵樹上．他們就在樹上直掛到晚上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",10,27,"日頭要落的時候、約書亞一吩咐、人就把屍首從樹上取下來、丟在他們藏過的洞裡、把幾塊大石頭放在洞口、直存到今日。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",10,28,"當日約書亞奪了瑪基大、用刀擊殺城中的人、和王．將其中一切人口盡行殺滅、沒有留下一個．他待瑪基大王像從前待耶利哥王一樣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",10,29,"約書亞和以色列眾人從瑪基大往立拿去、攻打立拿。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",10,30,"耶和華將立拿、和立拿的王、也交在以色列人手裡．約書亞攻打這城、用刀擊殺了城中的一切人口、沒有留下一個、他待立拿王、像從前待耶利哥王一樣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",10,31,"約書亞和以色列眾人從立拿往拉吉去、對著拉吉安營、攻打這城。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",10,32,"耶和華將拉吉交在以色列人的手裡．第二天約書亞就奪了拉吉、用刀擊殺了城中的一切人口．是照他向立拿一切所行的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",10,33,"那時基色王荷蘭上來幫助拉吉、約書亞就把他和他的民都擊殺了、沒有留下一個。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",10,34,"約書亞和以色列眾人從拉吉往伊磯倫去、對著伊磯倫安營、攻打這城．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",10,35,"當日就奪了城、用刀擊殺了城中的人．那日約書亞將城中的一切人口、盡行殺滅．是照他向拉吉一切所行的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",10,36,"約書亞和以色列眾人從伊磯倫上希伯崙去、攻打這城．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",10,37,"就奪了希伯崙和屬希伯崙的諸城邑、用刀將城中的人、與王、並那些城邑中的人口、都擊殺了、沒有留下一個．是照他向伊磯倫所行的、把城中的一切人口、盡行殺滅。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",10,38,"約書亞和以色列眾人回到底璧、攻打這城．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",10,39,"就奪了底璧、和屬底璧的城邑、又擒獲底璧的王、用刀將這些城中的人口盡行殺滅、沒有留下一個．他待底璧和底璧王像從前待希伯崙、和立拿與立拿王一樣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",10,40,"這樣約書亞擊殺全地的人、就是山地、南地、高原、山坡的人、和那些地的諸王、沒有留下一個．將凡有氣息的盡行殺滅、正如耶和華以色列的　神所吩咐的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",10,41,"約書亞從加低斯巴尼亞攻擊到迦薩、又攻擊歌珊全地、直到基遍。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",10,42,"約書亞一時殺敗了這些王、並奪了他們的地．因為耶和華以色列的　神為以色列爭戰。")');
