tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",13,1,"約書亞年紀老邁、耶和華對他說、你年紀老邁了、還有許多未得之地、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",13,2,"就是非利士人的全境、和基述人的全地．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",13,3,"從埃及前的西曷河往北、直到以革倫的境界、就算屬迦南人之地．有非利士人五個首領所管的迦薩人、亞實突人、亞實基倫人、迦特人、以革倫人之地．並有南方亞衛人之地．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",13,4,"又有迦南人的全地．並屬西頓人的米亞拉、到亞弗、直到亞摩利人的境界．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",13,5,"還有迦巴勒人之地、並向日出的全利巴嫩、就是從黑門山根的巴力迦得、直到哈馬口．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",13,6,"山地的一切居民、從利巴嫩直到米斯利弗瑪音、就是所有的西頓人、我必在以色列人面前趕出他們去．你只管照我所吩咐的、將這地拈鬮分給以色列人為業．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",13,7,"現在你要把這地分給九個支派、和瑪拿西半個支派為業。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",13,8,"瑪拿西那半支派、和流便迦得二支派、已經受了產業、就是耶和華的僕人摩西在約但河東所賜給他們的、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",13,9,"是從亞嫩谷邊的亞羅珥和谷中的城、並米底巴的全平原、直到底本、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",13,10,"和在希實本作王亞摩利王西宏的諸城、直到亞捫人的境界。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",13,11,"又有基列地、基述人、瑪迦人的地界．並黑門全山．巴珊全地、直到撒迦、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",13,12,"又有巴珊王噩的全國．他在亞斯他錄和以得來作王、利乏音人所存留的只剩下他．這些地的人、都是摩西所擊殺所趕逐的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",13,13,"以色列人卻沒有趕逐基述人、瑪迦人、這些人仍住在以色列中、直到今日。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",13,14,"只是利未支派、摩西〔原文作他〕沒有把產業分給他們．他們的產業、乃是獻與耶和華以色列　神的火祭．正如耶和華所應許他們的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",13,15,"摩西按著流便支派的宗族、分給他們產業．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",13,16,"他們的境界、是亞嫩谷邊的亞羅珥、和谷中的城、靠近米底巴的全平原．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",13,17,"希實本並屬希實本平原的各城、底本、巴末巴力、伯巴力勉、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jos",13,18,"雅雜、基底莫、米法押、")');
