tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",6,3,"人若生一百個兒子、活許多歲數、以致他的年日甚多、心裡卻不得滿享福樂、又不得埋葬．據我說、那不到期而落的胎比他倒好．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",6,4,"因為虛虛而來、暗暗而去、名字被黑暗遮蔽．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",6,5,"並且沒有見過天日、也毫無知覺．這胎、比那人倒享安息。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",6,6,"那人雖然活千年、再活千年、卻不享福、眾人豈不都歸一個地方去麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",6,7,"人的勞碌都為口腹、心裡卻不知足。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",6,8,"這樣看來、智慧人比愚昧人有甚麼長處呢．窮人在眾人面前知道如何行、有甚麼長處呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",6,9,"眼睛所看的、比心裡妄想的倒好．這也是虛空、也是捕風。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",6,10,"先前所有的、早已起了名．並知道何為人．他也不能與那比自己力大的相爭。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",6,11,"加增虛浮的事既多、這與人有甚麼益處呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",6,12,"人一生虛度的日子、就如影兒經過．誰知道甚麼與他有益呢．誰能告訴他身後在日光之下有甚麼事呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",7,1,"名譽強如美好的膏油．人死的日子、勝過人生的日子。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",7,2,"往遭喪的家去、強如往宴樂的家去、因為死是眾人的結局．活人也必將這事放在心上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",7,3,"憂愁強如喜笑、因為面帶愁容終必使心喜樂。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",7,4,"智慧人的心、在遭喪之家．愚昧人的心、在快樂之家。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",7,5,"聽智慧人的責備、強如聽愚昧人的歌唱。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",7,6,"愚昧人的笑聲、好像鍋下燒荊棘的爆聲、這也是虛空。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",7,7,"勒索使智慧人變為愚妄．賄賂能敗壞人的慧心。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",7,8,"事情的終局、強如事情的起頭．存心忍耐的、勝過居心驕傲的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",7,9,"你不要心裡急躁惱怒、因為惱怒存在愚昧人的懷中。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",7,10,"不要說、先前的日子強過如今的日子、是甚麼緣故呢．你這樣問、不是出於智慧。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",7,11,"智慧和產業並好．而且見天日的人、得智慧更為有益。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",7,12,"因為智慧護庇人、好像銀錢護庇人一樣．惟獨智慧能保全智慧人的生命、這就是知識的益處。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",7,13,"你要察看　神的作為、因　神使為曲的、誰能變為直呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",7,14,"遇亨通的日子、你當喜樂．遭患難的日子、你當思想．因為　神使這兩樣並列、為的是叫人查不出身後有甚麼事。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",7,15,"有義人行義、反致滅亡．有惡人行惡、倒享長壽．這都是我在虛度之日中所見過的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",7,16,"不要行義過分．也不要過於自逞智慧．何必自取敗亡呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",7,17,"不要行惡過分．也不要為人愚昧．何必不到期而死呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",7,18,"你持守這個為美．那個也不要鬆手．因為敬畏　神的人、必從這兩樣出來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",7,19,"智慧使有智慧的人、比城中十個官長更有能力。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",7,20,"時常行善而不犯罪的義人、世上實在沒有。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",7,21,"人所說的一切話、你不要放在心上、恐怕聽見你的僕人咒詛你。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",7,22,"因為你心裡知道、自己也曾屢次咒詛別人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",7,23,"我曾用智慧試驗這一切事．我說、要得智慧、智慧卻離我遠。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",7,24,"萬事之理、離我甚遠、而且最深、誰能測透呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",7,25,"我轉念、一心要知道、要考察、要尋求智慧、和萬事的理由．又要知道邪惡為愚昧、愚昧為狂妄。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",7,26,"我得知有等婦人、比死還苦、他的心是網羅、手是鎖鍊．凡蒙　神喜悅的人、必能躲避他．有罪的人、卻被他纏住了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",7,27,"傳道者說、看哪、一千男子中、我找到一個正直人．但眾女子中、沒有找到一個．我將這事一一比較、要尋求其理、我心仍要尋找、卻未曾找到。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",7,28,"見上節")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",7,29,"我所找到的、只有一件、就是　神造人原是正直、但他們尋出許多巧計。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",8,1,"誰如智慧人呢．誰知道事情的解釋呢．人的智慧使他的臉發光、並使他臉上的暴氣改變。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",8,2,"我勸你遵守王的命令．既指　神起誓、理當如此。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",8,3,"不要急躁離開王的面前．不要固執行惡．因為他凡事都隨自己的心意而行。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",8,4,"王的話本有權力、誰敢問他說、你作甚麼呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",8,5,"凡遵守命令的、必不經歷禍患．智慧人的心、能辨明時候和定理。〔原文作審判下節同〕")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",8,6,"各樣事務成就、都有時候和定理．因為人的苦難、重壓在他身上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",8,7,"他不知道將來的事、因為將來如何、誰能告訴他呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",8,8,"無人有權力掌管生命、將生命留住．也無人有權力掌管死期．這場爭戰、無人能免．邪惡也不能救那好行邪惡的人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",8,9,"這一切我都見過．也專心查考日光之下所作的一切事．有時這人管轄那人、令人受害。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",8,10,"我見惡人埋葬、歸入墳墓．又見行正直事的、離開聖地、在城中被人忘記．這也是虛空。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",8,11,"因為斷定罪名、不立刻施刑、所以世人滿心作惡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",8,12,"罪人雖然作惡百次、倒享長久的年日．然而我準知道、敬畏　神的、就是在他面前敬畏的人、終久必得福樂。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",8,13,"惡人卻不得福樂、也不得長久的年日．這年日好像影兒、因他不敬畏　神。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",8,14,"世上有一件虛空的事、就是義人所遭遇的、反照惡人所行的．又有惡人所遭遇的、反照義人所行的．我說、這也是虛空。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",8,15,"我就稱讚快樂、原來人在日光之下、莫強如喫喝快樂．因為他在日光之下、　神賜他一生的年日、要從勞碌中、時常享受所得的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",8,16,"我專心求智慧、要看世上所作的事．（有晝夜不睡覺、不合眼的）")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",8,17,"我就看明　神一切的作為．知道人查不出日光之下所作的事．任憑他費多少力尋查、都查不出來．就是智慧人雖想知道、也是查不出來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",9,1,"我將這一切事放在心上、詳細考究、就知道義人和智慧人、並他們的作為、都在　神手中．或是愛、或是恨、都在他們的前面、人不能知道。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",9,2,"凡臨到眾人的事、都是一樣．義人和惡人、都遭遇一樣的事．好人、潔淨人和不潔淨人、獻祭的與不獻祭的、也是一樣．好人如何、罪人也如何．起誓的如何、怕起誓的也如何。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",9,3,"在日光之下所行的一切事上、有一件禍患、就是眾人所遭遇的、都是一樣．並且世人的心、充滿了惡．活著的時候心裡狂妄、後來就歸死人那裡去了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",9,4,"與一切活人相連的、那人還有指望．因為活著的狗、比死了的獅子更強。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",9,5,"活著的人、知道必死．死了的人、毫無所知．也不再得賞賜、他們的名無人記念．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",9,6,"他們的愛、他們的恨、他們的嫉妒、早都消滅了．在日光之下所行的一切事上、他們永不再有分了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",9,7,"你只管去歡歡喜喜喫你的飯．心中快樂喝你的酒．因為　神已經悅納你的作為。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",9,8,"你的衣服當時常潔白．你頭上也不要缺少膏油。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",9,9,"在你一生虛空的年日、就是　神賜你在日光之下虛空的年日、當同你所愛的妻、快活度日．因為那是你生前、在日光之下勞碌的事上所得的分。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",9,10,"凡你手所當作的事、要盡力去作．因為在你所必去的陰間、沒有工作、沒有謀算、沒有知識、也沒有智慧。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",9,11,"我又轉念、見日光之下、快跑的未必能贏、力戰的未必得勝、智慧的未必得糧食、明哲的未必得資財、靈巧的未必得喜悅．所臨到眾人的、是在乎當時的機會。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",9,12,"原來人也不知道自己的定期．魚被惡網圈住、鳥被網羅捉住、禍患忽然臨到的時候、世人陷在其中、也是如此。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",9,13,"我見日光之下有一樣智慧、據我看乃是廣大、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",9,14,"就是有一小城、其中的人數稀少、有大君王來攻擊、修築營壘、將城圍困．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",9,15,"城中有一個貧窮的智慧人、他用智慧救了那城、卻沒有人記念那窮人．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",9,16,"我就說、智慧勝過勇力．然而那貧窮人的智慧、被人藐視、他的話也無人聽從。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",9,17,"寧可在安靜之中聽智慧人的言語、不聽掌管愚昧人的喊聲。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",9,18,"智慧勝過打仗的兵器．但一個罪人、能敗壞許多善事。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",10,1,"死蒼蠅、使作香的膏油發出臭氣．這樣、一點愚昧、也能敗壞智慧和尊榮。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",10,2,"智慧人的心居右．愚昧人的心居左。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",10,3,"並且愚昧人行路、顯出無知．對眾人說、他是愚昧人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",10,4,"掌權者的心、若向你發怒、不要離開你的本位、因為柔和能免大過。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",10,5,"我見日光之下、有一件禍患、似乎出於掌權的錯誤、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",10,6,"就是愚昧人立在高位．富足人坐在低位。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",10,7,"我見過僕人騎馬、王子像僕人在地上步行。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",10,8,"挖陷坑的、自己必掉在其中．拆牆垣的、必為蛇所咬。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",10,9,"鑿開〔或作挪移〕石頭的、必受損傷．劈開木頭的、必遭危險。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",10,10,"鐵器鈍了、若不將刃磨快、就必多費氣力．但得智慧指教、便有益處。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",10,11,"未行法術以先、蛇若咬人、後行法術也是無益。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",10,12,"智慧人的口、說出恩言．愚昧人的嘴、吞滅自己。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",10,13,"他口中的言語、起頭是愚昧．他話的末尾、是奸惡的狂妄。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",10,14,"愚昧人多有言語、人卻不知將來有甚麼事．他身後的事、誰能告訴他呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",10,15,"凡愚昧人、他的勞碌使自己困乏．因為連進城的路他也不知道。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",10,16,"邦國阿、你的王若是孩童、你的群臣早晨宴樂、你就有禍了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",10,17,"邦國阿、你的王若是貴冑之子、你的群臣按時喫喝、為要補力、不為酒醉、你就有福了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",10,18,"因人懶惰、房頂塌下．因人手懶、房屋滴漏。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",10,19,"設擺筵席、是為喜笑．酒能使人快活、錢能叫萬事應心。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",10,20,"你不可咒詛君王、也不可心懷此念．在你臥房也不可咒詛富戶．因為空中的鳥、必傳揚這聲音．有翅膀的、也必述說這事。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",11,1,"當將你的糧食撒在水面、因為日久必能得著。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",11,2,"你要分給七人、或分給八人、因為你不知道將來有甚麼災禍臨到地上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",11,3,"雲若滿了雨、就必傾倒在地上．樹若向南倒、或向北倒、樹倒在何處、就存在何處。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",11,4,"看風的必不撒種．望雲的必不收割。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",11,5,"風從何道來、骨頭在懷孕婦人的胎中如何長成、你尚且不得知道、這樣、行萬事之　神的作為、你更不得知道。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",11,6,"早晨要撒你的種、晚上也不要歇你的手、因為你不知道那一樣發旺、或是早撒的、或是晚撒的、或是兩樣都好。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",11,7,"光本是佳美的、眼見日光也是可悅的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",11,8,"人活多年、就當快樂多年．然而也當想到黑暗的日子、因為這日子必多、所要來的都是虛空。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",11,9,"少年人哪、你在幼年時當快樂．在幼年的日子、使你的心歡暢、行你心所願行的、看你眼所愛看的、卻要知道、為這一切的事、　神必審問你。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",11,10,"所以你當從心中除掉愁煩．從肉體克去邪惡．因為一生的開端、和幼年之時、都是虛空的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",12,1,"你趁著年幼、衰敗的日子尚未來到、就是你所說、我毫無喜樂的那些年日未曾臨近之先、當記念造你的主．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",12,2,"不要等到日頭、光明、月亮、星宿、變為黑暗、雨後雲彩反回、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",12,3,"看守房屋的發顫、有力的屈身、推磨的稀少就止息、從窗戶往外看的都昏暗、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",12,4,"街門關閉、推磨的響聲微小、雀鳥一叫、人就起來、歌唱的女子、也都衰微、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",12,5,"人怕高處、路上有驚慌、杏樹開花、蚱蜢成為重擔、人所願的也都廢掉、因為人歸他永遠的家、弔喪的在街上往來、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",12,6,"銀鍊折斷、金罐破裂、瓶子在泉旁損壞、水輪在井口破爛、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",12,7,"塵土仍歸於地、靈仍歸於賜靈的　神。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",12,8,"傳道者說、虛空的虛空、凡事都是虛空。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",12,9,"再者、傳道者因有智慧、仍將知識教訓眾人．又默想、又考查、又陳說許多箴言。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",12,10,"傳道者專心尋求可喜悅的言語、是憑正直寫的誠實話。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",12,11,"智慧人的言語、好像刺棍．會中之師的言語、又像釘穩的釘子．都是一個牧者所賜的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",12,12,"我兒、還有一層、你當受勸戒．著書多、沒有窮盡．讀書多、身體疲倦。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",12,13,"這些事都已聽見了．總意就是敬畏　神、謹守他的誡命、這是人所當盡的本分。〔或作這是眾人的本分〕")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ecc",12,14,"因為人所作的事、連一切隱藏的事、無論是善是惡、　神都必審問。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",1,1,"所羅門的歌、是歌中的雅歌。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",1,2,"願他用口與我親嘴．因你的愛情比酒更美。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",1,3,"你的膏油馨香．你的名如同倒出來的香膏．所以眾童女都愛你。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",1,4,"願你吸引我、我們就快跑跟隨你．王帶我進了內室．我們必因你歡喜快樂．我們要稱讚你的愛情、勝似稱讚美酒．他們愛你是理所當然的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",1,5,"耶路撒冷的眾女子阿、我雖然黑、卻是秀美、如同基達的帳棚、好像所羅門的幔子。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",1,6,"不要因日頭把我曬黑了、就輕看我。我同母的弟兄向我發怒．他們使我看守葡萄園、我自己的葡萄園、卻沒有看守。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",1,7,"我心所愛的阿、求你告訴我你在何處牧羊、晌午在何處使羊歇臥．我何必在你同伴的羊群旁邊、好像蒙著臉的人呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",1,8,"你這女子中極美麗的、你若不知道、只管跟隨羊群的腳蹤去、把你的山羊羔、牧放在牧人帳棚的旁邊。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",1,9,"我的佳偶、我將你比法老車上套的駿馬。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",1,10,"你的兩腮、因髮辮而秀美、你的頸項、因珠串而華麗。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",1,11,"我們要為你編上金辮、鑲上銀釘。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",1,12,"王正坐席的時候、我的哪噠香膏發出香味。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",1,13,"我以我的良人為一袋沒藥、常在我懷中。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",1,14,"我以我的良人為一棵鳳仙花、在隱基底葡萄園中。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",1,15,"我的佳偶、你甚美麗、你甚美麗．你的眼好像鴿子眼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",1,16,"我的良人哪、你甚美麗可愛、我們以青草為床榻、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",1,17,"以香柏樹為房屋的棟梁、以松樹為椽子。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",2,1,"我是沙崙的玫瑰花、〔或作水仙花〕是谷中的百合花。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",2,2,"我的佳偶在女子中、好像百合花在荊棘內。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",2,3,"我的良人在男子中、如同蘋果樹在樹林中。我歡歡喜喜坐在他的蔭下、嘗他果子的滋味覺得甘甜。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",2,4,"他帶我入筵宴所、以愛為旗在我以上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",2,5,"求你們給我葡萄乾增補我力、給我蘋果暢快我心．因我思愛成病。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",2,6,"他的左手在我頭下、他的右手將我抱住。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",2,7,"耶路撒冷的眾女子阿、我指著羚羊、或田野的母鹿、囑咐你們、不要驚動、不要叫醒我所親愛的、等他自己情願。〔不要叫醒雲雲或作不要激動愛情等他自發〕")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",2,8,"聽阿是我良人的聲音．看哪、他躥山越嶺而來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",2,9,"我的良人好像羚羊、或像小鹿．他站在我們牆壁後、從窗戶往裡觀看、從窗櫺往裡窺探。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",2,10,"我良人對我說、我的佳偶、我的美人、起來、與我同去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",2,11,"因為冬天已往．雨水止住過去了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",2,12,"地上百花開放．百鳥鳴叫的時候〔或作修理葡萄樹的時候〕已經來到、斑鳩的聲音在我們境內也聽見了．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",2,13,"無花果樹的果子漸漸成熟、葡萄樹開花放香。我的佳偶、我的美人、起來、與我同去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",2,14,"我的鴿子阿、你在磐石穴中、在陡巖的隱密處．求你容我得見你的面貌、得聽你的聲音．因為你的聲音柔和、你的面貌秀美。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",2,15,"要給我們擒拿狐狸、就是毀壞葡萄園的小狐狸．因為我們的葡萄正在開花。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",2,16,"良人屬我、我也屬他．他在百合花中牧放群羊。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",2,17,"我的良人哪、求你等到天起涼風、日影飛去的時候、你要轉回、好像羚羊、或像小鹿在比特山上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",3,1,"我夜間躺臥在床上、尋找我心所愛的．我尋找他、卻尋不見。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",3,2,"我說、我要起來、遊行城中．在街市上、在寬闊處、尋找我心所愛的．我尋找他、卻尋不見。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",3,3,"城中巡邏看守的人遇見我．我問他們、你們看見我心所愛的沒有。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",3,4,"我剛離開他們、就遇見我心所愛的．我拉住他、不容他走、領他入我母家、到懷我者的內室。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",3,5,"耶路撒冷的眾女子阿、我指著羚羊、或田野的母鹿、囑咐你們、不要驚動、不要叫醒我所親愛的、等他自己情願。〔不要叫醒雲雲或作不要激動愛情等他自發〕")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",3,6,"那從曠野上來、形狀如煙柱、以沒藥和乳香、並商人各樣香粉薰的、是誰呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",3,7,"看哪、是所羅門的轎．四圍有六十個勇士、都是以色列中的勇士．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",3,8,"手都持刀、善於爭戰．腰間佩刀、防備夜間有驚慌。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",3,9,"所羅門王用利巴嫩木、為自己製造一乘華轎。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",3,10,"轎柱是用銀作的、轎底是用金作的、坐墊是紫色的、其中所鋪的乃耶路撒冷眾女子的愛情。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",3,11,"錫安的眾女子阿、你們出去、觀看所羅門王、頭戴冠冕、就是在他婚筵的日子、心中喜樂的時候、他母親給他戴上的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",4,1,"我的佳偶、你甚美麗、你甚美麗．你的眼在帕子內好像鴿子眼。你的頭髮如同山羊群、臥在基列山旁。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",4,2,"你的牙齒如新剪毛的一群母羊、洗淨上來、個個都有雙生、沒有一隻喪掉子的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",4,3,"你的唇好像一條朱紅線、你的嘴也秀美。你的兩太陽、在帕子內如同一塊石榴。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",4,4,"你的頸項好像大衛建造收藏軍器的高臺、其上懸掛一千盾牌、都是勇士的籐牌。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",4,5,"你的兩乳、好像百合花中喫草的一對小鹿、就是母鹿雙生的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",4,6,"我要往沒藥山、和乳香岡去、直等到天起涼風、日影飛去的時候回來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",4,7,"我的佳偶、你全然美麗、毫無瑕疵。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",4,8,"我的新婦、求你與我一同離開利巴嫩、與我一同離開利巴嫩．從亞瑪拿頂、從示尼珥與黑門頂、從有獅子的洞、從有豹子的山、往下觀看。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",4,9,"我妹子、我新婦、你奪了我的心．你用眼一看、用你項上的一條金鍊、奪了我的心。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",4,10,"我妹子、我新婦、你的愛情何其美．你的愛情比酒更美．你膏油的香氣勝過一切香品。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",4,11,"我新婦、你的嘴唇滴蜜、好像蜂房滴蜜．你的舌下有蜜有奶．你衣服的香氣如利巴嫩的香氣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",4,12,"我妹子、我新婦、乃是關鎖的園、禁閉的井、封閉的泉源。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",4,13,"你園內所種的結了石榴、有佳美的果子、並鳳仙花、與哪噠樹。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",4,14,"有哪噠和番紅花、菖蒲、和桂樹、並各樣乳香木、沒藥、沉香與一切上等的果品。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",4,15,"你是園中的泉、活水的井、從利巴嫩流下來的溪水。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",4,16,"北風阿、興起．南風阿、吹來．吹在我的園內、使其中的香氣發出來。願我的良人進入自己園裡、喫他佳美的果子。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",5,1,"我妹子、我新婦、我進了我的園中、採了我的沒藥和香料、喫了我的蜜房和蜂蜜．喝了我的酒和奶．我的朋友們、請喫、我所親愛的、請喝、且多多的喝。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",5,2,"我身睡臥、我心卻醒．這是我良人的聲音．他敲門、說、我的妹子、我的佳偶、我的鴿子、我的完全人、求你給我開門、因我的頭滿了露水、我的頭髮被夜露滴濕。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",5,3,"我回答說、我脫了衣裳、怎能再穿上呢．我洗了腳、怎能再玷污呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",5,4,"我的良人從門孔裡伸進手來、我便因他動了心。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",5,5,"我起來、要給我良人開門．我的兩手滴下沒藥、我的指頭有沒藥汁滴在門閂上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",5,6,"我給我的良人開了門．我的良人卻已轉身走了。他說話的時候、我神不守舍．我尋找他、竟尋不見．我呼叫他、他卻不回答。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",5,7,"城中巡邏看守的人遇見我、打了我、傷了我．看守城牆的人奪去我的披肩。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",5,8,"耶路撒冷的眾女子阿、我囑咐你們、若遇見我的良人、要告訴他、我因思愛成病。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",5,9,"你這女子中極美麗的、你的良人、比別人的良人有何強處．你的良人、比別人的良人有何強處、你就這樣囑咐我們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",5,10,"我的良人、白而且紅、超乎萬人之上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",5,11,"他的頭像至精的金子．他的頭髮厚密纍垂、黑如烏鴉。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",5,12,"他的眼如溪水旁的鴿子眼、用奶洗淨、安得合式。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",5,13,"他的兩腮如香花畦、如香草臺．他的嘴唇像百合花、且滴下沒藥汁。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",5,14,"他的兩手好像金管、鑲嵌水蒼玉．他的身體如同雕刻的像牙、周圍鑲嵌藍寶石。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",5,15,"他的腿好像白玉石柱、安在精金座上．他的形狀如利巴嫩、且佳美如香柏樹。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",5,16,"他的口極其甘甜．他全然可愛。耶路撒冷的眾女子阿、這是我的良人、這是我的朋友。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",6,1,"你這女子中極美麗的、你的良人往何處去了．你的良人轉向何處去了、我們好與你同去尋找他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",6,2,"我的良人下入自己園中、到香花畦、在園內牧放群羊、採百合花。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",6,3,"我屬我的良人、我的良人也屬我．他在百合花中牧放群羊。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",6,4,"我的佳偶阿、你美麗如得撒、秀美如耶路撒冷、威武如展開旌旗的軍隊。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",6,5,"求你掉轉眼目不看我、因你的眼目使我驚亂。你的頭髮如同山羊群、臥在基列山旁。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",6,6,"你的牙齒如一群母羊、洗淨上來．個個都有雙生、沒有一隻喪掉子的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",6,7,"你的兩太陽在帕子內如同一塊石榴。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",6,8,"有六十王後八十妃嬪、並有無數的童女。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",6,9,"我的鴿子、我的完全人、只有這一個、是他母親獨生的．是生養他者所寶愛的。眾女子見了、就稱他有福．王後妃嬪見了、也讚美他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",6,10,"那向外觀看如晨光發現、美麗如月亮、皎潔如日頭、威武如展開旌旗軍隊的是誰呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",6,11,"我下入核桃園、要看谷中青綠的植物、要看葡萄發芽沒有、石榴開花沒有．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",6,12,"不知不覺、我的心將我安置在我尊長的車中。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",6,13,"回來、回來．書拉密女、你回來、你回來、使我們得觀看你。你們為何要觀看書拉密女、像觀看瑪哈念跳舞的呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",7,1,"王女阿、你的腳在鞋中何其美好．你的大腿圓潤好像美玉、是巧匠的手作成的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",7,2,"你的肚臍如圓杯不缺調和的酒．你的腰如一堆麥子、周圍有百合花。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",7,3,"你的兩乳好像一對小鹿、就是母鹿雙生的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",7,4,"你的頸項如像牙臺．你的眼目像希實本巴特拉併門旁的水池．你的鼻子彷彿朝大馬色的利巴嫩塔。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",7,5,"你的頭在你身上好像迦密山．你頭上的髮是紫黑色．王的心因這下垂的髮綹繫住了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",7,6,"我所愛的、你何其美好．何其可悅。使人歡暢喜樂．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",7,7,"你的身量、好像棕樹．你的兩乳如同其上的果子、纍纍下垂。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",7,8,"我說我要上這棕樹、抓住枝子．願你的兩乳、好像葡萄纍纍下垂、你鼻子的氣味香如蘋果、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",7,9,"你的口如上好的酒、女子說、為我的良人下咽舒暢、流入睡覺人的嘴中。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",7,10,"我屬我的良人．他也戀慕我。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",7,11,"我的良人、來罷、你我可以往田間去．你我可以在村莊住宿。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",7,12,"我們早晨起來往葡萄園去看看葡萄發芽開花沒有、石榴放蕊沒有．我在那裡要將我的愛情給你。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",7,13,"風茄放香．在我們的門內有各樣新陳佳美的果子．我的良人、這都是我為你存留的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",8,1,"巴不得你像我的兄弟、像喫我母親奶的兄弟．我在外頭遇見你、就與你親嘴．誰也不輕看我。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",8,2,"我必引導你、領你進我母親的家、我可以領受教訓、也就使你喝石榴汁釀的香酒。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",8,3,"他的左手必在我頭下、他的右手必將我抱住。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",8,4,"耶路撒冷的眾女子阿、我囑咐你們、不要驚動、不要叫醒我所親愛的、等他自己情願。〔不要叫醒雲雲或作不要激動愛情等他自發〕")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",8,5,"那靠著良人從曠野上來的、是誰呢。我在蘋果樹下叫醒你．你母親在那裡為你劬勞、生養你的在那裡為你劬勞。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",8,6,"求你將我放在你心上如印記、帶在你臂上如戳記．因為愛情如死之堅強．嫉恨如陰間之殘忍．所發的電光、是火焰的電光、是耶和華的烈焰。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",8,7,"愛情、眾水不能息滅、大水也不能淹沒．若有人拿家中所有的財寶要換愛情、就全被藐視。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",8,8,"我們有一小妹、他的兩乳尚未長成．人來提親的日子、我們當為他怎樣辦理。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",8,9,"他若是牆、我們要在其上建造銀塔．他若是門、我們要用香柏木板圍護他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",8,10,"我是牆、我兩乳像其上的樓．那時我在他眼中像得平安的人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",8,11,"所羅門在巴力哈們有一葡萄園．他將這葡萄園交給看守的人、為其中的果子必交一千舍客勒銀子。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",8,12,"我自己的葡萄園在我面前．所羅門哪、一千舍客勒歸你、二百舍客勒歸看守果子的人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",8,13,"你這住在園中的、同伴都要聽你的聲音．求你使我也得聽見。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Son",8,14,"我的良人哪、求你快來、如羚羊或小鹿在香草山上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",1,1,"當烏西雅、約坦、亞哈斯、希西家、作猶大王的時候、亞摩斯的兒子以賽亞得默示、論到猶大、和耶路撒冷。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",1,2,"天哪、要聽、地阿、側耳而聽．因為耶和華說、我養育兒女、將他們養大、他們竟悖逆我。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",1,3,"牛認識主人、驢認識主人的槽．以色列卻不認識、我的民卻不留意。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",1,4,"嗐、犯罪的國民、擔著罪孽的百姓、行惡的種類、敗壞的兒女．他們離棄耶和華、藐視以色列的聖者、與他生疏、往後退步。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",1,5,"你們為甚麼屢次悖逆、還要受責打麼。你們已經滿頭疼痛、全心發昏。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",1,6,"從腳掌到頭頂、沒有一處完全的．盡是傷口、青腫、與新打的傷痕．都沒有收口、沒有纏裹、也沒有用膏滋潤。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",1,7,"你們的地土已經荒涼．你們的城邑被火焚燬．你們的田地、在你們眼前為外邦人所侵吞、既被外邦人傾覆、就成為荒涼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",1,8,"僅存錫安城、〔城原文作女子〕好像葡萄園的草棚、瓜田的茅屋、被圍困的城邑。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",1,9,"若不是萬軍之耶和華給我們稍留餘種、我們早已像所多瑪、蛾摩拉的樣子了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",1,10,"你們這所多瑪的官長阿、要聽耶和華的話．你們這蛾摩拉的百姓阿、要側耳聽我們　神的訓誨。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",1,11,"耶和華說、你們所獻的許多祭物、與我何益呢．公綿羊的燔祭、、和肥畜的脂油、我已經夠了．公牛的血、羊羔的血、公山羊的血、我都不喜悅。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",1,12,"你們來朝見我、誰向你們討這些、使你們踐踏我的院宇呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",1,13,"你們不要再獻虛浮的供物．香品是我所憎惡的．月朔、和安息日、並宣召的大會、也是我所憎惡的．作罪孽、又守嚴肅會、我也不能容忍。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",1,14,"你們的月朔、和節期、我心裡恨惡、我都以為麻煩．我擔當、便不耐煩。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",1,15,"你們舉手禱告、我必遮眼不看．就是你們多多的祈禱、我也不聽．你們的手都滿了殺人的血。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",1,16,"你們要洗濯、自潔．從我眼前除掉你們的惡行．要止住作惡、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",1,17,"學習行善．尋求公平、解救受欺壓的、給孤兒伸冤、為寡婦辨屈。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",1,18,"耶和華說、你們來、我們彼此辯論．你們的罪雖像硃紅、必變成雪白．雖紅如丹顏、必白如羊毛。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",1,19,"你們若甘心聽從、必喫地上的美物．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",1,20,"若不聽從、反倒悖逆、必被刀劍吞滅．這是耶和華親口說的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",1,21,"可歎忠信的城、變為妓女．從前充滿了公平、公義居在其中、現今卻有兇手居住。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",1,22,"你的銀子、變為渣滓、你的酒、用水攙對。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",1,23,"你的官長居心悖逆、與盜賊作伴．各都喜愛賄賂、追求贓私．他們不為孤兒伸冤、寡婦的案件、也不得呈到他們面前。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",1,24,"因此、主萬軍之耶和華以色列的大能者說、哎、我要向我的對頭雪恨、向我的敵人報仇．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",1,25,"我必反手加在你身上、煉盡你的渣滓、除淨你的雜質．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",1,26,"我也必復還你的審判官、像起初一樣、復還你的謀士、像起先一般．然後、你必稱為公義之城、忠信之邑。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",1,27,"錫安必因公平得蒙救贖、其中歸正的人、必因公義得蒙救贖。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",1,28,"但悖逆的、、和犯罪的、必一同敗亡、離棄耶和華的、必致消滅。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",1,29,"那等人必因你們所喜愛的橡樹抱愧、你們必因所選擇的園子蒙羞。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",1,30,"因為你們必如葉子枯乾的橡樹、好像無水澆灌的園子。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",1,31,"有權勢的必如麻瓤、他的工作、好像火星、都要一同焚燬、無人撲滅。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",2,1,"亞摩斯的兒子以賽亞得默示、論到猶大、、和耶路撒冷。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",2,2,"末後的日子、耶和華殿的山必堅立、超乎諸山、高舉過於萬嶺．萬民都要流歸這山")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",2,3,"必有許多國的民前往、說、來罷、我們登耶和華的山、奔雅各　神的殿．主必將他的道教訓我們、我們也要行他的路．因為訓誨必出於錫安、耶和華的言語、必出於耶路撒冷。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",2,4,"他必在列國中施行審判、為許多國民斷定是非．他們要將刀打成犁頭、把槍打成鐮刀．這國不舉刀攻擊那國、他們也不再學習戰事。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",2,5,"雅各家阿、來罷、我們在耶和華的光明中行走。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",2,6,"耶和華、你離棄了你百姓雅各家、是因他們充滿了東方的風俗、作觀兆的、像非利士人一樣、並與外邦人擊掌。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",2,7,"他們的國滿了金銀、財寶也無窮．他們的地滿了馬匹、車輛也無數。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",2,8,"他們的地滿了偶像．他們跪拜自己手所造的、就是自己指頭所作的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",2,9,"卑賤人屈膝、尊貴人下跪．所以不可饒恕他們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",2,10,"你當進入巖穴、藏在土中、躲避耶和華的驚嚇、、和他威嚴的榮光。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",2,11,"到那日、眼目高傲的必降為卑、性情狂傲的都必屈膝．惟獨耶和華被尊崇。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",2,12,"必有萬軍耶和華降罰的一個日子、要臨到驕傲狂妄的、一切自高的都必降為卑．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",2,13,"又臨到利巴嫩高大的香柏樹、、和巴珊的橡樹．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",2,14,"又臨到一切高山的峻嶺．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",2,15,"又臨到高臺、、和堅固城牆．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",2,16,"又臨到他施的船隻、並一切可愛的美物．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",2,17,"驕傲的必屈膝、狂妄的必降卑．在那日、惟獨耶和華被尊崇。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",2,18,"偶像必全然廢棄。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",2,19,"耶和華興起使地大震動的時候、人就進入石洞、進入土穴、躲避耶和華的驚嚇、、和他威嚴的榮光。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",2,20,"到那日、人必將為拜而造的金偶像、銀偶像、拋給田鼠、和蝙蝠．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",2,21,"到耶和華興起使地大震動的時候、人好進入磐石洞中、、和巖石穴裡、躲避耶和華的驚嚇、、和他威嚴的榮光。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",2,22,"你們休要倚靠世人、他鼻孔裡不過有氣息．他在一切事上可算甚麼呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",3,1,"主萬軍之耶和華從耶路撒冷和猶大、除掉眾人所倚靠的、所仗賴的、就是所倚靠的糧、所仗賴的水．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",3,2,"除掉勇士、和戰士、審判官、和先知、占蔔的、和長老．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",3,3,"五十夫長、和尊貴人．謀士、和有巧藝的、以及妙行法術的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",3,4,"主說、我必使孩童作他們的首領、使嬰孩轄管他們。")');
