tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",23,4,"西頓哪、你當慚愧．因為大海說、就是海中的保障說、我沒有劬勞、也沒有生產、沒有養育男子、也沒有撫養童女。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",23,5,"這風聲傳到埃及、埃及人為推羅的風聲、極其疼痛。")');
