tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",24,16,"我們聽見從地極有人歌唱、說、榮耀歸於義人。我卻說、我消滅了、我消滅了、我有禍了．詭詐的行詭詐、詭詐的大行詭詐。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",24,17,"地上的居民哪、恐懼、陷坑、網羅、都臨近你。")');
