tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",26,14,"他們死了、必不能再活．他們去世、必不能再起．因為你刑罰他們、毀滅他們、他們的名號就全然消滅。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",26,15,"耶和華阿、你增添國民、你增添國民．你得了榮耀、又擴張地的四境。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",26,16,"耶和華阿、他們在急難中尋求你．你的懲罰臨到他們身上、他們就傾心吐膽禱告你。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",26,17,"婦人懷孕、臨產疼痛、在痛苦之中喊叫、耶和華阿、我們在你面前、也是如此。")');
