tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",30,30,"耶和華必使人聽他威嚴的聲音、又顯他降罰的膀臂、和他怒中的忿恨、並吞滅的火焰、與霹雷、暴風、冰雹。")');
