tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",37,2,"使家宰以利亞敬、和書記舍伯那、並祭司中的長老、都披上麻布、去見亞摩斯的兒子先知以賽亞。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",37,3,"對他說、希西家如此說、今日是急難、責罰、凌辱的日子、就如婦人將要生產嬰孩、卻沒有力量生產")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",37,4,"或者耶和華你的　神、聽見拉伯沙基的話、就是他主人亞述王打發他來辱罵永生　神的話．耶和華你的　神聽見這話就發斥責．故此求你為餘剩的民、揚聲禱告。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",37,5,"希西家王的臣僕、就去見以賽亞。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",37,6,"以賽亞對他們說、要這樣對你們的主人說、耶和華如此說、你聽見亞述王的僕人褻瀆我的話、不要懼怕。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",37,7,"我必驚動〔原文作使靈進入〕他的心、他要聽見風聲就歸回本地、我必使他在那裡倒在刀下。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",37,8,"拉伯沙基回去、正遇見亞述王攻打立拿．原來他早聽見亞述王拔營離開拉吉。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",37,9,"亞述王聽見人論古實王特哈加說、他出來要與你爭戰。亞述王一聽見、就打發使者去見希西家、吩咐他們說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",37,10,"你們對猶大王希西家如此說、不要聽你所倚靠的　神欺哄你、說、耶路撒冷必不交在亞述王的手中。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",37,11,"你總聽說亞述諸王向列國所行的、乃是盡行滅絕、難道你還能得救麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",37,12,"我列祖所毀滅的、就是歌散、哈蘭、利色、和屬提拉撒的伊甸人．這些國的神、何曾拯救這些國呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",37,13,"哈馬的王、亞珥拔的王、西法瓦音城的王、希拿和以瓦的王、都在哪裡呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",37,14,"希西家從使者手裡接過書信來、看完了、就上耶和華的殿、將書信在耶和華面前展開。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",37,15,"希西家向耶和華禱告說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",37,16,"坐在二基路伯上萬軍之耶和華以色列的　神阿、你、惟有你、是天下萬國的　神．你曾創造天地。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",37,17,"耶和華阿、求你側耳而聽．耶和華阿、求你睜眼而看．要聽西拿基立的一切話、他是打發使者來辱罵永生　神的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",37,18,"耶和華阿、亞述諸王果然使列國、和列國之地變為荒涼．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",37,19,"將列國的神像都扔在火裡、因為他本不是神、乃是人手所造的、是木頭、石頭的、所以滅絕他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",37,20,"耶和華我們的　神阿、現在求你救我們脫離亞述王的手、使天下萬國都知道惟有你是耶和華。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",37,21,"亞摩斯的兒子以賽亞、就打發人去見希西家說、耶和華以色列的　神如此說、你既然求我攻擊亞述王西拿基立、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",37,22,"所以耶和華論他這樣說、錫安的處女藐視你、嗤笑你、耶路撒冷的女子向你搖頭。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",37,23,"你辱罵誰、褻瀆誰、揚起聲來、高舉眼目攻擊誰呢、乃是攻擊以色列的聖者。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",37,24,"你藉你的臣僕辱罵主、說、我率領許多戰車上山頂、到利巴嫩極深之處、我要砍伐其中高大的香柏樹、和佳美的松樹、我必上極高之處、進入肥田的樹林。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",37,25,"我已經挖井喝水．我必用腳掌踏乾埃及的一切河。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",37,26,"耶和華說、你豈沒有聽見我早先所作的、古時所立的麼、現在藉你使堅固城荒廢、變為亂堆。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",37,27,"所以其中的居民力量甚小、驚惶羞愧．他們像野草、像青菜、如房頂上的草、又如田間未長成的禾稼、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",37,28,"你坐下、你出去、你進來、你向我發烈怒、我都知道。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",37,29,"因你向我發烈怒、又因你狂傲的話達到我耳中、我就要用鉤子鉤上你的鼻子、把嚼環放在你口裡、使你從原路轉回去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",37,30,"以色列人哪、我賜你們一個證據、你們今年要喫自生的、明年也要喫自長的、至於後年、你們要耕種收割、栽植葡萄園、喫其中的果子。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",37,31,"猶大家所逃脫餘剩的、仍要往下扎根、向上結果。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",37,32,"必有餘剩的民、從耶路撒冷而出、必有逃脫的人、從錫安山而來．萬軍之耶和華的熱心必成就這事。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",37,33,"所以耶和華論亞述王如此說、他必不得來到這城、也不在這裡射箭、不得拿盾牌到城前、也不築壘攻城。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",37,34,"他從那條路來、必從那條路回去、必不得來到這城．這是耶和華說的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",37,35,"因我為自己的緣故、又為我僕人大衛的緣故、必保護拯救這城。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",37,36,"耶和華的使者出去、在亞述營中殺了十八萬五千人．清早有人起來一看、都是死屍了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",37,37,"亞述王西拿基立、就拔營回去、住在尼尼微。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",37,38,"一日在他的神、尼斯洛廟裡叩拜、他兒子亞得米勒、和沙利色、用刀殺了他、就逃到亞拉臘地．他兒子以撒哈頓接續他作王。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",38,1,"那時希西家病得要死、亞摩斯的兒子先知以賽亞去見他、對他說、耶和華如此說、你當留遺命與你的家、因為你必死不能活了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",38,2,"希西家就轉臉朝牆、禱告耶和華說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",38,3,"耶和華阿、求你記念我在你面前怎樣存完全的心、按誠實行事、又作你眼中所看為善的．希西家就痛哭了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",38,4,"耶和華的話臨到以賽亞說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",38,5,"你去告訴希西家說、耶和華你祖大衛的　神如此說、我聽見了你的禱告、看見了你的眼淚、我必加增你十五年的壽數．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",38,6,"並且我要救你和這城脫離亞述王的手、也要保護這城。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",38,7,"我耶和華必成就我所說的、我先給你一個兆頭．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",38,8,"就是叫亞哈斯的日晷、向前進的日影往後退十度．於是前進的日影、果然在日晷上往後退了十度。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",38,9,"猶大王希西家患病已經痊愈、就作詩說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",38,10,"我說、正在我中年〔或作晌午〕之日、必進入陰間的門．我餘剩的年歲不得享受。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",38,11,"我說、我必不得見耶和華、就是在活人之地不見耶和華．我與世上的居民不再見面。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",38,12,"我的住處被遷去離開我、好像牧人的帳棚一樣．我將性命捲起、像織布的捲布一樣．耶和華必將我從機頭剪斷、從早到晚、他要使我完結。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",38,13,"我使自己安靜直到天亮．他像獅子折斷我一切的骨頭、從早到晚、他要使我完結。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",38,14,"我像燕子呢喃．像白鶴鳴叫．又像鴿子哀鳴．我因仰觀、眼睛困倦．耶和華阿、我受欺壓、求你為我作保。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",38,15,"我可說甚麼呢、他應許我的、也給我成就了．我因心裡的苦楚、在一生的年日、必悄悄而行。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",38,16,"主阿、人得存活、乃在乎此．我靈存活、也全在此。所以求你使我痊愈、仍然存活。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",38,17,"看哪、我受大苦、本為使我得平安、你因愛我的靈魂、〔或作生命〕便救我脫離敗壞的坑、因為你將我一切的罪、扔在你的背後。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",38,18,"原來陰間不能稱謝你、死亡不能頌揚你、下坑的人不能盼望你的誠實．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",38,19,"只有活人、活人必稱謝你、像我今日稱謝你一樣．為父的、必使兒女知道你的誠實。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",38,20,"耶和華肯救我、所以我們要一生一世、在耶和華殿中用絲絃的樂器、唱我的詩歌。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",38,21,"以賽亞說、當取一塊無花果餅來、貼在瘡上、王必痊愈。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",38,22,"希西家問說、我能上耶和華的殿、有甚麼兆頭呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",39,1,"那時、巴比倫王巴拉但的兒子米羅達巴拉但、聽見希西家病而痊愈、就送書信和禮物給他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",39,2,"希西家喜歡見使者、就把自己寶庫的金子、銀子、香料、貴重的膏油、和他武庫的一切軍器、並所有的財寶都給他們看．他家中和全國之內、希西家沒有一樣不給他們看的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",39,3,"於是先知以賽亞來見希西家王、問他說、這些人說甚麼、他們從那裡來見你．希西家說、他們從遠方的巴比倫來見我。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",39,4,"以賽亞說、他們在你家裡看見了甚麼．希西家說、凡我家中所有的、他們都看見了、我財寶中沒有一樣不給他們看的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",39,5,"以賽亞對希西家說、你要聽萬軍之耶和華的話．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",39,6,"日子必到、凡你家裡所有的、並你列祖積蓄到如今的、都要被擄到巴比倫去、不留下一樣、這是耶和華說的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",39,7,"並且從你本身所生的眾子、其中必有被擄去、在巴比倫王宮裡當太監的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",39,8,"希西家對以賽亞說、你所說耶和華的話甚好、因為在我的年日中、必有太平和穩固的景況。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",40,1,"你們的　神說、你們要安慰、安慰我的百姓。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",40,2,"要對耶路撒冷說安慰的話、又向他宣告說、他爭戰的日子已滿了、他的罪孽赦免了、他為自己的一切罪、從耶和華手中加倍受罰。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",40,3,"有人聲喊著說、在曠野預備耶和華的路、〔或作在曠野有人聲喊著說當預備耶和華的路〕在沙漠地修平我們　神的道。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",40,4,"一切山窪都要填滿、大小山岡都要削平、高高低低的要改為平坦、崎崎嶇嶇的必成為平原。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",40,5,"耶和華的榮耀必然顯現、凡有血氣的、必一同看見、因為這是耶和華親口說的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",40,6,"有人聲說、你喊叫罷．有一個說、我喊叫甚麼呢．說、凡有血氣的、盡都如草、他的美容、都像野地的花。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",40,7,"草必枯乾、花必凋殘、因為耶和華的氣吹在其上．百姓誠然是草。")');
