tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",40,11,"他必像牧人牧養自己的羊群、用膀臂聚集羊羔抱在懷中、慢慢引導那乳養小羊的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",40,12,"誰曾用手心量諸水、用手虎口量蒼天、用升鬥盛大地的塵土、用秤稱山嶺、用天平平岡陵呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",40,13,"誰曾測度耶和華的心、〔或作誰曾指示耶和華的靈〕、或作他的謀士指教他呢。")');
