tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",40,15,"看哪、萬民都像水桶的一滴、又算如天平上的微塵．他舉起眾海島、好像極微之物。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Isa",40,16,"利巴嫩的樹林不夠當柴燒、其中的走獸也不夠作燔祭。")');
