tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jer",6,26,"我民哪、〔民原文作民女〕應當腰束麻布、滾在灰中．你要悲傷、如喪獨生子痛痛哭號、因為滅命的要忽然臨到我們。")');
