tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jer",7,1,"耶和華的話臨到耶利米、說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jer",7,2,"你當站在耶和華殿的門口、在那裡宣傳這話、說、你們進這些門敬拜耶和華的一切猶大人、當聽耶和華的話。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jer",7,3,"萬軍之耶和華以色列的　神如此說、你們改正行動作為、我就使你們在這地方仍然居住。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jer",7,4,"你們不要倚靠虛謊的話、說、這些是耶和華的殿、是耶和華的殿、是耶和華的殿。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Jer",7,5,"你們若實在改正行動作為、在人和鄰舍中間誠然施行公平．")');
