tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Eze",12,7,"我就照著所吩咐的去行、白日帶出我的物件、好像預備擄去使用的物件．到了晚上、我用手挖通了牆．天黑的時候、就當他們眼前搭在肩頭上帶出去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Eze",12,8,"次日早晨、耶和華的話臨到我說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Eze",12,9,"人子阿、以色列家、就是那悖逆之家、豈不是問你說、你作什麼呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Eze",12,10,"你要對他們說、主耶和華如此說、這是關乎耶路撒冷的君王、和他周圍以色列全家的預表。〔原文作擔子〕")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Eze",12,11,"你要說、我作你們的預兆、我怎樣行、他們所遭遇的也必怎樣、他們必被擄去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Eze",12,12,"他們中間的君王、也必在天黑的時候將物件搭在肩頭上帶出去．他們要挖通了牆、從其中帶出去．他必蒙住臉、眼看不見地。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Eze",12,13,"我必將我的網撒在他身上、他必在我的網羅中纏住、我必帶他到迦勒底人之地的巴比倫、他雖死在那裡、卻看不見那地。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Eze",12,14,"周圍一切幫助他的、和他所有的軍隊、我必分散四方、〔方原文作風〕也要拔刀追趕他們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Eze",12,15,"我將他們四散在列國、分散在列邦的時候、他們就知道我是耶和華。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Eze",12,16,"我卻要留下他們幾個人得免刀劍、飢荒、瘟疫、使他們在所到的各國中、述說他們一切可憎的事．人就知道我是耶和華。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Eze",12,17,"耶和華的話又臨到我說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Eze",12,18,"人子阿、你喫飯必膽戰、喝水必惶惶憂慮。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Eze",12,19,"你要對這地的百姓說、主耶和華論耶路撒冷、和以色列地的居民、如此說、他們喫飯必憂慮、喝水必驚惶．因其中居住的眾人所行強暴的事、這地必然荒廢、一無所存。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Eze",12,20,"有居民的城邑必變為荒場、地也必變為荒廢．你們就知道我是耶和華。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Eze",12,21,"耶和華的話臨到我說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Eze",12,22,"人子阿、在你們以色列地、怎麼有這俗語說、日子遲延、一切異像都落了空呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Eze",12,23,"你要告訴他們說、主耶和華如此說、我必使這俗語止息、以色列中不再用這俗語、你卻要對他們說、日子臨近、一切的異像必都應驗。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Eze",12,24,"從此、在以色列家中必不再有虛假的異像、和奉承的占蔔．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Eze",12,25,"我耶和華說話、所說的必定成就、不再耽延．你們這悖逆之家、我所說的話、必趁你們在世的日子成就．這是主耶和華說的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Eze",12,26,"耶和華的話又臨到我說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Eze",12,27,"人子阿、以色列家的人說、他所見的異像、是關乎後來許多的日子、所說的預言、是指著極遠的時候。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Eze",12,28,"所以你要對他們說、主耶和華如此說、我的話沒有一句再耽延的、我所說的必定成就．這是主耶和華說的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Eze",13,1,"耶和華的話臨到我說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Eze",13,2,"人子阿、你要說預言攻擊以色列中說預言的先知、對那些本己心發預言的說、你們當聽耶和華的話。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Eze",13,3,"主耶和華如此說、愚頑的先知有禍了、他們隨從自己的心意、卻一無所見。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Eze",13,4,"以色列阿、你的先知好像荒場中的狐狸．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Eze",13,5,"沒有上去堵擋破口、也沒有為以色列家重修牆垣、使他們當耶和華的日子、在陣上站立得住。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Eze",13,6,"這些人所見的是虛假、是謊詐的占蔔、他們說、是耶和華說的、其實耶和華並沒有差遣他們、他們倒使人指望那話必然立定。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Eze",13,7,"你們豈不是見了虛假的異像麼．豈不是說了謊詐的占蔔麼．你們說、這是耶和華說的、其實我沒有說。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Eze",13,8,"所以主耶和華如此說、因你們說的是虛假、見的是謊詐、我就與你們反對．這是主耶和華說的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Eze",13,9,"我的手必攻擊那見虛假異像、用謊詐占蔔的先知、他們必不列在我百姓的會中、不錄在以色列家的冊上、也不進入以色列地．你們就知道我是主耶和華。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Eze",13,10,"因為他們誘惑我的百姓、說、平安、其實沒有平安、就像有人立起牆壁、他們倒用未泡透的灰抹上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Eze",13,11,"所以你要對那些抹上未泡透灰的人說、牆要倒塌、必有暴雨漫過．大冰雹阿、你們要降下、狂風也要吹裂這牆。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Eze",13,12,"這牆倒塌之後、人豈不問你們說、你們抹上未泡透的灰在哪裡呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Eze",13,13,"所以主耶和華如此說、我要發怒、使狂風吹裂這牆、在怒中使暴雨漫過、又發怒降下大冰雹、毀滅這牆。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Eze",13,14,"我要這樣拆毀你們那未泡透灰所抹的牆、拆平到地、以致根基露出、牆必倒塌、你們也必在其中滅亡．你們就知道我是耶和華。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Eze",13,15,"我要這樣向牆、和用未泡透灰抹牆的人成就我怒中所定的、並要對你們說、牆和抹牆的人都沒有了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Eze",13,16,"這抹牆的就是以色列的先知、他們指著耶路撒冷說預言、為這城見了平安的異像、其實沒有平安．這是主耶和華說的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Eze",13,17,"人子阿、你要面向本民中、從己心發預言的女子說預言、攻擊他們、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Eze",13,18,"說、主耶和華如此說、這些婦女有禍了．他們為眾人的膀臂縫靠枕、給高矮之人作下垂的頭巾、為要獵取人的性命．難道你們要獵取我百姓的性命、為利己將人救活麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Eze",13,19,"你們為兩把大麥、為幾塊餅、在我民中褻瀆我、對肯聽謊言的民說謊、殺死不該死的人、救活不該活的人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Eze",13,20,"所以主耶和華如此說、看哪、我與你們的靠枕反對、就是你們用以獵取人、使人的性命如鳥飛的．我要將靠枕從你們的膀臂上扯去、釋放你們獵取如鳥飛的人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Eze",13,21,"我也必撕裂你們下垂的頭巾、救我百姓脫離你們的手、不再被獵取、落在你們手中．你們就知道我是耶和華。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Eze",13,22,"我不使義人傷心、你們卻以謊話使他傷心、又堅固惡人的手、使他不回頭離開惡道得以救活．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Eze",13,23,"你們就不再見虛假的異像、也不再行占蔔的事、我必救我的百姓脫離你們的手．你們就知道我是耶和華。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Eze",14,1,"有幾個以色列長老到我這裡來、坐在我面前。")');
