tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",2,2,"就有許多人聚集、甚至連門前都沒有空地、耶穌就對他們講道。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",2,3,"有人帶著一個癱子來見耶穌、是用四個人抬來的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",2,4,"因為人多、不得近前、就把耶穌所在的房子、拆了房頂、既拆通了、就把癱子連所躺臥的褥子都縋下來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",2,5,"耶穌見他們的信心、就對癱子說、小子、你的罪赦了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",2,6,"有幾個文士坐在那裡、心裡議論說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",2,7,"這個人為甚麼這樣說呢．他說僭妄的話了．除了　神以外、誰能赦罪呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",2,8,"耶穌心中知道他們心裡這樣議論、就說、你們心裡為甚麼這樣議論呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",2,9,"或對癱子說、你的罪赦了．或說、起來、拿你的褥子行走．那一樣容易呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",2,10,"但要叫你們知道人子在地上有赦罪的權柄、就對癱子說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",2,11,"我吩咐你起來、拿你的褥子回家去罷。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",2,12,"那人就起來、立刻拿著褥子、當眾人面前出去了．以致眾人都驚奇、歸榮耀與　神說、我們從來沒有見過這樣的事。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",2,13,"耶穌又出到海邊去、眾人都就了他來、他便教訓他們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",2,14,"耶穌經過的時候、看見亞勒腓的兒子利未、坐在稅關上、就對他說、你跟從我來．他就起來跟從了耶穌。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",2,15,"耶穌在利未家裡坐席的時候、有好些稅吏和罪人、與耶穌並門徒一同坐席．因為這樣的人多、他們也跟隨耶穌。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",2,16,"法利賽人中的文士、〔有古卷作文士和法利賽人〕看見耶穌和罪人並稅吏一同喫飯、就對他門徒說、他和稅吏並罪人一同喫喝麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",2,17,"耶穌聽見、就對他們說、健康的人用不著醫生、有病的人纔用得著．我來本不是召義人、乃是召罪人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",2,18,"當下、約翰的門徒和法利賽人禁食．他們來問耶穌說、約翰的門徒和法利賽人的門徒禁食、你的門徒倒不禁食、這是為甚麼呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",2,19,"耶穌對他們說、新郎和陪伴之人同在的時候、陪伴之人豈能禁食呢．新郎還同在、他們不能禁食。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",2,20,"但日子將到、新郎要離開他們、那日他們就要禁食。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",2,21,"沒有人把新布縫在舊衣服上．恐怕所補上的新布、帶壞了舊衣服、破的就更大了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",2,22,"也沒有人把新酒裝在舊皮袋裡．恐怕酒把皮袋裂開、酒和皮袋就都壞了．惟把新酒裝在新皮袋裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",2,23,"耶穌當安息日、從麥地經過．他門徒行路的時候、掐了麥穗。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",2,24,"法利賽人對耶穌說、看哪、他們在安息日為甚麼作不可作的事呢．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",2,25,"耶穌對他們說、經上記著大衛和跟從他的人、缺乏飢餓之時所作的事、你們沒有念過麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",2,26,"他當亞比亞他作大祭司的時候、怎麼進了　神的殿、喫了陳設餅、又給跟從他的人喫．這餅除了祭司以外、人都不可喫。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",2,27,"又對他們說、安息日是為人設立的、人不是為安息日設立的．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",2,28,"所以人子也是安息日的主。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",3,1,"耶穌又進了會堂．在那裡有一個人、枯乾了一隻手。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",3,2,"眾人窺探耶穌、在安息日醫治不醫治、意思是要控告耶穌。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",3,3,"耶穌對那枯乾一隻手的人說、起來、站在當中。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",3,4,"又問眾人說、在安息日行善行惡、救命害命、那樣是可以的呢．他們都不作聲。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",3,5,"耶穌怒目周圍看他們、憂愁他們的心剛硬、就對那人說、伸出手來．他把手一伸、手就復了原。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",3,6,"法利賽人出去、同希律一黨的人商議、怎樣可以除滅耶穌。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",3,7,"耶穌和門徒退到海邊去．有許多人從加利利跟隨他．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",3,8,"還有許多人聽見他所作的大事、就從猶太、耶路撒冷、以土買、約但河外、並推羅西頓的四方、來到他那裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",3,9,"他因為人多、就吩咐門徒叫一隻小船伺候著、免得眾人擁擠他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",3,10,"他治好了許多人、所以凡有災病的、都擠進來要摸他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",3,11,"污鬼無論何時看見他、就俯伏在他面前、喊著說、你是　神的兒子。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",3,12,"耶穌再三的囑咐他們、不要把他顯露出來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",3,13,"耶穌上了山、隨自己的意思叫人來、他們便來到他那裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",3,14,"他就設立十二個人、要他們常和自己同在、也要差他們去傳道、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",3,15,"並給他們權柄趕鬼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",3,16,"這十二個人有西門、耶穌又給他起名叫彼得．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",3,17,"還有西庇太的兒子雅各、和雅各的兄弟約翰．又給這兩個人起名叫半尼其、就是雷子的意思．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",3,18,"又有安得烈、腓力、巴多羅買、馬太、多馬、亞勒腓的兒子雅各、和達太、並奮銳黨的西門。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",3,19,"還有賣耶穌的加略人猶大。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",3,20,"耶穌進了一個屋子、眾人又聚集、甚至他連飯也顧不得喫。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",3,21,"耶穌的親屬聽見、就出來要拉住他、因為他們說他癲狂了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",3,22,"從耶路撒冷下來的文士說、他是被別西蔔附著．又說、他是靠著鬼王趕鬼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",3,23,"耶穌叫他們來、用比喻對他們說、撒但怎能趕出撒但呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",3,24,"若一國自相分爭、那國就站立不住。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",3,25,"若一家自相分爭、那家就站立不住。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",3,26,"若撒但自相攻打分爭、他就站立不住、必要滅亡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",3,27,"沒有人能進壯士家裡、搶奪他的家具．必先捆住那壯士、纔可以搶奪他的家。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",3,28,"我實在告訴你們、世人一切的罪、和一切褻瀆的話、都可得赦免．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",3,29,"凡褻瀆聖靈的、卻永不得赦免、乃要擔當永遠的罪。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",3,30,"這話是因為他們說、他是被污鬼附著的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",3,31,"當下耶穌的母親和弟兄、來站在外邊、打發人去叫他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",3,32,"有許多人在耶穌周圍坐著．他們就告訴他說、看哪、你母親、和你弟兄、在外邊找你。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",3,33,"耶穌回答說、誰是我的母親、誰是我的弟兄。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",3,34,"就四面觀看那周圍坐著的人、說、看哪、我的母親、我的弟兄。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",3,35,"凡遵行　神旨意的人、就是我的弟兄姐妹和母親了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",4,1,"耶穌又在海邊教訓人．有許多人到他那裡聚集、他只得上船坐下．船在海裡、眾人都靠近海站在岸上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",4,2,"耶穌就用比喻教訓他們許多道理．在教訓之間、對他們說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",4,3,"你們聽阿．有一個撒種的．出去撒種。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",4,4,"撒的時候、有落在路旁的、飛鳥來喫盡了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",4,5,"有落在土淺石頭地上的、土既不深、發苗最快．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",4,6,"日頭出來一曬、因為沒有根、就枯乾了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",4,7,"有落在荊棘裡的、荊棘長起來、把他擠住了、就不結實。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",4,8,"又有落在好土裡的、就發生長大、結實有三十倍的、有六十倍的、有一百倍的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",4,9,"又說、有耳可聽的、就應當聽。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",4,10,"無人的時候、跟隨耶穌的人、和十二個門徒、問他這比喻的意思。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",4,11,"耶穌對他們說、　神國的奧秘、只叫你們知道、若是對外人講、凡事就用比喻．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",4,12,"叫他們看是看見、卻不曉得．聽是聽見、卻不明白．恐怕他們回轉過來、就得赦免。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",4,13,"又對他們說、你們不明白這比喻麼．這樣怎能明白一切的比喻呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",4,14,"撒種之人所撒的、就是道。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",4,15,"那撒在路旁的、就是人聽了道、撒但立刻來、把撒在他心裡的道奪了去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",4,16,"那撒在石頭地上的、就是人聽了道、立刻歡喜領受．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",4,17,"但他心裡沒有根、不過是暫時的、及至為道遭了患難、或是受了逼迫、立刻就跌倒了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",4,18,"還有那撒在荊棘裡的、就是人聽了道．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",4,19,"後來有世上的思慮、錢財的迷惑、和別樣的私慾、進來把道擠住了、就不能結實。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",4,20,"那撒在好地上的、就是人聽道、又領受、並且結實、有三十倍的、有六十倍的、有一百倍的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",4,21,"耶穌又對他們說、人拿燈來、豈是要放在鬥底下、床底下、不放在燈臺上麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",4,22,"因為掩藏的事、沒有不顯出來的．隱瞞的事、沒有不露出來的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",4,23,"有耳可聽的、就應當聽。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",4,24,"又說、你們所聽的要留心．你們用甚麼量器量給人、也必用甚麼量器量給你們、並且要多給你們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",4,25,"因為有的、還要給他．沒有的、連他所有的也要奪去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",4,26,"又說、　神的國、如同人把種撒在地上、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",4,27,"黑夜睡覺、白日起來、這種就發芽漸長、那人卻不曉得如何這樣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",4,28,"地生五穀是出於自然的．先發苗、後長穗、再後穗上結成飽滿的子粒。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",4,29,"穀既熟了、就用鐮刀去割、因為收成的時候到了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",4,30,"又說、　神的國、我們可用甚麼比較呢．可用甚麼比喻表明呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",4,31,"好像一粒芥菜種、種在地裡的時候、雖比地上的百種都小、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",4,32,"但種上以後、就長起來、比各樣的菜都大、又長出大枝來．甚至天上的飛鳥、可以宿在他的蔭下。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",4,33,"耶穌用許多這樣的比喻、照他們所能聽的、對他們講道．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",4,34,"若不用比喻、就不對他們講．沒有人的時候、就把一切的道講給門徒聽。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",4,35,"當那天晚上、耶穌對門徒說、我們渡到那邊去罷。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",4,36,"門徒離開眾人、耶穌仍在船上、他們就把他一同帶去．也有別的船和他同行。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",4,37,"忽然起了暴風、波浪打入船內、甚至船要滿了水。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",4,38,"耶穌在船尾上、枕著枕頭睡覺．門徒叫醒了他、說、夫子、我們喪命、你不顧麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",4,39,"耶穌醒了．斥責風、向海說、住了罷、靜了罷．風就止住、大大的平靜了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",4,40,"耶穌對他們說、為甚麼膽怯．你們還沒有信心麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",4,41,"他們就大大的懼怕、彼此說、這到底是誰、連風和海也聽從他了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",5,1,"他們來到海那邊、格拉森人的地方。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",5,2,"耶穌一下船、就有一個被污鬼附著的人、從墳塋裡出來迎著他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",5,3,"那人常住在墳塋裡、沒有人能捆住他、就是用鐵鍊也不能．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",5,4,"因為人屢次用腳鐐和鐵鍊捆鎖他、鐵鍊竟被他掙斷了、腳鐐也被他弄碎了．總沒有人能制伏他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",5,5,"他晝夜常在墳塋裡和山中喊叫、又用石頭砍自己。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",5,6,"他遠遠的看見耶穌、就跑過去拜他．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",5,7,"大聲呼叫說、至高　神的兒子耶穌、我與你有甚麼相干．我指著　神懇求你、不要叫我受苦。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",5,8,"是因耶穌曾吩咐他說、污鬼阿、從這人身上出來罷。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",5,9,"耶穌問他說、你名叫甚麼．回答說、我名叫群、因為我們多的緣故。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",5,10,"就再三的求耶穌、不要叫他們離開那地方。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",5,11,"在那裡山坡上、有一大群豬喫食。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",5,12,"鬼就央求耶穌說、求你打發我們往豬群裡附著豬去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",5,13,"耶穌准了他們．污鬼就出來、進入豬裡去．於是那群豬闖下山崖、投在海裡、淹死了．豬的數目、約有二千。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",5,14,"放豬的就逃跑了、去告訴城裡和鄉下的人．眾人就來要看是甚麼事。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",5,15,"他們來到耶穌那裡、看見那被鬼附著的人、就是從前被群鬼所附的、坐著、穿上衣服、心裡明白過來．他們就害怕。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",5,16,"看見這事的、便將鬼附之人所遇見的、和那群豬的事、都告訴了眾人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",5,17,"眾人就央求耶穌離開他們的境界。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",5,18,"耶穌上船的時候、那從前被鬼附著的人、懇求和耶穌同在。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",5,19,"耶穌不許、卻對他說、你回家去、到你的親屬那裡、將主為你所作的、是何等大的事、是怎樣憐憫你、都告訴他們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",5,20,"那人就走了、在低加波利、傳揚耶穌為他作了何等大的事、眾人就都希奇。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",5,21,"耶穌坐船又渡到那邊去、就有許多人到他那裡聚集．他正在海邊上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",5,22,"有一個管會堂的人、名叫睚魯、來見耶穌、就俯伏在他腳前、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",5,23,"再三的求他說、我的小女兒快要死了、求你去按手在他身上、使他痊愈、得以活了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",5,24,"耶穌就和他同去、有許多人跟隨擁擠他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",5,25,"有一個女人、患了十二年的血漏、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",5,26,"在好些醫生手裡、受了許多的苦．又花盡了他所有的、一點也不見好、病勢反倒更重了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",5,27,"他聽見耶穌的事、就從後頭來、雜在眾人中間、摸耶穌的衣裳．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",5,28,"意思說、我只摸他的衣裳、就必痊愈。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",5,29,"於是他血漏的源頭、立刻乾了．他便覺得身上的災病好了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",5,30,"耶穌頓時心裡覺得有能力從自己身上出去、就在眾人中間轉過來、說、誰摸我的衣裳。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",5,31,"門徒對他說、你看眾人擁擠你、還說誰摸我麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",5,32,"耶穌周圍觀看、要見作這事的女人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",5,33,"那女人知道在自己身上所成的事、就恐懼戰兢、來俯伏在耶穌跟前、將實情全告訴他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",5,34,"耶穌對他說、女兒、你的信救了你、平平安安的回去罷．你的災病痊愈了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",5,35,"還說話的時候、有人從管會堂的家裡來說、你的女兒死了、何必還勞動先生呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",5,36,"耶穌聽見所說的話、就對管會堂的說、不要怕．只要信。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",5,37,"於是帶著彼得、雅各、和雅各的兄弟約翰同去、不許別人跟隨他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",5,38,"他們來到管會堂的家裡、耶穌看見那裡亂嚷、並有人大大的哭泣哀號。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",5,39,"進到裡面、就對他們說、為甚麼亂嚷哭泣呢、孩子不是死了、是睡著了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",5,40,"他們就嗤笑耶穌．耶穌把他們都攆出去、就帶著孩子的父母、和跟隨的人進了孩子所在的地方．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",5,41,"就拉著孩子的手、對他說、大利大古米．〔翻出來、就是說、閨女、我吩咐你起來。〕")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",5,42,"那閨女立時起來走．他們就大大的驚奇．閨女已經十二歲了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",5,43,"耶穌切切的囑咐他們、不要叫人知道這事．又吩咐給他東西喫。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",6,1,"耶穌離開那裡、來到自己的家鄉．門徒也跟從他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",6,2,"到了安息日、他在會堂裡教訓人．眾人聽見、就甚希奇、說、這人從那裡有這些事呢、所賜給他的是甚麼智慧、他手所作的是何等的異能呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",6,3,"這不是那木匠麼．不是馬利亞的兒子、雅各約西猶大西門的長兄麼．他妹妹們不也是在我們這裡麼．他們就厭棄他。〔厭棄他原文作因他跌倒〕")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",6,4,"耶穌對他們說、大凡先知、除了本地親屬本家之外、沒有不被人尊敬的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",6,5,"耶穌就在那裡不得行甚麼異能、不過按手在幾個病人身上、治好他們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",6,6,"他也詫異他們不信、就往周圍鄉村教訓人去了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",6,7,"耶穌叫了十二個門徒來、差遣他們兩個兩個的出去．也賜給他們權柄、制伏污鬼．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",6,8,"並且囑咐他們、行路的時候、不要帶食物和口袋、腰袋裡也不要帶錢、除了柺杖以外、甚麼都不要帶．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",6,9,"只要穿鞋．也不要穿兩件掛子。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",6,10,"又對他們說、你們無論到何處、進了人的家、就住在那裡、直到離開那地方。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",6,11,"何處的人、不接待你們、不聽你們、你們離開那裡的時候、就把腳上的塵土跺下去、對他們作見證。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",6,12,"門徒就出去、傳道叫人悔改。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",6,13,"又趕出許多的鬼、用油抹了許多病人、治好他們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",6,14,"耶穌的名聲傳揚出來．希律王聽見了、就說、施洗的約翰從死裡復活了、所以這些異能由他裡面發出來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",6,15,"但別人說、是以利亞．又有人說、是先知、正像先知中的一位。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",6,16,"希律聽見、卻說、是我所斬的約翰、他復活了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",6,17,"先是希律為他兄弟腓力的妻子希羅底的緣故、差人去拿住約翰、鎖在監裡．因為希律已經娶了那婦人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",6,18,"約翰曾對希律說、你娶你兄弟的妻子是不合理的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",6,19,"於是希羅底懷恨他、想要殺他．只是不能．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",6,20,"因為希律知道約翰是義人、是聖人、所以敬畏他、保護他．聽他講論、就多照著行．並且樂意聽他。〔多照著行有古卷作游移不定〕")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",6,21,"有一天、恰巧是希律的生日、希律擺設筵席、請了大臣和千夫長、並加利利作首領的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",6,22,"希羅底的女兒進來跳舞、使希律和同席的人都歡喜．王就對女子說、你隨意向我求甚麼、我必給你。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",6,23,"又對他起誓說、隨你向我求甚麼、就是我國的一半、我也必給你。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",6,24,"他就出去、對他母親說、我可以求甚麼呢．他母親說、施洗約翰的頭。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",6,25,"他就急忙進去見王、求他說、我願王立時把施洗約翰的頭、放在盤子裡給我。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",6,26,"王就甚憂愁．但因他所起的誓、又因同席的人、就不肯推辭。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",6,27,"隨即差一個護衛兵、吩咐拿約翰的頭來．護衛兵就去在監裡斬了約翰、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",6,28,"把頭放在盤子裡、拿來給女子、女子就給他母親。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",6,29,"約翰的門徒聽見了、就來把他的屍首領去、葬在墳墓裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",6,30,"使徒聚集到耶穌那裡、將一切所作的事、所傳的道、全告訴他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",6,31,"他就說、你們來同我暗暗的到曠野地方去歇一歇．這是因為來往的人多、他們連喫飯也沒有工夫。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",6,32,"他們就坐船、暗暗的往曠野地方去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",6,33,"眾人看見他們去、有許多認識他們的、就從各城步行、一同跑到那裡、比他們先趕到了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",6,34,"耶穌出來、見有許多的人、就憐憫他們．因為他們如同羊沒有牧人一般．於是開口教訓他們許多道理。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",6,35,"天已經晚了、門徒進前來說、這是野地、天已經晚了、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",6,36,"請叫眾人散開、他們好往四面鄉村裡去、自己買甚麼喫。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",6,37,"耶穌回答說、你們給他們喫罷．門徒說、我們可以去買二十兩銀子的餅、給他們喫麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",6,38,"耶穌說、你們有多少餅、可以去看看．他們知道了、就說、五個餅、兩條魚。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",6,39,"耶穌吩咐他們叫眾人一幫一幫的、坐在青草地上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",6,40,"眾人就一排一排的坐下、有一百一排的、有五十一排的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",6,41,"耶穌拿著這五個餅、兩條魚、望著天祝福、擘開餅、遞給門徒擺在眾人面前．也把那兩條魚分給眾人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",6,42,"他們都喫、並且喫飽了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",6,43,"門徒就把碎餅碎魚、收拾起來、裝滿了十二個籃子。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",6,44,"喫餅的男人、共有五千。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",6,45,"耶穌隨即催門徒上船、先渡到那邊伯賽大去、等他叫眾人散開。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",6,46,"他既辭別了他們、就往山上去禱告。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",6,47,"到了晚上、船在海中、耶穌獨自在岸上．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",6,48,"看見門徒、因風不順、搖櫓甚苦．夜裡約有四更天、就在海面上走、往他們那裡去、意思要走過他們去．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",6,49,"但門徒看見他在海面上走、以為是鬼怪、就喊叫起來．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",6,50,"因為他們都看見了他、且甚驚慌。耶穌連忙對他們說、你們放心．是我、不要怕。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",6,51,"於是到他們那裡上了船、風就住了．他們心裡十分驚奇。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",6,52,"這是因為他們不明白那分餅的事、心裡還是愚頑。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",6,53,"既渡過去、來到革尼撒勒地方、就靠了岸。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",6,54,"一下船、眾人認得是耶穌．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",6,55,"就跑遍那一帶地方、聽見他在何處、便將有病的人、用褥子抬到那裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",6,56,"凡耶穌所到的地方、或村中、或城裡、或鄉間、他們都將病人放在街市上、求耶穌只容他們摸他的衣裳繸子．凡摸著的人就都好了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",7,1,"有法利賽人、和幾個文士、從耶路撒冷、來到耶穌那裡聚集。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",7,2,"他們曾看見他的門徒中、有人用俗手、就是沒有洗的手、喫飯。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",7,3,"（原來法利賽人和猶太人、都拘守古人的遺傳、若不仔細洗手、就不喫飯．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",7,4,"從市上來、若不洗浴、也不喫飯、還有好些別的規矩、他們歷代拘守、就是洗杯、罐、銅器、等物。）")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",7,5,"法利賽人和文士問他說、你的門徒為甚麼不照古人的遺傳、用俗手喫飯呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",7,6,"耶穌說、以賽亞指著你們假冒為善之人所說的預言、是不錯的、如經上說、『這百姓用嘴唇尊敬我、心卻遠離我。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",7,7,"他們將人的吩咐、當作道理教導人、所以拜我也是枉然。』")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",7,8,"你們是離棄　神的誡命、拘守人的遺傳。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",7,9,"又說、你們誠然是廢棄　神的誡命、要守自己的遺傳。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",7,10,"摩西說、『當孝敬父母。』又說、『咒罵父母的、必治死他。』")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",7,11,"你們倒說、人若對父母說、我所當奉給你的、已經作了各耳板（各耳板、就是供獻的意思）")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",7,12,"以後你們就不容他再奉養父母．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",7,13,"這就是你們承接遺傳、廢了　神的道．你們還作許多這樣的事。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",7,14,"耶穌又叫眾人來、對他們說、你們都要聽我的話、也要明白．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",7,15,"從外面進去的、不能污穢人、惟有從裡面出來的、乃能污穢人。〔有古卷在此有〕")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",7,16,"〔有耳可聽的就應當聽〕")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",7,17,"耶穌離開眾人、進了屋子、門徒就問他這比喻的意思。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",7,18,"耶穌對他們說、你們也是這樣不明白麼．豈不曉得凡從外面進入的、不能污穢人．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",7,19,"因為不是入他的心、乃是入他的肚腹、又落到茅廁裡。這是說、各樣的食物、都是潔淨的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",7,20,"又說、從人裡面出來的、那纔能污穢人．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",7,21,"因為從裡面、就是從人心裡、發出惡念、苟合、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",7,22,"偷盜、兇殺、姦淫、貪婪、邪惡、詭詐、淫蕩、嫉妒、謗讟、驕傲、狂妄．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",7,23,"這一切的惡、都是從裡面出來、且能污穢人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",7,24,"耶穌從那裡起身、往推羅西頓的境內去．進了一家、不願意人知道、卻隱藏不住。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",7,25,"當下有一個婦人、他的小女兒被污鬼附著、聽見耶穌的事、就來俯伏在他腳前。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",7,26,"這婦人是希利尼人、屬敘利非尼基族。他求耶穌趕出那鬼、離開他的女兒。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",7,27,"耶穌對他說、讓兒女們先喫飽．不好拿兒女的餅丟給狗喫。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",7,28,"婦人回答說、主阿、不錯．但是狗在桌子底下、也喫孩子們的碎渣兒。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",7,29,"耶穌對他說、因這句話、你回去罷．鬼已經離開你的女兒了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",7,30,"他就回家去、見小孩子躺在床上、鬼已經出去了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",7,31,"耶穌又離了推羅的境界、經過西頓、就從低加波利境內來到加利利海。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",7,32,"有人帶著一個耳聾舌結的人、來見耶穌、求他按手在他身上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",7,33,"耶穌領他離開眾人、到一邊去、就用指頭探他的耳朵、吐唾沫抹他的舌頭、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",7,34,"望天歎息、對他說、以法大、就是說、開了罷。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",7,35,"他的耳朵就開了、舌結也解了、說話也清楚了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",7,36,"耶穌囑咐他們、不要告訴人．但他越發囑咐、他們越發傳揚開了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",7,37,"眾人分外希奇、說、他所作的事都好、他連聾子也叫他們聽見、啞吧也叫他們說話。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",8,1,"那時、又有許多人聚集、並沒有甚麼喫的．耶穌叫門徒來、說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",8,2,"我憐憫這眾人、因為他們同我在這裡已經三天、也沒有喫的了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",8,3,"我若打發他們餓著回家、就必在路上困乏．因為其中有從遠處來的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",8,4,"門徒回答說、在這野地、從那裡能得餅、叫這些人喫飽呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",8,5,"耶穌問他們說、你們有多少餅．他們說、七個。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",8,6,"他吩咐眾人坐在地上、就拿著這七個餅、祝謝了、擘開遞給門徒叫他們擺開、門徒就擺在眾人面前。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",8,7,"又有幾條小魚．耶穌祝了福、就吩咐也擺在眾人面前。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",8,8,"眾人都喫、並且喫飽了．收拾剩下的零碎、有七筐子。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",8,9,"人數約有四千。耶穌打發他們走了、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",8,10,"隨即同門徒上船、來到大瑪努他境內。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",8,11,"法利賽人出來盤問耶穌、求他從天上顯個神蹟給他們看、想要試探他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",8,12,"耶穌心裡深深的歎息說、這世代為甚麼求神蹟呢。我實在告訴你們、沒有神蹟給這世代看。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",8,13,"他就離開他們、又上船往海那邊去了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",8,14,"門徒忘了帶餅．在船上除了一個餅、沒有別的食物。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",8,15,"耶穌囑咐他們說、你們要謹慎、防備法利賽人的酵、和希律的酵。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",8,16,"他們彼此議論說、這是因為我們沒有餅罷。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",8,17,"耶穌看出來、就說、你們為甚麼因為沒有餅就議論呢。你們還不省悟、還不明白麼．你們的心還是愚頑麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",8,18,"你們有眼睛、看不見麼、有耳朵、聽不見麼．也不記得麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",8,19,"我擘開那五個餅分給五千人、你們收拾的零碎、裝滿了多少籃子呢．他們說、十二個。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",8,20,"又擘開那七個餅分給四千人、你們收拾的零碎、裝滿了多少筐子呢．他們說、七個。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",8,21,"耶穌說、你們還是不明白麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",8,22,"他們來到伯賽大、有人帶一個瞎子來、求耶穌摸他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",8,23,"耶穌拉著瞎子的手、領他到村外．就吐唾沫在他眼睛上、按手在他身上、問他說、你看見甚麼了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",8,24,"他就抬頭一看、說、我看見人了．他們好像樹木、並且行走。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",8,25,"隨後又按手在他眼睛上、他定睛一看、就復了原、樣樣都看得清楚了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",8,26,"耶穌打發他回家、說、連這村子你也不要進去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",8,27,"耶穌和門徒出去、往該撒利亞腓立比的村莊去。在路上問門徒說、人說我是誰。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",8,28,"他們說、有人說、是施洗的約翰．有人說、是以利亞．又有人說、是先知裡的一位。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",8,29,"又問他們說、你們說我是誰．彼得回答說、你是基督。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",8,30,"耶穌就禁戒他們、不要告訴人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",8,31,"從此他教訓他們說、人子必須受許多的苦、被長老祭司長和文士棄絕、並且被殺、過三天復活。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",8,32,"耶穌明明的說這話、彼得就拉著他、勸他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",8,33,"耶穌轉過來、看著門徒、就責備彼得說、撒但、退我後邊去罷．因為你不體貼　神的意思、只體貼人的意思。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",8,34,"於是叫眾人和門徒來、對他們說、若有人要跟從我、就當捨己、背起他的十字架來跟從我。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",8,35,"因為凡要救自己生命的、〔生命或作靈魂下同〕必喪掉生命．凡為我和福音喪掉生命的、必救了生命。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",8,36,"人就是賺得全世界、賠上自己的生命、有甚麼益處呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",8,37,"人還能拿什麼換生命呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",8,38,"凡在這淫亂罪惡的世代、把我和我的道當作可恥的、人子在他父的榮耀裡、同聖天使降臨的時候、也要把那人當作可恥的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",9,1,"耶穌又對他們說、我實在告訴你們、站在這裡的、有人在沒嘗死味以前、必要看見　神的國大有能力臨到。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",9,2,"過了六天、耶穌帶著彼得、雅各、約翰、暗暗的上了高山、就在他們面前變了形像．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",9,3,"衣服放光、極其潔白．地上漂布的、沒有一個能漂得那樣白。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",9,4,"忽然有以利亞同摩西向他們顯現．並且和耶穌說話。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",9,5,"彼得對耶穌說、拉比、〔拉比就是夫子〕我們在這裡真好．可以搭三座棚、一座為你、一座為摩西、一座為以利亞。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",9,6,"彼得不知道說甚麼纔好．因為他們甚是懼怕。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",9,7,"有一朵雲彩來遮蓋他們．也有聲音從雲彩裡出來說、這是我的愛子、你們要聽他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",9,8,"門徒忽然周圍一看、不再見一人、只見耶穌同他們在那裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",9,9,"下山的時候、耶穌囑咐他們說、人子還沒有從死裡復活、你們不要將所看見的告訴人．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",9,10,"門徒將這話存記在心、彼此議論從死裡復活是甚麼意思。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",9,11,"他們就問耶穌說、文士為甚麼說、以利亞必須先來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",9,12,"耶穌說、以利亞固然先來、復興萬事．經上不是指著人子說、他要受許多的苦、被人輕慢呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",9,13,"我告訴你們、以利亞已經來了、他們也任意待他、正如經上所指著他的話。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",9,14,"耶穌到了門徒那裡看見有許多人圍著他們、又有文士和他們辯論。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",9,15,"眾人一見耶穌、都甚希奇、就跑上去問他的安。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",9,16,"耶穌問他們說、你們和他們辯論的是甚麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",9,17,"眾人中間有一個人回答說、夫子、我帶了我的兒子到你這裡來、他被啞巴鬼附著．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",9,18,"無論在那裡、鬼捉弄他、把他摔倒、他就口中流沫、咬牙切齒、身體枯乾、我請過你的門徒把鬼趕出去、他們卻是不能。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",9,19,"耶穌說、噯、不信的世代阿、我在你們這裡要到幾時呢．我忍耐你們要到幾時呢．把他帶到我這裡來罷。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",9,20,"他們就帶了他來。他一見耶穌、鬼便叫他重重的抽瘋．倒在地上、翻來覆去、口中流沫。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",9,21,"耶穌問他父親說、他得這病、有多少日子呢．回答說、從小的時候。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",9,22,"鬼屢次把他扔在火裡、水裡、要滅他．你若能作甚麼、求你憐憫我們、幫助我們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",9,23,"耶穌對他說、你若能信、在信的人、凡事都能。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",9,24,"孩子的父親立時喊著說、我信．但我信不足、求主幫助。〔有古卷作立時流淚的喊著說〕")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",9,25,"耶穌看見眾人都跑上來、就斥責那污鬼、說、你這聾啞的鬼、我吩咐你從他裡頭出來、再不要進去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",9,26,"那鬼喊叫、使孩子大大的抽了一陣瘋、就出來了．孩子好像死了一般。以致眾人多半說、他是死了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",9,27,"但耶穌拉著他的手、扶他起來、他就站起來了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",9,28,"耶穌進了屋子、門徒就暗暗的問他說、我們為甚麼不能趕出他去呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",9,29,"耶穌說、非用禱告、〔有古卷在此有禁食二字〕這一類的鬼、總不能出來。〔或作不能趕他出來〕")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",9,30,"他們離開那地方、經過加利利．耶穌不願意人知道。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",9,31,"於是教訓門徒、說、人子將要被交在人手裡、他們要殺害他．被殺以後、過三天他要復活。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",9,32,"門徒卻不明白這話、又不敢問他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",9,33,"他們來到迦百農．耶穌在屋裡問門徒說、你們在路上議論的是甚麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",9,34,"門徒不作聲、因為他們在路上彼此爭論誰為大。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",9,35,"耶穌坐下、叫十二個門徒來、說、若有人願意作首先的、他必作眾人末後的、作眾人的用人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",9,36,"於是領過一個小孩子來、叫他站在門徒中間．又抱起他來、對他們說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",9,37,"凡為我名、接待一個像這小孩子的就是接待我．凡接待我的、不是接待我、乃是接待那差我來的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",9,38,"約翰對耶穌說、夫子、我們看見一個人、奉你的名趕鬼、我們就禁止他、因為他不跟從我們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",9,39,"耶穌說、不要禁止他．因為沒有人奉我名行異能、反倒輕易毀謗我。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",9,40,"不敵擋我們的、就是幫助我們的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",9,41,"凡因你們是屬基督、給你們一杯水喝的、我實在告訴你們、他不能不得賞賜。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",9,42,"凡使這信我的一個小子跌倒的、倒不如把大磨石拴在這人的頸項上、扔在海裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",9,43,"倘若你一隻手叫你跌倒、就把他砍下來．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",9,44,"你缺了肢體進入永生、強如有兩隻手落到地獄、入那不滅的火裡去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",9,45,"倘若你一隻腳叫你跌倒、就把他砍下來．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",9,46,"你瘸腿進入永生、強如有兩隻腳被丟在地獄裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",9,47,"倘若你一隻眼叫你跌倒、就去掉他．你只有一隻眼進入　神的國、強如有兩隻眼被丟在地獄裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",9,48,"在那裡蟲是不死的、火是不滅的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",9,49,"因為必用火當鹽醃各人。〔有古卷在此有凡祭物必用鹽醃〕")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",9,50,"鹽本是好的、若失了味、可用甚麼叫他再鹹呢。你們裡頭應當有鹽、彼此和睦。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",10,1,"耶穌從那裡起身、來到猶太的境界、並約但河外。眾人又聚集到他那裡、他又照常教訓他們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",10,2,"有法利賽人來問他說、人休妻可以不可以、意思要試探他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",10,3,"耶穌回答說、摩西吩咐你們的是甚麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",10,4,"他們說、摩西許人寫了休書便可以休妻。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",10,5,"耶穌說、摩西因為你們的心硬、所以寫這條例給你們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",10,6,"但從起初創造的時候、　神造人是造男造女。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",10,7,"因此人要離開父母、與妻子連合、二人成為一體。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",10,8,"既然如此、夫妻不再是兩個人、乃是一體的了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",10,9,"所以　神配合的、人不可分開。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",10,10,"到了屋裡、門徒就問他這事。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",10,11,"耶穌對他們說、凡休妻另娶的、就是犯姦淫、辜負他的妻子．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",10,12,"妻子若離棄丈夫另嫁、也是犯姦淫了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",10,13,"有人帶著小孩子來見耶穌、要耶穌摸他們、門徒便責備那些人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",10,14,"耶穌看見就惱怒、對門徒說、讓小孩子到我這裡來、不要禁止他們．因為在　神國的、正是這樣的人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",10,15,"我實在告訴你們、凡要承受　神國的、若不像小孩子、斷不能進去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",10,16,"於是抱著小孩子、給他們按手、為他們祝福。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",10,17,"耶穌出來行路的時候、有一個人跑來、跪在他面前問他說、良善的夫子、我當作甚麼事、纔可以承受永生。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",10,18,"耶穌對他說、你為甚麼稱我是良善的．除了　神一位之外、再沒有良善的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",10,19,"誡命你是曉得的、不可殺人、不可姦淫、不可偷盜、不可作假見證、不可虧負人、當孝敬父母。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",10,20,"他對耶穌說、夫子、這一切我從小都遵守了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",10,21,"耶穌看著他、就愛他、對他說、你還缺少一件．去變賣你所有的、分給窮人、就必有財寶在天上．你還要來跟從我。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",10,22,"他聽見這話、臉上就變了色、憂憂愁愁的走了．因為他的產業很多。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",10,23,"耶穌周圍一看、對門徒說、有錢財的人進　神的國是何等的難哪。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",10,24,"門徒希奇他的話。耶穌又對他們說、小子、倚靠錢財的人進　神的國、是何等的難哪。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",10,25,"駱駝穿過鍼的眼、比財主進　神的國、還容易呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",10,26,"門徒就分外希奇、對他說、這樣誰能得救呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",10,27,"耶穌看著他們說、在人是不能、在　神卻不然．因為　神凡事都能。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",10,28,"彼得就對他說、看哪、我們已經撇下所有的跟從你了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",10,29,"耶穌說、我實在告訴你們、人為我和福音、撇下房屋、或是弟兄、姐妹、父母、兒女、田地。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",10,30,"沒有不在今世得百倍的、就是房屋、弟兄、姐妹、母親、兒女、田地、並且要受逼迫．在來世必得永生。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",10,31,"然而有許多在前的將要在後、在後的、將要在前。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",10,32,"他們行路上耶路撒冷去．耶穌在前頭走、門徒就希奇、跟從的人也害怕．耶穌又叫過十二個門徒來、把自己將要遭遇的事、告訴他們說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",10,33,"看哪、我們上耶路撒冷去、人子將要被交給祭司長和文士、他們要定他死罪、交給外邦人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",10,34,"他們要戲弄他、吐唾沫在他臉上、鞭打他、殺害他．過了三天、他要復活。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",10,35,"西庇太的兒子雅各、約翰進前來、對耶穌說、夫子、我們無論求你甚麼、願你給我們作。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",10,36,"耶穌說、要我給你們作甚麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",10,37,"他們說、賜我們在你的榮耀裡、一個坐在你右邊、一個坐在你左邊。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",10,38,"耶穌說、你們不知道所求的是甚麼。我所喝的杯．你們能喝麼．我所受的洗、你們能受麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",10,39,"他們說、我們能。耶穌說、我所喝的杯、你們也要喝．我所受的洗、你們也要受．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",10,40,"只是坐在我的左右、不是我可以賜的．乃是為誰預備的、就賜給誰。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",10,41,"那十個門徒聽見、就惱怒雅各、約翰。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",10,42,"耶穌叫他們來對他們說、你們知道、外邦人有尊為君王的、治理他們．有大臣操權管束他們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",10,43,"只是在你們中間、不是這樣．你們中間、誰願為大、就必作你們的用人．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",10,44,"在你們中間、誰願為首、就必作眾人的僕人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",10,45,"因為人子來、並不是要受人的服事、乃是要服事人、並且要捨命、作多人的贖價。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",10,46,"到了耶利哥．耶穌同門徒並許多人出耶利哥的時候、有一個討飯的瞎子、是底買的兒子巴底買、坐在路旁。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",10,47,"他聽見是拿撒勒的耶穌、就喊著說、大衛的子孫耶穌阿、可憐我罷。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",10,48,"有許多人責備他、不許他作聲、他卻越發大聲喊著說、大衛的子孫哪、可憐我罷。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",10,49,"耶穌就站住、說、叫過他來．他們就叫那瞎子、對他說、放心、起來、他叫你啦。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",10,50,"瞎子就丟下衣服、跳起來、走到耶穌那裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",10,51,"耶穌說、要我為你作甚麼。瞎子說、拉波尼、我要能看見。〔拉波尼就是夫子〕")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",10,52,"耶穌說、你去罷．你的信救了你了。瞎子立刻看見了、就在路上跟隨耶穌。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",11,1,"耶穌和門徒將近耶路撒冷、到了伯法其和伯大尼、在橄欖山那裡．耶穌就打發兩個門徒、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",11,2,"對他們說、你們往對面村子裡去．一進去的時候、必看見一匹驢駒拴在那裡、是從來沒有人騎過的、可以解開牽來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",11,3,"若有人對你們說、為甚麼作這事．你們就說、主要用他．那人必立時讓你們牽來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",11,4,"他們去了、便看見一匹驢駒、拴在門外街道上、就把牠解開。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",11,5,"在那裡站著的人、有幾個說、你們解驢駒作甚麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",11,6,"門徒照著耶穌所說的回答、那些人就任憑他們牽去了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",11,7,"他們把驢駒牽到耶穌那裡、把自己的衣服搭在上面、耶穌就騎上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",11,8,"有許多人、把衣服鋪在路上、也有人把田間的樹枝砍下來、鋪在路上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",11,9,"前行後隨的人、都喊著說、和散那．〔和散那原有求救的意思在此乃是稱頌的話〕奉主名來的、是應當稱頌的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",11,10,"那將要來的我祖大衛之國、是應當稱頌的．高高在上和散那。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",11,11,"耶穌進了耶路撒冷、入了聖殿、周圍看了各樣物件．天色已晚、就和十二個門徒出城往伯大尼去了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",11,12,"第二天、他們從伯大尼出來．耶穌餓了、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",11,13,"遠遠的看見一棵無花果樹、樹上有葉子、就往那裡去、或者在樹上可以找著甚麼．到了樹下、竟找不著甚麼、不過有葉子．因為不是收無花果的時候．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",11,14,"耶穌就對樹說、從今以後、永沒有人喫你的果子。他的門徒也聽見了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",11,15,"他們來到耶路撒冷、耶穌進入聖殿、趕出殿裡作買賣的人、推倒兌換銀錢之人的桌子、和賣鴿子之人的凳子．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",11,16,"也不許人拿著器具從殿裡經過。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",11,17,"便教訓他們說、經上不是記著說、『我的殿必稱為萬國禱告的殿』麼．你們倒使他成為賊窩了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",11,18,"祭司長和文士聽見這話就想法子要除滅耶穌．卻又怕他、因為眾人都希奇他的教訓。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",11,19,"每天晚上、耶穌出城去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",11,20,"早晨、他們從那裡經過、看見無花果樹連根都枯乾了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",11,21,"彼得想起耶穌的話來、就對他說、拉比、請看、你所咒詛的無花果樹、已經枯乾了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",11,22,"耶穌回答說、你們當信服　神。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",11,23,"我實在告訴你們、無論何人對這座山說、你挪開此地投在海裡．他若心裡不疑惑、只信他所說的必成、就必給他成了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",11,24,"所以我告訴你們、凡你們禱告祈求的、無論是甚麼、只要信是得著的、就必得著。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",11,25,"你們站著禱告的時候、若想起有人得罪你們、就當饒恕他、好叫你們在天上的父、也饒恕你們的過犯。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",11,26,"你們若不饒恕人、你們在天上的父、也不饒恕你們的過犯。〔有古卷無此節〕")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",11,27,"他們又來到耶路撒冷．耶穌在殿裡行走的時候、祭司長和文士並長老進前來、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",11,28,"問他說、你仗著甚麼權柄作這些事、給你這權柄的是誰呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",11,29,"耶穌對他們說、我要問你們一句話、你們回答我、我就告訴你們、我仗著甚麼權柄作這些事。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",11,30,"約翰的洗禮是從天上來的是從人間來的呢．你們可以回答我。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",11,31,"他們彼此商議說、我們若說從天上來、他必說、這樣、你們為甚麼不信他呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",11,32,"若說從人間來、卻又怕百姓．因為眾人真以約翰為先知。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",11,33,"於是回答耶穌說、我們不知道．耶穌說、我也不告訴你們、我仗著甚麼權柄作這些事。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",12,1,"耶穌就用比喻對他們說、有人栽了一個葡萄園、周圍圈上籬笆、挖了一個壓酒池蓋了一座樓、租給園戶、就往外國去了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",12,2,"到了時候、打發一個僕人到園戶那裡、要從園戶收葡萄園的果子。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",12,3,"園戶拿住他、打了他、叫他空手回去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",12,4,"再打發一個僕人到他們那裡．他們打傷他的頭、並且凌辱他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",12,5,"又打發一個僕人去．他們就殺了他。後又打發好些僕人去．有被他們打的、有被他們殺的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",12,6,"園主還有一位、是他的愛子．末後又打發他去、意思說、他們必尊敬我的兒子。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",12,7,"不料、那些園戶彼此說、這是承受產業的．來罷、我們殺他、產業就歸我們了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",12,8,"於是拿住他、殺了他、把他丟在園外。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",12,9,"這樣、葡萄園的主人要怎樣辦呢．他要來除滅那些園戶、將葡萄園轉給別人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",12,10,"經上寫著說、〔匠人所棄的石頭、已作了房角的頭塊石頭．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",12,11,"這是主所作的．在我們眼中看為希奇。〕這經你們沒有念過麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",12,12,"他們看出這比喻是指著他們說的、就想要捉拿他、只是懼怕百姓．於是離開他走了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",12,13,"後來他們打發幾個法利賽人和幾個希律黨的人、到耶穌那裡、要就著他的話陷害他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",12,14,"他們來了、就對他說、夫子、我們知道你是誠實的、甚麼人你都不徇情面．因為你不看人的外貌、乃是誠誠實實傳　神的道．納稅給該撒可以不可以。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",12,15,"我們該納不該納。耶穌知道他們的假意、就對他們說、你們為甚麼試探我．拿一個銀錢來給我看。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",12,16,"他們就拿了來。耶穌說。這像和這號是誰的．他們說、是該撒的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",12,17,"耶穌說、該撒的物當歸給該撒、　神的物當歸給　神。他們就很希奇他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",12,18,"撒都該人常說沒有復活的事．他們來問耶穌說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",12,19,"夫子、摩西為我們寫著說、人若死了、撇下妻子、沒有孩子、他兄弟當娶他的妻、為哥哥生子立後。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",12,20,"有弟兄七人、第一個娶了妻、死了、沒有留下孩子．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",12,21,"第二個娶了他、也死了、沒有留下孩子．第三個也是這樣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",12,22,"那七個人都沒有留下孩子．末了、那婦人也死了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",12,23,"當復活的時候、他是那一個的妻子呢．因為他們七個人都娶過他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",12,24,"耶穌說、你們所以錯了、豈不是因為不明白聖經、不曉得　神的大能麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",12,25,"人從死裡復活、也不娶、也不嫁、乃像天上的使者一樣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",12,26,"論到死人復活、你們沒有念過摩西的書、荊棘篇上所載的麼．　神對摩西說、『我是亞伯拉罕的　神、以撒的　神、雅各的　神。』")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",12,27,"　神不是死人的　神、乃是活人的　神．你們是大錯了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",12,28,"有一個文士來、聽見他們辯論、曉得耶穌回答的好、就問他說、誡命中那是第一要緊的呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",12,29,"耶穌回答說、第一要緊的、就是說、以色列阿、你要聽．主我們　神、是獨一的主。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",12,30,"你要盡心、盡性、盡意、盡力、愛主你的　神。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",12,31,"其次、就是說、要愛人如己。再沒有比這兩條誡命更大的了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",12,32,"那文士對耶穌說、夫子說、　神是一位、實在不錯．除了他以外、再沒有別的　神．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",12,33,"並且盡心、盡智、盡力、愛他、又愛人如己、就比一切燔祭、和各樣祭祀、好的多。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",12,34,"耶穌見他回答的有智慧、就對他說、你離　神的國不遠了。從此以後、沒有人敢再問他甚麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",12,35,"耶穌在殿裡教訓人、就問他們說、文士怎麼說、基督是大衛的子孫呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",12,36,"大衛被聖靈感動說、『主對我主說、你坐在我的右邊、等我使你仇敵作你的腳凳。』")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",12,37,"大衛既自己稱他為主、他怎麼又是大衛的子孫呢。眾人都喜歡聽他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",12,38,"耶穌在教訓之間、說、你們要防備文士、他們好穿長衣遊行、喜愛人在街市上問他們的安、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",12,39,"又喜愛會堂裡的高位筵席上的首座．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",12,40,"他們侵吞寡婦的家產、假意作很長的禱告．這些人要受更重的刑罰。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",12,41,"耶穌對銀庫坐著、看眾人怎樣投錢入庫．有好些財主、往裡投了若干的錢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",12,42,"有一個窮寡婦來、往裡投了兩個小錢、就是一個大錢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",12,43,"耶穌叫門徒來、說、我實在告訴你們、這窮寡婦投入庫裡的、比眾人所投的更多．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",12,44,"因為他們都是自己有餘、拿出來投在裡頭．但這寡婦是自己不足、把他一切養生的都投上了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",13,1,"耶穌從殿裡出來的時候、有一個門徒對他說、夫子、請看、這是何等的石頭、何等的殿宇。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",13,2,"耶穌對他說、你看見這大殿宇麼．將來在這裡沒有一塊石頭留在石頭上、不被拆毀了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",13,3,"耶穌在橄欖山上對聖殿而坐．彼得、雅各、約翰和安得烈、暗暗的問他說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",13,4,"請告訴我們、甚麼時候有這些事呢。這一切事、將成的時候、有甚麼預兆呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",13,5,"耶穌說、你們要謹慎、免得有人迷惑你們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",13,6,"將來有好些人冒我的名來、說、我是基督．並且要迷惑許多人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",13,7,"你們聽見打仗、和打仗的風聲、不要驚慌．這些事是必須有的、只是末期還沒有到．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",13,8,"民要攻打民、國要攻打國、多處必有地震、饑荒．這都是災難的起頭。〔災難原文作生產之難〕")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",13,9,"但你們要謹慎．因為人要把你們交給公會、並且你們在會堂裡要受鞭打．又為我的緣故、站在諸侯與君王面前、對他們作見證．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",13,10,"然而福音必須先傳給萬民。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",13,11,"人把你們拉去交官的時候、不要預先思慮說甚麼．到那時候、賜給你們甚麼話、你們就說甚麼、因為說話的不是你們、乃是聖靈。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",13,12,"弟兄要把弟兄、父親要把兒子、送到死地．兒女要起來與父母為敵、害死他們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",13,13,"並且你們要為我的名、被眾人恨惡、惟有忍耐到底的、必然得救。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",13,14,"你們看見那行毀壞可憎的、站在不當站的地方．（讀這經的人、須要會意）那時在猶太的、應當逃到山上．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",13,15,"在房上的、不要下來、也不要進去拿家裡的東西．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",13,16,"在田裡的、也不要回去取衣裳。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",13,17,"當那些日子、懷孕的和奶孩子的有禍了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",13,18,"你們應當祈求、叫這些事不在冬天臨到。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",13,19,"因為在那些日子必有災難、自從　神創造萬物直到如今、並沒有這樣的災難．後來也必沒有。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",13,20,"若不是主減少那日子、凡有血氣的、總沒有一個得救的．只是為主的選民、他將那日子減少了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",13,21,"那時若有人對你們說、看哪、基督在這裡．或說、基督在那裡．你們不要信。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",13,22,"因為假基督、假先知、將要起來、顯神蹟奇事．倘若能行、就把選民迷惑了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",13,23,"你們要謹慎．看哪、凡事我都預先告訴你們了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",13,24,"在那些日子、那災難以後、日頭要變黑了、月亮也不放光、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",13,25,"眾星要從天上墜落、天勢都要震動。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",13,26,"那時他們〔馬太二十四章三十節作地上的萬族〕要看見人子有大能力、大榮耀、駕雲降臨。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",13,27,"他要差遣天使、把他的選民、從四方、從地極直到天邊、都招聚了來。〔方原文作風〕")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",13,28,"你們可以從無花果樹學個比方．當樹枝發嫩長葉的時候、你們就知道夏天近了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",13,29,"這樣、你們幾時看見這些事成就、也該知道人子近了、〔人子或作　神的國〕正在門口了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",13,30,"我實在告訴你們、這世代還沒有過去、這些事都要成就。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",13,31,"天地要廢去．我的話卻不能廢去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",13,32,"但那日子、那時辰、沒有人知道、連天上的使者也不知道、子也不知道、惟有父知道。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",13,33,"你們要謹慎、儆醒祈禱、因為你們不曉得那日期幾時來到。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",13,34,"這事正如一個人離開本家、寄居外邦、把權柄交給僕人、分派各人當作的工、又吩咐看門的儆醒。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",13,35,"所以你們要儆醒、因為你們不知道家主甚麼時候來、或晚上、或半夜、或雞叫、或早晨。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",13,36,"恐怕他忽然來到、看見你們睡著了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",13,37,"我對你們所說的話、也是對眾人說、要儆醒。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,1,"過兩天是逾越節、又是除酵節．祭司長和文士、想法子怎麼用詭計捉拿耶穌殺他．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,2,"只是說、當節的日子不可、恐怕百姓生亂。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,3,"耶穌在伯大尼長大痲瘋的西門家裡坐席的時候、有一個女人、拿著一玉瓶至貴的真哪噠香膏來、打破玉瓶、把膏澆在耶穌的頭上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,4,"有幾個人心中很不喜悅、說、何用這樣枉費香膏呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,5,"這香膏可以賣三十多兩銀子賙濟窮人．他們就向那女人生氣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,6,"耶穌說、由他罷．為甚麼難為他呢．他在我身上作的是一件美事。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,7,"因為常有窮人和你們同在、要向他們行善、隨時都可以．只是你們不常有我。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,8,"他所作的、是盡他所能的．他是為我安葬的事、把香膏預先澆在我身上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,9,"我實在告訴你們、普天之下、無論在甚麼地方傳這福音、也要述說這女人所作的以為記念。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,10,"十二門徒之中有一個加略人猶大、去見祭司長、要把耶穌交給他們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,11,"他們聽見就歡喜、又應許給他銀子．他就尋思如何得便、把耶穌交給他們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,12,"除酵節的第一天、就是宰逾越羊羔的那一天、門徒對耶穌說、你喫逾越節的筵席、要我們往那裡去預備呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,13,"耶穌就打發兩個門徒、對他們說、你們進城去、必有人拿著一瓶水、迎面而來．你們就跟著他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,14,"他進那家去、你們就對那家的主人說、夫子說、客房在那裡、我與門徒好在那裡喫逾越節的筵席。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,15,"他必指給你們擺設整齊的一間大樓、你們就在那裡為我們預備。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,16,"門徒出去、進了城、所遇見的、正如耶穌所說的．他們就預備了逾越節的筵席。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,17,"到了晚上、耶穌和十二個門徒都來了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,18,"他們坐席正喫的時候、耶穌說、我實在告訴你們、你們中間有一個與我同喫的人要賣我了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,19,"他們就憂愁起來、一個一個的問他說、是我麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,20,"耶穌對他們說、是十二個門徒中同我蘸手在盤子裡的那個人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,21,"人子必要去世、正如經上指著他所寫的．但賣人子的人有禍了。那人不生在世上倒好。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,22,"他們喫的時候、耶穌拿起餅來、祝了福、就擘開遞給他們說、你們拿著喫．這是我的身體。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,23,"又拿起杯來、祝謝了、遞給他們．他們都喝了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,24,"耶穌說、這是我立約的血、為多人流出來的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,25,"我實在告訴你們、我不再喝這葡萄汁、直到我在　神的國裡、喝新的那日子。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,26,"他們唱了詩、就出來、往橄欖山去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,27,"耶穌對他們說、你們都要跌倒了．因為經上記著說、『我要擊打牧人、羊就分散了。』")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,28,"但我復活以後、要在你們以先往加利利去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,29,"彼得說、眾人雖然跌倒、我總不能。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,30,"耶穌對他說、我實在告訴你、就在今天夜裡、雞叫兩遍以先、你要三次不認我。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,31,"彼得卻極力的說、我就是必須和你同死、也總不能不認你．眾門徒都是這樣說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,32,"他們來到一個地方、名叫客西馬尼．耶穌對門徒說、你們坐在這裡、等我禱告。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,33,"於是帶著彼得、雅各、約翰同去、就驚恐起來、極其難過。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,34,"對他們說、我心裡甚是憂傷、幾乎要死．你們在這裡、等候儆醒。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,35,"他就稍往前走、俯伏在地禱告說、倘若可行、便叫那時候過去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,36,"他說、阿爸、父阿、在你凡事都能．求你將這杯撤去．然而不要從我的意思、只要從你的意思。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,37,"耶穌回來、見他們睡著了、就對彼得說、西門、你睡覺麼。不能儆醒片時麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,38,"總要儆醒禱告、免得入了迷惑．你們心靈固然願意、肉體卻軟弱了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,39,"耶穌又去禱告、說的話還是與先前一樣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,40,"又來、見他們睡著了、因為他們的眼睛甚是困倦．他們也不知道怎麼回答。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,41,"第三次來、對他們說、現在你們仍然睡覺安歇罷．〔罷或作麼〕夠了、時候到了．看哪、人子被賣在罪人手裡了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,42,"起來、我們走罷．看哪、那賣我的人近了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,43,"說話之間、忽然那十二個門徒裡的猶大來了、並有許多人帶著刀棒、從祭司長和文士並長老那裡與他同來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,44,"賣耶穌的人曾給他們一個暗號、說、我與誰親嘴、誰就是他．你們把他拿住、牢牢靠靠的帶去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,45,"猶大來了、隨即到耶穌跟前說、拉比、便與他親嘴。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,46,"他們就下手拿住他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,47,"旁邊站著的人、有一個拔出刀來、將大祭司的僕人砍了一刀、削掉了他一個耳朵。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,48,"耶穌對他們說、你們帶著刀棒、出來拿我、如同拿強盜麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,49,"我天天教訓人、同你們在殿裡、你們並沒有拿我．但這事成就、為要應驗經上的話。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,50,"門徒都離開他逃走了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,51,"有一個少年人、赤身披著一塊麻布、跟隨耶穌、眾人就捉拿他．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,52,"他卻丟了麻布、赤身逃走了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,53,"他們把耶穌帶到大祭司那裡．又有眾祭司長和長老並文士、都來和大祭司一同聚集。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,54,"彼得遠遠的跟著耶穌、一直進入大祭司的院裡、和差役一同坐在火光裡烤火。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,55,"祭司長和全公會尋找見證控告耶穌、要治死他．卻尋不著。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,56,"因為有好些人作假見證告他、只是他們的見證、各不相合。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,57,"又有幾個人站起來、作假見證告他說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,58,"我們聽見他說、我要拆毀這人手所造的殿、三日內就另造一座不是人手所造的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,59,"他們就是這麼作見證、也是各不相合。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,60,"大祭司起來、站在中間、問耶穌說、你甚麼都不回答麼．這些人作見證告你的是甚麼呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,61,"耶穌卻不言語、一句也不回答。大祭司又問他說、你是那當稱頌者的兒子基督不是。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,62,"耶穌說、我是．你們必看見人子、坐在那權能者的右邊、駕著天上的雲降臨。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,63,"大祭司就撕開衣服、說、我們何必再用見證人呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,64,"你們已經聽見他這僭妄的話了．你們的意見如何．他們都定他該死的罪。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,65,"就有人吐唾沫在他臉上、又蒙著他的臉、用拳頭打他、對他說、你說預言罷．差役接過他來用手掌打他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,66,"彼得在下邊、院子裡、來了大祭司的一個使女．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,67,"見彼得烤火、就看著他說、你素來也是同拿撒勒人耶穌一夥的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,68,"彼得卻不承認、說、我不知道、也不明白你說的是甚麼．於是出來、到了前院．雞就叫了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,69,"那使女看見他、又對旁邊站著的人說、這也是他們一黨的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,70,"彼得又不承認。過了不多的時候、旁邊站著的人又對彼得說、你真是他們一黨的。因為你是加利利人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,71,"彼得就發咒起誓的說、我不認得你們說的這個人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",14,72,"立時雞叫了第二遍．彼得想起耶穌對他所說的話、雞叫兩遍以先、你要三次不認我。思想起來、就哭了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",15,1,"一到早晨、祭司長和長老文士全公會的人大家商議、就把耶穌捆綁解去、交給彼拉多。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",15,2,"彼拉多問他說、你是猶太人的王麼．耶穌回答說、你說的是。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",15,3,"祭司長告他許多的事。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",15,4,"彼拉多又問他說、你看、他們告你這麼多的事、你甚麼都不回答麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",15,5,"耶穌仍不回答、以致彼拉多覺得希奇。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",15,6,"每逢這節期、巡撫照眾人所求的、釋放一個囚犯給他們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",15,7,"有一個人名叫巴拉巴、和作亂的人一同捆綁．他們作亂的時候、曾殺過人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",15,8,"眾人上去求巡撫、照常例給他們辦。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",15,9,"彼拉多說、你們要我釋放猶太人的王給你們麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",15,10,"他原曉得祭司長是因為嫉妒纔把耶穌解了來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",15,11,"只是祭司長挑唆眾人、寧可釋放巴拉巴給他們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",15,12,"彼拉多又說、那麼樣你們所稱為猶太人的王、我怎麼辦他呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",15,13,"他們又喊著說、把他釘十字架。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",15,14,"彼拉多說、為什麼呢、他作了甚麼惡事呢。他們便極力的喊著說、把他釘十字架。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",15,15,"彼拉多要叫眾人喜悅、就釋放巴拉巴給他們、將耶穌鞭打了、交給人釘十字架。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",15,16,"兵丁把耶穌帶進衙門院裡．叫齊了全營的兵。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",15,17,"他們給他穿上紫袍、又用荊棘編作冠冕給他戴上．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",15,18,"就慶賀他說、恭喜猶太人的王阿。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",15,19,"又拿一根葦子、打他的頭、吐唾沫在他臉上屈膝拜他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",15,20,"戲弄完了、就給他脫了紫袍、仍穿上他自己的衣服、帶他出去、要釘十字架。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",15,21,"有一個古利奈人西門、就是亞力山大和魯孚的父親、從鄉下來、經過那地方、他們就勉強他同去、好背著耶穌的十字架。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",15,22,"他們帶耶穌到了各各他地方、（各各他翻出來、就是髑髏地）")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",15,23,"拿沒藥調和的酒給耶穌、他卻不受。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",15,24,"於是將他釘在十字架上、拈鬮分他的衣服、看是誰得甚麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",15,25,"釘他在十字架上、是巳初的時候。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",15,26,"在上面有他的罪狀、寫的是猶太人的王。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",15,27,"他們又把兩個強盜、和他同釘十字架．一個在右邊、一個在左邊。〔有古卷在此有〕")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",15,28,"〔這就應了經上的話說『他被列在罪犯之中』〕")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",15,29,"從那裡經過的人辱罵他、搖著頭說、咳、你這拆毀聖殿、三日又建造起來的．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",15,30,"可以救自己從十字架上下來罷。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",15,31,"祭司長和文士也是這樣戲弄他、彼此說、他救了別人、不能救自己。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",15,32,"以色列的王基督、現在可以從十字架上下來、叫我們看見、就信了。那和他同釘的人也是譏誚他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",15,33,"從午正到申初遍地都黑暗了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",15,34,"申初的時候、耶穌大聲喊著說、以羅伊、以羅伊、拉馬撒巴各大尼．翻出來、就是我的　神、我的　神、為甚麼離棄我。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",15,35,"旁邊站著的人、有的聽見就說、看哪、他叫以利亞呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",15,36,"有一個人跑去、把海絨蘸滿了醋綁在葦子上、送給他喝、說、且等著、看以利亞來不來把他取下。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",15,37,"耶穌大聲喊叫、氣就斷了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",15,38,"殿裡的幔子、從上到下裂為兩半。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",15,39,"對面站著的百夫長、看見耶穌這樣喊叫斷氣、〔有古卷無喊叫二字〕就說、這人真是　神的兒子。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",15,40,"還有些婦女、遠遠的觀看．內中有抹大拉的馬利亞、又有小雅各和約西的母親馬利亞、並有撒羅米．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",15,41,"就是耶穌在加利利的時候、跟隨他、服事他的那些人、還有同耶穌上耶路撒冷的好些婦女在那裡觀看。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",15,42,"到了晚上、因為這是預備日、就是安息日的前一日、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",15,43,"有亞利馬太的約瑟前來、他是尊貴的議士、也是等候　神國的．他放膽進去見彼拉多、求耶穌的身體。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",15,44,"彼拉多詫異耶穌已經死了．便叫百夫長來、問他耶穌死了久不久。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",15,45,"既從百夫長得知實情、就把耶穌的屍首賜給約瑟。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",15,46,"約瑟買了細麻布、把耶穌取下來、用細麻布裹好、安放在磐石中鑿出來的墳墓裡．又滾過一塊石頭來擋住墓門。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",15,47,"抹大拉的馬利亞、和約西的母親馬利亞、都看見安放他的地方。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",16,1,"過了安息日、抹大拉的馬利亞、和雅各的母親馬利亞、並撒羅米、買了香膏、要去膏耶穌的身體。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",16,2,"七日的第一日清早、出太陽的時候、他們來到墳墓那裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",16,3,"彼此說、誰給我們把石頭從墓門滾開呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",16,4,"那石頭原來很大、他們抬頭一看、卻見石頭已經滾開了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",16,5,"他們進了墳墓、看見一個少年人坐在右邊、穿著白袍．就甚驚恐。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",16,6,"那少年人對他們說、不要驚恐．你們尋找那釘十字架的拿撒勒人耶穌．他已經復活了、不在這裡．請看安放他的地方。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",16,7,"你們可以去告訴他的門徒和彼得說、他在你們以先往加利利去．在那裡你們要見他、正如他從前所告訴你們的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",16,8,"他們就出來、從墳墓那裡逃跑．又發抖、又驚奇、甚麼也不告訴人．因為他們害怕。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",16,9,"在七日的第一日清早、耶穌復活了、就先向抹大拉的馬利亞顯現．耶穌從他身上曾趕出七個鬼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",16,10,"他去告訴那向來跟隨耶穌的人．那時他們正哀慟哭泣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",16,11,"他們聽見耶穌活了、被馬利亞看見、卻是不信。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",16,12,"這事以後、門徒中間有兩個人、往鄉下去．走路的時候、耶穌變了形像向他們顯現、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",16,13,"他們就去告訴其餘的門徒．其餘的門徒、也是不信。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",16,14,"後來十一個門徒坐席的時候、耶穌向他們顯現、責備他們不信、心裡剛硬．因為他們不信那些在他復活以後看見他的人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",16,15,"他又對他們說、你們往普天下去、傳福音給萬民聽。〔萬民原文作凡受造的〕")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Mak",16,16,"信而受洗的必然得救．不信的必被定罪。")');
